css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Restores [Ext]Firefm old statusbar icon buttons borders on the toolbars near the top of screen */
#navigator-toolbox:not([customizing]) 
   #status-bar #firefm-statusbar-toolbar .toolbarbutton-icon {
       margin-top: -3px !important;
       margin-bottom: -2px !important;
       margin-right: 1px !important;
       margin-left: 1px !important;
       padding: 0.5px 1px !important;
       border-radius: 3px !important;
       background: rgba(151,152,153,.05)
       -moz-linear-gradient(rgba(251,252,253,.95), rgba(246,247,248,.47) 49%, rgba(231,232,233,.45) 51%, rgba(225,226,229,.3)) !important;
       background-clip: padding-box !important;
       border: 1px solid !important;
       border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
   }

/* Restores [Ext]Firefm old statusbar icon buttons borders on the Add-on Bar */

#navigator-toolbox:not([customizing]) ~ #browser-bottombox
   #status-bar #firefm-statusbar-toolbar .toolbarbutton-icon {
       margin-top: -3px !important;
       margin-bottom: -2px !important;
       margin-right: 1px !important;
       margin-left: 1px !important;
       padding: 0.5px 1px !important;
       border-radius: 3px !important;
       background: rgba(151,152,153,.05)
       -moz-linear-gradient(rgba(251,252,253,.95), rgba(246,247,248,.47) 49%, rgba(231,232,233,.45) 51%, rgba(225,226,229,.3)) !important;
       background-clip: padding-box !important;
       border: 1px solid !important;
       border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
   }


/* Prevents [Ext]Firefm old statusbar Icons from moving when mouse hovered on the toolbars near the top of screen */

#navigator-toolbox:not([customizing])
   #status-bar #firefm-statusbar-toolbar toolbarbutton {
      padding: 0px !important;
      border: none 0 transparent !important;
      height: 18px !important;
      -moz-appearance: none !important;}

/* Prevents [Ext]Firefm old statusbar Icons from moving when mouse hoveredon the Add-on Bar */

#navigator-toolbox:not([customizing]) ~ #browser-bottombox
   #status-bar #firefm-statusbar-toolbar toolbarbutton {
      padding: 0px !important;
      border: none 0 transparent !important;
      height: 18px !important;
      -moz-appearance: none !important;}


/* Fixes [Ext]UI Fixer "Movable Status Bar Button" spacing */

   #fixer-status-bar {
      margin-top: 0px !important;
      padding: 0px 2px !important;
   }
