css:
@-moz-document domain("netflix.com") {
#netflix a {background: transparent url('http://goo.gl/KT3JI') no-repeat!important;
margin-left: -10px!important; width: 95px!important;}

input:focus, textarea:focus {outline: none!important;}

#fground, #bdshadowwrapper, #headlessshadowwrapper, #fground-nohead {border:1px solid #ccc!important; box-shadow:none!important; padding:5px;}

body, .fixedHeaderLayer, #fground #bd.main-content, #bd .hr-dmbr, html, .custom-view-w{background:#fff!important;}

#global-search {background:none!important;}
#global-search div {background: white!important; height: 17px!important; padding: 3px 4px 4px!important;}
#global-tools {margin-right: 10px!important;}

.agMovie span, #header, #navigation {border:none!important;}
span.hoverPlay:hover img {opacity: 0.5!important;}

#BobMovie {margin-top: -49px!important; margin-left: -12px!important;}
#BobMovie-content {box-shadow:none!important; border: 2px solid #B9090B!important;}
#BobMovie .bobMovieContent {font: 12px/14px arial!important;}
#BobMovie .bobMovieHeader {border:none!important;}
#BobMovie-arrow {display:none!important;}


#navigation, .navigation.clearfix {background: #E60014!important;}
.navigation .nav-item, .navigation .nav-item-current {border-right: 1px solid #F94A51!important;}
.navigation .nav-item-current {background: brown!important;}

.navigation .nav-item a, .navigation .nav-item a:visited, .navigation .nav-item a:hover, .navigation .nav-item a:active {border-right: 1px solid brown!important; color:#fff!important;}

.nav-item.nav-characters a.sub-tab {margin-bottom:0!important;}

.dropdown-menu .inner1, .dropdown-menu .inner3 div, .inner3 {background:none!important; border:none!important;}
.dropdown-menu .inner1 .inner2 {border:none!important; opacity:1!important; margin-top:2px!important;}

.dropdown-menu a {border-bottom: 1px solid #FD2!important; background: #FC0!important; border-top: 1px solid #FA0!important;}
.dropdown-menu a:hover {background:#FA2!important; color:#444!important;}

#ft {padding-top: 7px!important; background-color: #B9090B!important;}

.progress-bar, .mini-progress-bar {background-color:#333!important; height:10px!important; margin-left: 10px!important;}
.progress-bar .bar, .mini-progress-bar .bar {background: #777!important; height:8px!important;}

.mrow-rating, .mrow-rating .hd, .mrow-rating .bd {background-color: #222!important;}

.nfmessage.large.stop.urgent.marginalize { display:none!important; }
}
