css:
#www-fogcreek-com-fogbugz
{
	background: #333;
	margin:0;
	padding:0;
	font-family:'Cambria',Georgia,'Times New Roman',Times,serif;
	height:100%;
	color:#AAA;
}

h1
{
	color:white!important;
}

#navTop a.navlink,#navTop a .underline,#navTop a .dotted
{
	display:inline-block;
	text-decoration:none;
	border:none;
	color:#AAA;
	margin:0 1px;
}

#navTopContainer
{
	background-color:#f3f3f3;
	background-image:-webkit-gradient(linear, left top, left bottom, from(#777), to(#222));
	background-image:-webkit-linear-gradient(top, #777, #222);
	background-image:-moz-linear-gradient(top, #777, #222);
	background-image:-ms-linear-gradient(top, #777, #222);
	background-image:-o-linear-gradient(top, #777, #222);
	background-image:linear-gradient(top, #777, #222);
	filter:progid:DXImageTransform.Microsoft.gradient(startColorStr='#777', EndColorStr='#222');
	border-bottom:1px solid #3E454E;
	height:20px;
}

#banner
{
	clear:both;
	border-bottom:1px solid #555;
	border-top:1px solid #888;
	background-color:#18354D;
	height:40px;
}

#mainnav a.navlink,#mainnav .neutral a.navlink
{
	display:inline-block;
	padding:4px 4px;
	margin:8px 5px 6px 0;
	color:#90A2C0;
	font-family:'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-size:14px;
	font-weight:bold;
	text-decoration:none;
}

#mainnav a.navlink:hover,#mainnav #mainnav a.navlink:focus
{
	background-color:#292F33;
	color:#FFF;
	-moz-border-radius:4px;
	-webkit-border-radius:4px;
	border-radius:4px;
	-moz-box-shadow:inset 0 1px 1px #8293ad;
	-webkit-box-shadow:inset 0 1px 1px #8293ad;
	box-shadow:inset 0 1px 1px #8293ad;
}


#belowBanner
{
	background-color:#18354D;
	float:right;
	margin: 0px 15px 0px 0px;
	padding:4px 6px 3px 2px;
	border:1px solid #666;
	border-top:none;
	border-right:none;
	-webkit-border-bottom-left-radius:5px;
	-webkit-border-bottom-right-radius:5px;
	-moz-border-radius-bottomleft:3px;
	border-bottom-left-radius:3px;
	-moz-box-shadow:inset 0 1px 2px #53575A;
	-webkit-box-shadow:inset 0 1px 2px #53575A;
	box-shadow:inset 0 1px 2px #53575A;
}

#belowBanner a.navlink
{
	display:inline-block;
	color:#BBB;
	padding:2px 4px 2px 4px;
	margin-left:1px;
	font-family:'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-size:11px;
	font-weight:bold;
	text-decoration:none;
	-moz-border-radius:3px;
	-webkit-border-radius:3px;
	border-radius:3px;
}

#belowBanner a.navlink:hover,#belowBanner a.navlink:focus
{
	background-color:#292F33;
	color:#FFF;
}



#bugviewContainer
{
	background-color:#000;
	border:1px solid #333;
	-moz-box-shadow:0 1px 5px #333;
	-webkit-box-shadow:0 1px 5px #333;
	box-shadow:0 1px 5px #333;
	margin-left:auto;
	margin-right:auto;
	overflow:hidden;
	position:relative;
}

#bugviewContainer .buttonbar ul.toolbar.buttons
{
	background-image:-webkit-gradient(linear, left top, left bottom, from(#444), to(#000));
	background-image:-webkit-linear-gradient(top, #444, #000);
	text-align:right;
	height:28px;
}

#bugviewContainer .buttonbar ul.toolbar li a
{
	border:1px solid transparent;
	color:#BBB;
	display:inline-block;
	padding:5px;
	text-decoration:none;
}

#bugviewContainer .buttonbar ul.toolbar.buttons li a:hover
{
	color:#FFF;
        background-color:#000;
	border-color:#333;
}

#bugviewContainer .buttonbar ul.toolbar.nextprev
{
	float:left;
	height:28px;
	background-color:#888;
	-webkit-border-radius:5px;
}

#bugviewContainer .buttonbar.buttonbarTop .buttons
{
	border-bottom:1px solid #444;
}

#bugviewContainerTop
{
	background-color:#222;
	margin-bottom:10px;
}

#bugviewContainer .top .ixBug a
{
	text-decoration:none;
	font-weight:bold;
	line-height:1em;
	color:#6C87B3;
}

#bugviewContainer .top .ixBug a:visited
{
	color:#7B9ACD;
}

#bugviewContainer .top .ixBug a:hover
{
	color:#FFF;
	text-decoration:underline;
}

#bugviewContainer .top .title
{
	font-size:24px;
	font-family:'Helvetica Neue',Helvetica,Arial,sans-serif;
	line-height:24px;
	color:#6C87B3;
	font-weight:bold;
}

.content.subtitle a
{
	color:#6C87B3;
}

.content.subtitle a:hover
{
	color:#FFF;
}

.content.subtitle a:visited
{
	color:#7B9ACD;
}

#bugviewContainer .top .subtitle .fixfor
{
	color:#BBB;
}

#bugviewContainer .top .statusbar
{
	color:#BBB;
	padding-top:1em;
}

#bugviewContainer .top .statusbar a
{
	color:#6C87B3;
}

#bugviewContainer .top .statusbar a:visited
{
	color:#7B9ACD;
}

#bugviewContainer .side label
{
	font-weight:bold;
	color:#AAA;
	font-size:11px;
}

#bugviewContainer .side .content
{
	padding-left:1em;
	padding-bottom:1em;
	padding-right:1em;
	padding-top:1px;
	font-size:11px;
	color:#DDD;
}


#bugviewContainer .bugevents .bugevent.detailed,#bugviewContainer .bugevents .pseudobugevent.detailed
{
	background-color:#444;
	border:1px solid #444;
	-moz-border-radius:4px;
	-webkit-border-radius:4px;
	-webkit-box-shadow: none;
	border-radius:4px;
	color:#EEE;
	font-style:normal;
	padding:9px 9px 0;
	border-bottom: 1px solid #666;
}

#bugviewContainer .bugevents .bugevent .summary,#bugviewContainer .bugevents .pseudobugevent .summary,#bugviewContainer .bugevents .bugevent .changes,#bugviewContainer .bugevents .pseudobugevent .changes
{
	color:#EEE;
	font-size:11px;
	line-height:15px;
}

#bugviewContainer .bugevents .bugevent .summary .action,#bugviewContainer .bugevents .pseudobugevent .summary .action,#bugviewContainer .bugevents .bugevent .changes .action,#bugviewContainer .bugevents .pseudobugevent .changes .action
{
	font-weight:bold;
	text-shadow:0 1px rgba(0, 0, 0, 0.5);
}

#bugviewContainer .bugevents .bugevent .summary .action a,#bugviewContainer .bugevents .pseudobugevent .summary .action a,#bugviewContainer .bugevents .bugevent .changes .action a,#bugviewContainer .bugevents .pseudobugevent .changes .action a
{
	font-weight:normal;
	text-shadow:none;
	color:#6C87B3;
}

#bugviewContainer .bugevents .bugevent .summary .action a:visited,#bugviewContainer .bugevents .pseudobugevent .summary .action a:visited,#bugviewContainer .bugevents .bugevent .changes .action a:visited,#bugviewContainer .bugevents .pseudobugevent .changes .action a:visited
{
	color:#7B9ACD;
}

#bugviewContainer .bugevents .bugevent.detailed .body,#bugviewContainer .bugevents .bugevent.detailed .attachments,#bugviewContainer .bugevents .pseudobugevent.detailed .body,#bugviewContainer .bugevents .pseudobugevent.detailed .attachments
{
	background-color:#222;
}

#bugviewContainer .bugevents .bugevent.detailed .body,#bugviewContainer .bugevents .pseudobugevent.detailed .body
{
	border-top:0px solid #222;
	padding:10px;
}

#bugviewContainer .bugevents .bugevent.brief,#bugviewContainer .bugevents .pseudobugevent.brief
{
	background:#444;
	border:1px solid #333;
	-moz-border-radius:4px;
	-webkit-border-radius:4px;
	border-radius:4px;
	-moz-box-shadow:inset 1px 1px #555;
	-webkit-box-shadow:inset 1px 1px #555;
	box-shadow:inset 1px 1px #555;
}

#bugviewContainer .bugevents .bugevent.brief .body.editable,#bugviewContainer .bugevents .pseudobugevent.brief .body.editable
{
	border-top:1px dotted #222;
}

#listHeader
{
	background:#111;
	padding:4px 2px;
	margin-bottom:8px;
	-moz-border-radius:3px;
	-webkit-border-radius:3px;
	border-radius:3px;
	border:1px solid;
	border-color:#DDD #DDD #AEAEAE;
	-moz-box-shadow:0px 1px 1px #ccc;
	-webkit-box-shadow:0px 1px 1px #ccc;
	box-shadow:0px 1px 1px #ccc;
	*border:1px solid #CCC;
}

#listName a,#listName a:link,#listName a:visited,#listTitle a,#listTitle a:link,#listTitle a:visited
{
	font-weight:bold;
	color:#90A2C0;
	text-decoration:none;
	border-bottom:1px solid #90A2C0;
}

#listName,#listTitle
{
	color:#FFF;
	font-family:'Helvetica Neue',Helvetica,Arial,sans-serif;
	vertical-align:top;
}

#listTitle a:hover,#listName a:hover
{
	color:#FFF;
	border-bottom:1px solid #FFF;
}

#viewSelector div.dropdownHolder
{
	height:32px;
	border:1px solid #CCC;
	-moz-border-radius:3px;
	-webkit-border-radius:3px;
	border-radius:3px;
	-moz-box-shadow:1px 1px 0 #fff;
	-webkit-box-shadow:1px 1px 0 #fff;
	box-shadow:1px 1px 0 #fff;
	background:#555;
}

#listHeader #listTitle span.further,#listHeader #listTitle span.further a
{
	color:#90A2C0;
	font-size:11px;
	text-shadow:1px 1px 0 #555;
}

span.addRefinement.further span
{
	border-bottom:1px solid #90A2C0;
}

#listHeader #listTitle span.further a:hover
{
	color:#FFF;
}

.mainArea h2
{
	font:bold 14px 'Helvetica Neue',Helvetica,Arial,sans-serif;
	text-transform:uppercase;
	letter-spacing:0.05em;
	color:#FFF;
	margin:1em 0 0.1em 0;
}

.bugList .bugs th,.biglist th,.smalllist th,.mini-table th
{
	padding:0 4px;
	font-size:11px;
	height:22px;
	font-weight:bold;
	text-align:left;
	background:#252525;
	border-bottom:1px solid #ccc;
}

.bugList .bugs th a,.smalllist th a,.mini-table th a,.bugList .bugs th a:visited,.biglist th a:visited,.smallist th a:visited,.mini-table th a:visited
{
	color:#90A2C0;
}

#mainArea,#mainAreaContinued
{
	font:12px Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	color:#FFF;
	margin-left:12px;
	margin-right:12px;
	min-width:575px;
}

#mainArea .bugList .bugs td.rCB,#mainArea .bugList .bugs th.rCB,.biglist th.L,.biglist td.L
{
	border-left:1px solid #d6d6d6;
	background:#252525;
}

.bugList .bugs td,.biglist td
{
	font-size:11px;
	background:#252525;
}

html>body .bugList .bugs
{
	padding:0;
	-webkit-top-left-border-radius:5px;
	-webkit-top-right-border-radius:5px;
}

tr.g-r-more td, th.newIcon,th.newText
{
	background:#000 !important;
}

a.vb,a.uvb
{
	color:#90A2C0 !important;
}

a.uvb:visited, a.vb:visited
{
	color:#FFF !important;
}

a.actionButton:link,a.actionButton:visited,a.actionButton:hover,a.actionButton:active,a.actionButton:focus,a.actionButtonDisabled:link,a.actionButtonDisabled:visited,a.actionButtonDisabled:hover,a.actionButtonDisabled:active,a.actionButtonDisabled:focus
{
	padding:3px 6px;
	font-family:Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-size:11px;
	font-weight:bold;
	border-right:1px solid #ccc;
	border-bottom:1px solid #ccc;
	border-left:1px solid #d6d6d6;
	border-top:1px solid #d6d6d6;
	background:#151515;
	*display:inline-block;
}

a.actionButtonDisabled:link,a.actionButtonDisabled:visited,a.actionButtonDisabled:hover,a.actionButtonDisabled:active,a.actionButtonDisabled:focus
{
	text-decoration:none;
	color:#888;
	cursor:default;
}

a.actionButton:hover, a.actionButton:active, a.actionButton:focus, a.actionButton 
{
	color: #90A2C0 !important;
}

#fbsidebar,#fbsidebar th,#fbsidebar td
{
	font:11px Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	color:#FFF !important;
}
table.miniReport th.caption
{
	background:#555 !important;
}

table.miniReport td
{
	background:#333;
}

#bugviewContainer .bugevents #bugviewContainerEdit .bugevent .summary
{
	padding:9px 9px 9px;
	background-color:#111;
	-moz-box-shadow:inset 1px 1px #444;
	-webkit-box-shadow:inset 1px 1px #444;
	box-shadow:inset 1px 1px #444;
	-moz-border-radius:3px;
	-webkit-border-radius:3px;
	border-radius:3px;
}

div.editModeSelector
{
	background:#444;
	height:14px;
	text-align:right;
	font-size:10px;
	font-family:Verdana,Helvetica,Arial,sans-serif;
	border-bottom:1px solid #444;
	-moz-box-shadow:inset 0 1px #444;
	-webkit-box-shadow:inset 0 1px #444;
	box-shadow:inset 0 1px #444;
	padding:2px 4px;
	-webkit-user-select:none;
	-moz-user-select:none;
}

.cke_skin_kama .cke_wrapper
{
	background:#444;
}

.cke_1 .cke_wrapper, .cke_1_dialog .cke_dialog_contents, .cke_1_dialog a.cke_dialog_tab, .cke_1_dialog .cke_dialog_footer
{
	background:#444 !important;
}

.popupMenu,.favoritesPopup
{
	background:#111;
	border-left:1px solid #f1e9d6;
	border-top:1px solid #f1e9d6;
	border-right:1px solid #7e8276;
	border-bottom:1px solid #7e8276;
}

.popupMenu a:hover,.popupMenu a:active,.popupMenu a:focus,.popupMenu label.block:hover,#mainArea .popupMenu a:hover,#mainArea .popupMenu a:active,#mainArea .popupMenu a:focus
{
	text-decoration:none;
	padding:2px 9px;
	font-size:11px;
	color:#DDD;
	background:#000080
}

.popupMenu a:link,.popupMenu a:visited,.popupMenu label.block,.popupMenu div.popupHeadline,#mainArea .popupMenu a:link,#mainArea .popupMenu a:visited
{
	display:block;
	margin:0;
	text-decoration:none;
	padding:2px 9px;
	font-size:11px;
	width:100%;
	color:#FFF;
}

div.caseIdPopup
{
	position:absolute;
	z-index:4;
	width:425px;
	height:300px;
	border:1px solid #000;
	position:absolute;
	background:#222;
}

table.caseIdPopupTitle
{
	padding:0px 2px 0px 2px;
	background:#333;
}

td.emptyActionButtons
{
	background:#333;
}

td.middleLeft
{
	padding-bottom:3px;
	padding-right:15px;
	background-color:#454545;
	font-family:Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-size:11px;
	padding-left:3px;
}

td.middleRight
{
	padding-bottom:3px;
	background-color:#454545;
	width:100%;
}

.inverse 
{
	color:white;
}

.popover
{
	background:#222;
	-moz-box-shadow:0 1px 5px rgba(0, 0, 0, 0.25),0 0 2px rgba(0, 0, 0, 0.25);
	-webkit-box-shadow:0 1px 5px rgba(0, 0, 0, 0.25),0 0 2px rgba(0, 0, 0, 0.25);
	box-shadow:0 1px 5px rgba(0, 0, 0, 0.25),0 0 2px rgba(0, 0, 0, 0.25);
	-moz-border-radius:4px;
	-webkit-border-radius:4px;
	border-radius:4px;
	color:#CCC;
	display:none;
	font-size:12px;
	left:0;
	padding:10px 0;
	position:absolute;
	text-decoration:none;
	top:36px;
	width:300px;
	z-index:100;
}

.timeclockPopup
{
	background:#222!important;
}

p.dlg
{
	font-size:18px;
	font-family:Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-weight:bold;
	color:#FFF;
	width:100%;
	margin-top:0;
}

#username
{
	color:#FFF!important;
}

#mainArea .bugList .bugs .newCase th,#mainArea .bugList .bugs .emptyBorderTop th
{
	color:#AAA;
	background:#333;
	border:0;
	border-top:1px solid #d6d6d6;
}

.newCase
{
	border-bottom:20px;
}

.g-r-a td,.r-a td
{
	padding:2px 4px;
	background:#444;
}
.mini-table-container table tbody tr.row td
{
	background:#555;
}

p.headline
{
	color: white;
}

#bugviewContainer .bugevents .bugevent.detailed.email, #bugviewContainer .bugevents .pseudobugevent.detailed.email
{
	background:#444;
	box-shadow:none;
}

div.emailHeader,div.emailHeaderName,div.emailHeaderValue
{
	background:#036;
	color:#FFF!important;
	box-shadow:none;
	border:none;
}

div.emailBody 
{
	font-family: Verdana,'Helvetica Neue',Helvetica,Arial,sans-serif;
	font-size: 14px;
	word-wrap: break-word;
	background-color: #222;
}
td.newCaseCtrlTd input,td.newCaseCtrlTd,.bugList a.gridCatLink
{
	background: #333!important;
	color: #FFF;
}

.flat-down-arrow-black
{
border-top: 5px solid #FFF;
}
