css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gretschpages.com") {


/* gretschpages style ver 2.0 */
/* 2.1 changes profile text color to black */


h4 .button { padding: 0 15px; border: 1px solid #666; -moz-border-radius: 6px; border-radius: 6px; background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgba(119, 113, 98, 0.7)), to(rgba(55, 55, 45, 0.7))); background: -moz-linear-gradient(0% 0% 270deg, #f6f6f6, #c6c6c6); background-color: lightgrey !important; font-weight: bold; color: red !important; text-shadow: none !important; text-overflow: ellipsis; white-space: nowrap; text-decoration: none; line-height: 2em; color: black !important; }
h4 .button:hover, .button:active { background: #D2691E !important; color: blue !important; }

 .button a { background-color: red  !important; color: #black !important ; }
.button:active { background-color: red !important; font-weight: bold; color: blue !important;   }
.button .right { background-color: red !important; font-weight: bold; color: blue !important;   }

#main_nav { background:  none !important; color: black !important; text-shadow: none !important; }
#main_nav #menu a { border: 1px solid transparent; border-radius: 15px; display: block; padding: 2px 10px 3px; color: black !important; }
#main_nav #menu a:hover { background: #D2691E !important;  border: 1px inset rgba(75, 35, 8, 0.7); color: black !important; }
#main_nav #menu a span.right { background: LightGrey !important; color: black !important;  border-radius: 1.5em; padding: 0 3px 0 2px; text-align: center; font-size: 70%; display: inline-block; min-width: 1.5em; }

body {   background: none !important; background-color: white !important; text-shadow: none !important; }
#main div {   background-color: white !important; color: black   !important;  text-shadow: none !important; }
p {   line-height:140% ; font-size:90% !important; background-color: white !important; color: black ; text-shadow: none !important;}
li p a{ color: #D2691E !important; }
hgroup h1 { color: #D2691E; background-color: white !important; }
h1 { color: #D2691E !important; font-size: 175% !important; }
 li p { padding: 0 0 0 !important; }
#main h1 { background-color: white !important; }
#main { background-color: white !important; }

section h2 a { font-weight: bold !important; font-size: 75% !important; color: black !important; text-shadow: none !important; }
section address { font-weight: normal  !important ; font-size: 12px !important; line-height: 9px !important; text-shadow: none !important; }
section p a{ font-size: 85% !important; color: black !important; line-height: 85% !important; text-shadow: none !important; }
section time{ font-size: 85% !important; color: black !important; line-height: 85% !important; text-shadow: none !important; }
section p { font-size: 85% !important; color: black !important; line-height: 85% !important; text-shadow: none !important;}

#masthead hgroup h1 { background: transparent !important; margin: -4px !important ; padding: 0; margin-bottom: -10% !important ; }
#masthead hgroup h1 a img{ opacity:0.65 !important; }
#masthead hgroup h1 a{ position: float !important; width: 60% !important; max-width: 1000px !important; ; left: 6% ; right: 50% !important; }

#main ul li a { background-color: white !important; color: black !important; }
#main ul li a:hover { background-color: #D2691E !important; border: 1px inset rgba(75, 35, 8, 0.7); color: #fff; }

#secondary { background-color: white !important; color: black !important; }
#secondary a { background-color: white !important; color: black !important; }
#secondary a:hover { background-color: #D2691E !important; border: 1px inset rgba(75, 35, 8, 0.7); color: #fff; }
#container  { background-color: white !important; }

#forum_categories dt a { background-color: white !important; color: black !important; }
#forum_categories dt a:hover { background-color: #D2691E !important; color: white !important; }
#forum_categories dd  { background-color: white !important; color: black !important; }
ul li a { background-color: white !important; color: black !important; }
ul li a:hover { background-color: #D2691E !important; color: white !important; }

p span a { background-color: white !important; color: black !important; }
p span a:hover { background-color: #D2691E !important; border: 1px inset rgba(75, 35, 8, 0.7); color: #fff; }

#wrapper p { line-height: 140% !important; }
dd { background-color: white !important; color: black !important; }

}
