css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("club.pchome.net") {
body, div, td, th{font-family:Sans-Serif !important;}
a{/*font-family:Serif !important;*/}

#button a
{
	height:18px !important;
	line-height:18px !important;
}

#topic_search
{
	white-space:nowrap !important;
	padding-top:0 !important;
	padding:0 !important;
	margin:0 !important;
	width:549px !important;
	font-size:1px !important;
	letter-spacing:-4px !important;
}
#topic_search > *
{
	float:left !important;
	letter-spacing:normal !important;
	margin-right:1px !important;
	font-family:Sans-Serif !important;
}
#topic_search #searchKey
{
	width:323px !important;
}
#topic_search select
{
	height:20px !important;
	font-size:12px !important;
}
#topic_search #submit_seach
{
	margin-top:-1px;
	height:22px !important;
	width:68px !important;
}

#announcement {width:450px !important;}
#theme {float:right !important;}

#area #topic_select
{
	display:none;
}
#area #club_select
{
	font-size:medium;
}

/*reply*/
#reply {clear:both;}
#reply th{vertical-align:top !important;}
#message{width:100% !important; height: 10pc !important;}

/*list*/
div#left_content .list_title
{
	display:none !important;
}
div#left_content .list_item
{
	font-family:Sans-Serif !important;
}
div#left_content .list_item .i3
{
	font-size:50% !important;
	width:370px !important;
}
div#left_content .list_item .i3 a
{
	font-size:10pt !important;
}
div#left_content .list_item .i3 a.zhuti
{
	background-color:#eee !important;
	border:1px solid #ccc !important;
	font-size:150% !important;
	font-weight:bold !important;
	padding:0 2px !important;
}
div#left_content .list_item .i3 a.zhuti:hover
{
	background-color:#aaa !important;
	color:#fff !important;
	text-decoration:none !important;
}
div#left_content .list_item .i2,
div#left_content .list_item .i4
{
	font-size:100% !important;
	height:100% !important;
}
div#left_content .list_item .i2:before
{
	content:"/" !important;
}
div#left_content .list_item .i2
{
	position:relative !important;
	left:423px !important;
	margin-right:-100px !important;
	width:auto !important;
}
div#left_content .list_item .i4
{
	padding-right:55px !important;
	clear:right !important;
	text-align:right !important;
	width:50px !important;
}
div#left_content .list_item .i5,
div#left_content .list_item .i6,
div#left_content .list_item .i7,
div#left_content .list_item .i8
{
	float:right !important;
	height:auto !important;
	line-height:normal !important;
	width:auto !important;
	padding:0 !important;
}
div#left_content .list_item .i4,
div#left_content .list_item .i7 *,
div#left_content .list_item .i8
{
	color:#777 !important;
}
div#left_content .list_item .i5,
div#left_content .list_item .i7
{
	width:12em !important;
	text-align:left !important;
}
div#left_content .list_item .i7
{
	clear:right !important;
}
div#left_content .list_item .i6:after,
div#left_content .list_item .i8:after
{
	content:"by" !important;
	font-size:smaller !important;
	margin-right:4px !important;
}
div#left_content .list_item .i8:after
{
	margin-left:4px !important;
}

/*article*/
div#left_content div.item
{
	margin-bottom:-40px !important;
}
div#left_content div.item *
{
	font-family:Sans-Serif !important;
}
div#left_content div.item div.mc
{
/*	letter-spacing:1px;*/
}
div#left_content div.item div.p_time
{
	position:relative !important;
	float:right !important;
	top:-4em !important;
	border:0 !important;
}
div#left_content div.item div.o_info
{
	text-indent:-9999px !important;
}
div#left_content div.item div.o_info *
{
	display:none !important;
}
div#left_content div.item div.o_info span
{
	text-indent:0 !important;
	font-family:Sans Serif !important;
	font-size:180% !important;
	font-weight:bold !important;
	margin-top:-2.5em !important;
	display:block !important;
	background-color:#eee !important;
	padding:3px !important;
	border:1px solid #ccc !important;
}
}

@-moz-document url-prefix(http://club.pchome.net/topic_) {
div#area{display:none;}
div.topic_pager{clear:both;}
}
