css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sec.gov"), domain("idea.sec.gov")
{



body {
	background: #C8C8C9   
 top left repeat-x !important;
       text-align: justify !important;
       padding: 2% !important;
       width: 95% !important; 
       color: #11256E   !important;
       font-family: Tahoma, Arial, Helvetica, sans-serif !important;
}

span{
      text-align: justify !important;
      font-size: 12pt !important;
      color: #11256E   !important;
}

div {
      background: #C8C8C9   !important;
      text-align: justify !important;
      font-size: 12pt !important;
      color: #11256E   !important;
}

p {
      background: #C8C8C9   !important;
      text-align: justify !important;
      font-size: 12pt !important;
      color: #11256E   !important;
}

/*
  * headers
  * h1 , h2 , h3 , h4 , h5 , h6
  * caption , th , lh , dt , legend
 *
*/
h1  {
 color : #11256E   !important ;
 border-color :  #11256E   !important ;
 background-color :  #C8C8C9   !important ;
}

h2  ,
h3  ,
h4  ,
h5  ,
h6  ,
caption  , /* table h. */
th  , /* table-row|column h. */
lh  , /* list h. html3. how f***ing usefull element. deprecated. */
dl dt  , /* definition h. */
html fieldset legend  { /* form h. */
 color :  #11256E   !important ;
 border-color :  #11256E   !important ;
 background-color :  #C8C8C9   !important ;
}


td{
 color :  #11256E   !important ;
 border-color :  #11256E   !important ;
 background-color :  #C8C8C9   !important ;
}


hr{
 color :  #11256E   !important ;
 border-color :  #11256E   !important ;
 background-color :  #C8C8C9   !important ;
}

[class]{color:#11256E   !important} 

a:link { color: #348017 !important; }
a:visited { color: #FF0000 !important; }
a:active { color: #92CDFF !important; }
a:hover { color: #EFB233   !important }

}
