css:
/* AGENT_SHEET */
/*[[x-style-x]]*/

/*[[x-tumme-x]]*/

scrollbarbutton[sbattr="scrollbar-up-top"], scrollbarbutton[sbattr="scrollbar-down-top"], scrollbarbutton[sbattr="scrollbar-up-bottom"], scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: none !important;
    display: none !important;}
select scrollbarbutton[sbattr="scrollbar-up-top"], select scrollbarbutton[sbattr="scrollbar-down-top"], select scrollbarbutton[sbattr="scrollbar-up-bottom"], select scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: scalethumb-vertical !important;
    display: block !important;
     -moz-margin-start: 5px !important;}
scrollbar {
    -moz-appearance: none !important;
    background: none !important;
    -moz-box-align: start !important;
    position: relative !important;
    z-index: 1999999999 !important;}
richlistbox scrollbarbutton[sbattr="scrollbar-up-top"], richlistbox scrollbarbutton[sbattr="scrollbar-down-top"], richlistbox scrollbarbutton[sbattr="scrollbar-up-bottom"], richlistbox scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    -moz-appearance: separator !important;
    display: block!important;
    -moz-margin-start: -10000px !important;}
richlistbox scrollbar[orient="vertical"] {
    -moz-margin-start: -/*[[thickness]]*/ !important;
    width: /*[[thickness]]*/ !important;}
scrollbar[orient="vertical"] {
    -moz-margin-start: -/*[[thickness]]*/ !important;}
scrollbar[orient="vertical"] thumb {
    max-width: /*[[thickness]]*/ !important;
    min-width: /*[[thickness]]*/ !important;
    min-height: 80px !important;}
scrollbar[orient="horizontal"] thumb {
    max-height: /*[[thickness]]*/ !important;
    min-height: /*[[thickness]]*/ !important;
    min-width: 80px !important;}

style_setting_options.value:
scrollbar[orient="vertical"] thumb {
    -moz-appearance: none !important;
    border-radius: 2px !important;
    border: none !important;
    background-image: linear-gradient(to right, rgba(50,55,57,.8)0%, rgba(180,185,187,.8)1%  ,rgba(220,225,227,.8)20%,rgba(210,215,217,.8)40% , rgba(60,71,73,.8) 100%)!important;
    box-shadow: 0 0 0 8px rgba(42,71,71,.05) inset !important;}
scrollbar[orient="vertical"]:hover thumb {
    background-image: linear-gradient(to right, rgba(50,55,57,.9)0%, rgba(200,205,207,.9)1%  ,rgba(220,225,227,.9)20% , rgba(70,81,83,.9) 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;}
scrollbar[orient="vertical"]:active thumb {
    background-image: linear-gradient(to right, rgba(50,55,57,1)0%, rgba(200,205,207,1)1%  ,rgba(220,225,227,1)15% , #3C444C 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;}
scrollbar[orient="horizontal"] thumb {
    -moz-appearance: none !important;
    border-radius: 2px !important;
    border: none !important;
    background-image: linear-gradient(to bottom, rgba(50,55,57,.8)0%, rgba(180,185,187,.8)1%  ,rgba(220,225,227,.8)20%,rgba(210,215,217,.8)40% , rgba(60,71,73,.8) 100%)!important;
    box-shadow: 0 0 0 8px rgba(42,71,71,.05) inset !important;}
scrollbar[orient="horizontal"]:hover thumb {
    background-image: linear-gradient(to bottom, rgba(50,55,57,.9)0%, rgba(200,205,207,.9)1%  ,rgba(220,225,227,.9)20% , rgba(70,81,83,.9) 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;}
scrollbar[orient="horizontal"]:active thumb {
    background-image: linear-gradient(to bottom, rgba(50,55,57,1)0%, rgba(200,205,207,1)1%  ,rgba(220,225,227,1)15% , #2A4747 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;}

scrollbar [orient="vertical"] thumb{
    -moz-appearance: none !important;
    border-radius: 2px !important;
    background: linear-gradient(to right, #78aae4 0%, #95c4eb 50%, #88bdec 51%, #beddf4 100%) !important;
    border: 1px solid rgba(60,60,255,1) !important; border-style: outset !important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.1) inset !important;
    transition: all 0s !important; 
    opacity: .8 !important; }
scrollbar [orient="horizontal"] thumb {
    -moz-appearance: none !important;
    border-radius: 2px !important;
    background: linear-gradient(to bottom, #78aae4 0%, #95c4eb 50%, #88bdec 51%, #beddf4 100%) !important;
    border: 1px solid rgba(60,60,255,1) !important; border-style: outset !important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.1) inset !important;
    transition: all 0s !important; 
    opacity: .8 !important; }
scrollbar:hover thumb {
    box-shadow: 0 0 0 8px rgba(50,77,143,.3) inset !important;
    opacity: .8 !important; 
    transition: all .5s !important; }
scrollbar:active thumb{
    box-shadow: 0 0 0 8px rgba(0,27,93,.3) inset !important;
    opacity: .8 !important; 
    transition: all .5s !important; }

scrollbar [orient="vertical"] thumb{ 
    -moz-appearance: none !important; 
    border-radius: 4px !important;
    background: radial-gradient(to right, hsla(120,9%,78%,.50), #e5e5e5) !important;
    border-top: 2px solid #ddd !important;
    border-bottom: 2px solid #777 !important;
    border-left: 2px solid #ccc!important;
    border-right: 2px solid #777 !important;
    box-shadow: inset 1px 1px 1px #808080, inset -1px -1px 1px 0px #e9e9e9!important;}
scrollbar [orient="horizontal"] thumb {
    -moz-appearance: none !important; 
    border-radius: 4px !important;
    background: radial-gradient(to bottom, hsla(120,9%,78%,.50), #e5e5e5) !important;
    border-top: 2px solid #ddd !important;
    border-bottom: 2px solid #777 !important;
    border-left: 2px solid #ccc!important;
    border-right: 2px solid #777 !important;
    box-shadow: inset 1px 1px 1px  #808080, inset -1px 1px 1px 0px #e9e9e9!important;}
scrollbar[orient="vertical"]:hover thumb {
    -moz-appearance: none !important;
    background: radial-gradient(to right, hsla(120,9%,68%,.5), #d5d5d5) !important;
    box-shadow: inset 1px 1px 1px 0px #808080, inset -1px -1px 1px 0px #e9e9e9!important;}
scrollbar[orient="horizontal"]:hover thumb {
    -moz-appearance: none !important;
    background: radial-gradient(to bottom, hsla(120,9%,68%,.5), #d5d5d5) !important;
    box-shadow: inset 1px 1px 1px 0px #808080, inset -1px 1px 1px 0px #e9e9e9!important;}
scrollbar[orient="vertical"]:active thumb {
    -moz-appearance: none !important;
    background: radial-gradient(to right, hsla(120,9%,58%,.5), #c5c5c5) !important;
    box-shadow: inset 1px 1px 2px  #555, inset -1px 1px 1px 0px #e9e9e9!important;}
scrollbar[orient="horizontal"]:active thumb {
    -moz-appearance: none !important;
    background: radial-gradient(to bottom, hsla(120,9%,58%,.5), #c5c5c5)  !important;
    box-shadow: inset 1px 1px 2px #555, inset -1px 1px 1px 0px #e9e9e9!important;}

scrollbar [orient="vertical"] thumb{
    -moz-appearance: none !important;
    border-radius: 10px !important;
    border: 1px solid !important;
    -moz-border-top-colors: rgba(200,200,200,.5) rgba(75,75,75,.5) !important; 
    -moz-border-right-colors: #bbb !important;
    -moz-border-bottom-colors: rgba(75,75,75,.3) rgba(200,200,200,.7) !important; 
    -moz-border-left-colors: #ddd !important;
     background-image: linear-gradient(to right, #FFF 0%, #F0F0F0 25%, #C2C2C2 75%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: 0.8 !important;}
scrollbar[orient="vertical"]:hover thumb {
    background-image: linear-gradient(to right , #FFF 0%, #e5e5e5 30%, #b7b7b7 85%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: .9 !important;}
scrollbar[orient="vertical"]:active thumb {
    background-image: linear-gradient(to right, #f5f5f5 0%, #e9e9e9 12%, #a5a5a5 75%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: 1 !important;
    border: 1px solid !important;
    -moz-border-top-colors: rgba(200,200,200,.5) rgba(0,0,0,.5) !important; 
    -moz-border-right-colors: #888 !important;
    -moz-border-bottom-colors: rgba(0,0,0,.3) rgba(200,200,200,.7) !important; 
    -moz-border-left-colors: #ccc !important;}
scrollbar[orient="horizontal"] thumb {
    -moz-appearance: none !important;
    border-radius: 10px !important;
    border: 1px solid !important;
    -moz-border-right-colors: rgba(200,200,200,.5) rgba(75,75,75,.5) !important; 
    -moz-border-bottom-colors: #bbb !important;
    -moz-border-left-colors: rgba(75,75,75,.3) rgba(200,200,200,.7) !important; 
    -moz-border-top-colors: #ddd !important;
     background-image: linear-gradient(to bottom, #FFF 0%, #F0F0F0 25%, #C2C2C2 75%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: 0.8 !important;}
scrollbar[orient="horizontal"]:hover thumb {
    background-image: linear-gradient(to bottom, #FFF 0%, #e5e5e5 30%, #b7b7b7 85%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: .9 !important;}
scrollbar[orient="horizontal"]:active thumb {
    background-image: linear-gradient(to bottom, #f5f5f5 0%, #e9e9e9 12%, #a5a5a5 75%, #8F8F8F 100%)!important;
    box-shadow: 0 0 0 8px rgba(100,127,193,.05) inset !important;
    opacity: 1 !important;
    border: 1px solid !important;
    -moz-border-right-colors: rgba(200,200,200,.5) rgba(0,0,0,.5) !important; 
    -moz-border-bottom-colors: #888 !important;
    -moz-border-left-colors: rgba(0,0,0,.3) rgba(200,200,200,.7) !important; 
    -moz-border-top-colors: #ccc !important;}

/* ----Always visible---- */
/*
scrollbar [orient="vertical"] thumb, scrollbar [orient="horizontal"] thumb{
    -moz-appearance: none !important;
    border: none !important;
    background: none !important;
    box-shadow: none !important;}
*/
/* -------------------------- */

/* ----Hidden until hover---- */

scrollbar [orient="vertical"] thumb, scrollbar [orient="horizontal"] thumb{
    -moz-appearance: none !important;
    border: none !important;
    background: none !important;
    box-shadow: none !important;}

