css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("chrome://alertbox/content/inbox") {

body {background-color:#FFF3C9 !important;}
#global-links tbody tr td:first-child:before {content:url("chrome://alertbox/skin/icons/bell_16.png")" Alert Box"; color:#1C417C; font-weight:bold;}
h2 {display:none;}

.i-li-item {cursor:pointer;}
span.link:hover {text-decoration:none !important;}

.line-top, .line-bottom {display:none;}
.i-body-wrap {background-color:transparent !important;}
.i-linkbar-ct {padding:0 !important; font-size:80%; color:#666;}
.i-body {margin:0 !important; border-radius:5px; box-shadow: 0 0 3px #6694E3;}
#ext-gen2, .i-list > table > thead > tr.title {background:none !important;}
.i-list > table > thead > tr:nth-child(2) {border:0 !important;}

.i-li-grouper {background-color:#fff !important;}
a.grouper {background-color:#F6EA9D; color:#a22 !important; padding-right:0.5em;}

.i-list-item:not(.expanded) {background-color:rgba(239, 239, 239, 0.6) !important;}
.i-list-item:hover {background-color:#FFF3C9 !important;}

.history img {margin:0 5px;}

.i-btn-content{line-height:1.5em !important;}
#ext-gen8{font-size:70% !important; float:none !important; padding-left:10px !important;}

#global-links-footer {padding-top:8px; color:#999;}

.i-pop-shell.slimborder .i-body{border:none !important;border-radius:0px;margin-top:2px !important;}

}
