css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
 div[data-component-term="user_recommendations"],
.module.trends,
 div[data-component-term="footer"],
 div[data-component-term="similar_user_recommendations"],
 div[data-component-term="newest_list_members"],
 .wtf-module,
 .module.site-footer{display:none !important;}
 .dashboard,.content-main{width:auto !important;clear:both !important;float:inherit !important;}
 .profile-summary,.js-mini-profile-stats-container{display:none !important;}
 .tweet-box{ border-radius:4px !important; }
textarea.twitter-anywhere-tweet-box-editor{width:98% !important;}
 
 #doc.route-profile #page-outer #page-container.wrapper .dashboard,
 #doc.route-profile #page-outer #page-container.wrapper .content-main,
 #doc.route-Connect #page-outer #page-container.wrapper .dashboard,
 #doc.route-Connect #page-outer #page-container.wrapper .content-main,
 #doc.route-discover #page-outer #page-container.wrapper .dashboard,
 #doc.route-list #page-outer #page-container.wrapper .dashboard,
 #doc.route-discover #page-outer #page-container.wrapper .content-main{float:left !important;clear:none !important;}
 
 #doc.route-profile #page-outer #page-container.wrapper .dashboard,
 #doc.route-Connect #page-outer #page-container.wrapper .dashboard,
 #doc.route-list #page-outer #page-container.wrapper .dashboard,
 #doc.route-discover #page-outer #page-container.wrapper .dashboard{width:26% !important; clear:none !important;}
 
 #doc.route-profile #page-outer #page-container.wrapper .content-main,
 #doc.route-Connect #page-outer #page-container.wrapper .content-main,
 #doc.route-list #page-outer #page-container.wrapper .content-main,
 #doc.route-discover #page-outer #page-container.wrapper .content-main{margin-left:2% !important;width:72% !important;}
 
 
 #doc.route-list #page-outer #page-container.wrapper .content-main{clear:none !important; float:left !important;}
 
 #doc.route-profile #page-outer #page-container.wrapper .dashboard .twitter-anywhere-tweet-box-editor{width:90% !important;}
}
