css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

  body {
background: url('http://i44.tinypic.com/2uti5ar.jpg') bottom left #000000 no-repeat fixed !important;
 }
 
.selection_nipple.white, .selection_nipple {
display: none !important;
}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:transparent !important;
}

.notification, .notification.single_notification, .notification.first_notification, .notification.last_notification, .notification.alt {
background-color:transparent !important;
border:none !important;}

.nipple {
display: none !important;}

#posts .post_full .post_permalink { width: 23px; height: 23px;
background: url('http://28.media.tumblr.com/tumblr_lyxyzow5Vr1qa7g4ro1_100.gif') transparent !important;
position: absolute;
top: -4%;
right: -5%;
display: none;}


#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url(http://29.media.tumblr.com/tumblr_lyxz02JsMy1qa7g4ro1_250.png) no-repeat !important;}
}

.MissingE_tagslist_title.recessed {
background: rgba(0,0,0,0) !important;
}



@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {body {background:none!important;}}
