css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("what.cd") {

body {
  font-family: Tahoma, Arial, helvetica, sans-serif !important;
  font-size: 8pt !important;
  background-color: #E4ADED !important;
  
  color: #75466E !important;
  margin-top: 0px !important;
  margin-bottom: 0px !important;
  padding-top: 0px !important;
  padding-bottom: 0px !important;
}

#userinfobar {
background: #ECE9D8 !important;
color: #000000 !important; 
margin-top:0px !important; 
margin-bottom:20px !important; 
border: none !important;
}


#fullbody { 
background: #E4ADED repeat !important; 
padding: 0 !important; 
margin: 20px !important; 
}

#footer { 
background: #E6AEEE !important;
color: #000000 !important; 
padding: 0 !important; 
margin: 0 !important; 
}

table.optional          { background: transparent !important;}
td.optional             { border-width: 0px !important; }
td.altpoll              { border: none !important; text-align: left !important; }

table {
  border-collapse: collapse !important;
  border: none !important;
  background: #E4ADED none !important;
}

table.main, table.mainouter {
  background-color: #ECE9D8 !important;
}

table.bottom {
  background: transparent !important;
}


td.outer {
background: #ECE9D8 none repeat scroll 0% !important;
}

td.bigouter {
background: #ECE9D8 none repeat scroll 0% !important;
}



h1 {
  font-size: 12pt !important;
  color: #000000 !important;
}

h2 {
  font-size: 10pt !important;
  margin-bottom: 6px !important;
  color: #000000 !important;
}

p {
  font-size: 8pt !important;
  color: #000000 !important;
}

p.sub { 
  margin-bottom: 4pt !important;
}

td {
  font-size: 8pt !important;
  border: solid #000000 1px !important;
}

td.embedded {
  border: none !important;
  padding: 0px !important;
  text-align: left !important;
}

td.bottom {
  border: none !important;
}

td.heading {
  font-weight: bold !important;
}

td.text {
  padding: 10pt !important;
  text-align: left !important;
}

td.info {
  padding: 10pt !important;
  text-align: left !important;
  background-color: #f4f4f0 !important;
}

td.comment {
  padding: 10pt !important;
  font-size: 8pt !important;
  text-align: left !important;
}

td.colhead {
  font-weight: bold !important;
  color: #ffffff !important;
  background-color: #A64FC3 !important;
  background: none !important;
}

td.rowhead {
  font-weight: bold !important;
  text-align: right !important;
  vertical-align: top !important;
}

td.title {
  font-size: 14pt !important;
}

td.navigation {
  background: #ECE9D8 none repeat scroll 0% !important;
  font-weight: bold !important;
  font-size: 10pt !important;
  border: none !important;
}

td.clear {
  padding: 0px !important;
  border: 0px !important;
  background: transparent !important;
}

form {
  margin-top: 0 !important;
  margin-bottom: 0 !important;
}

.sublink {
  font-style: italic !important;
  font-size: 7pt !important;
  font-weight: normal !important;
}

font.transparent
{
  color: #ECE9D8 !important;
}

font.gray
{
  color: #ACA899 !important;
  text-decoration: underline !important;
}

a:link, a:visited {
  text-decoration: underline !important;
  color: #000000 !important;
}

a:hover {
  color: #FF0000 !important;
}

a.index {
  font-weight: bold !important;
}

a.biglink {
  font-weight: bold !important;
  font-size: 12pt !important;
}

a.altlink:link, a.altlink:visited{
  font-weight: bold !important;
  color: #A83838 !important;
  text-decoration: none !important;
}

a.altlink:hover {
  text-decoration: underline !important;
}

a.altlink_blue:link, a.altlink_blue:visited{
  font-weight: bold !important;
  color: #004E98 !important;
  text-decoration: none !important;
}

a.altlink_blue:hover {
  text-decoration: underline !important;
}

a.altlink_green:link, a.altlink_green:visited{
  font-weight: bold !important;
  color: #007800 !important;
  text-decoration: none !important;
}

a.altlink_green:hover {
  text-decoration: underline !important;
}

.important {
  font-weight: bold !important;
  font-size: 8pt !important;
}

.red {
  color: #ee0000 !important;
}

.yellow {
  color: #997700 !important;
}

.green {
  color: #000000 !important;
}

input, select, textarea {
  font-family: Tahoma, Arial, helvetica, sans-serif !important;
  font-size: 8pt !important;
}

.btn {
  width: 75px !important;
  height: 23px !important;
}

.small {
  font-size: 7pt !important;
}

.big {
  font-size: 10pt !important;
}

li  {
  text-align: left !important;
  margin-top: 6pt !important;
  margin-bottom: 6pt !important
}

ul {
  margin-left: 16pt !important;
  margin-top: 0px !important;
  margin-bottom: 0px !important;
}

img.border {  
  border-style: solid !important;  
  border-color: #000000 !important; 
  border-width: 1px !important; 
}

.link1 {
  color: #1900D1 !important;
  text-decoration:underline !important;
}
.link2 {
  color: #800000 !important;
  text-decoration:underline !important;
}

}
