@-moz-document regexp("http([s]?)://(.*)facebook.com(.*?)") {
    html {display:none !important;}
        body.ego_page {background-color:#efefef !important;}
	body.hasLeftCol{background-color:#000000 !important;}
	#blueBar {background-color:#7B0000 !important;border-bottom:1px solid #831337 !important;}
	.slimHeader #pageNav .topNavLink:after {background-color:#000000 !important;}
    .slimHeader #pageNav .topNavLink:hover, .slimHeader #pageNav .topNavLink:hover > a, .slimHeader #pageNav .topNavLink:hover:after, .slimHeader #pageNav .topNavLink:focus, .slimHeader #pageNav .topNavLink:focus > a, .slimHeader #pageNav .topNavLink:focus:after, .slimHeader #pageNav .topNavLink:active, .slimHeader #pageNav .topNavLink:active > a, .slimHeader #pageNav .topNavLink:active:after {color:#000000 !important;background-color:#ffffff !important;}
    #navSearch .uiTypeahead, #navSearch .uiTypeahead .wrap, .slimHeader #pageNav .headerTinymanPhoto {border-color:#000000 !important;}
    
    #pageLogo a {background-color:#7B0000 !important;}
    .slimHeader a.jewelButton:hover {background-color:#ffffff !important;}
    body.hasLeftCol #leftCol {margin-top:0px !important;padding:0px 0px 0px 2px !important;background-color:#eeeeee !important;border-radius:0px 0px 0px 6px !important;-moz-border-radius:0px 0px 0px 6px !important;-webkit-border-radius:0px 0px 0px 6px !important;-o-border-radius:0px 0px 0px 6px !important;}
    body.hasLeftCol #pagelet_left_column {color:#ffffff !important;background-color:#444444 !important;border-radius:0px 0px 0px 6px !important;-moz-border-radius:0px 0px 0px 6px !important;-webkit-border-radius:0px 0px 0px 6px !important;-o-border-radius:0px 0px 0px 6px !important;border-bottom:1px solid #eeeeee;margin-left:1px;}
    body.hasLeftCol #pagelet_left_column a {color:#cccccc !important;}
    body.hasLeftCol #pagelet_left_column a:hover {color:#cccccc !important;}
    body.hasLeftCol #pagelet_profile_picture {background-color:#000000 !important;border-left:3px solid #000000 !important;border-bottom:2px solid #eeeeee;background-color:#000000 !important;margin-left:-4px !important;}
    body.hasLeftCol #pagelet_profile_picture img {background-color:#000000 !important;}
    body.hasLeftCol #pagelet_welcome_box {margin-top:20px !important;}
    body.hasLeftCol .uiHeaderNav h4 {color:#eeeeee !important;}
    body.hasLeftCol .uiSideNav .sideNavItem.selectedItem a, body.hasLeftCol .uiSideNav .sideNavItem:hover a {background-color:#eeeeee !important;}
    body.hasLeftCol .uiSideNav .sideNavItem.selectedItem a .linkWrap, body.hasLeftCol .uiSideNav .sideNavItem:hover a .linkWrap {color:#000000 !important;}
    body.hasLeftCol #pagelet_left_column .profile-friends .profileFriendsText {white-space:normal !important;word-wrap:normal !important;}
    body.home #sideNav .moreSectionsLink {color:#242424 !important;margin-left:4px !important;margin-bottom:1em !important;}
    #contentCol, .fbFlyoutDialog .uiScrollableAreaContent {background-color:#efefef !important;}
    body.hasLeftCol .uiStreamStory .UIImageBlock_MED_Image > img, #leftCol .UIImageBlock_MED_Image > img {border:1px solid #000000 !important;border-radius:6px !important;-moz-border-radius:6px !important;-webkit-border-radius:6px !important;-o-border-radius:6px !important;}
     body.hasLeftCol *:not(.timelineUnitContainer) .uiUfiComments {border:none !important;}
    body.hasLeftCol *:not(.timelineUnitContainer) .uiUfiComments li.uiUfiComment, body.hasLeftCol *:not(.timelineUnitContainer) li.uiUfiAddComment, body.hasLeftCol *:not(.timelineUnitContainer) li.uiUfiLike {border:none !important;border-top:1px solid #efefef !important;color:#000000 !important;background-color:#999999 !important;}
    body.hasLeftCol *:not(.timelineUnitContainer) .uiUfiComments li.uiUfiComment a, body.hasLeftCol *:not(.timelineUnitContainer) li.uiUfiAddComment a {color:#7B0000 !important;}
    body.hasLeftCol *:not(.timelineUnitContainer) li.uiUfiLike a {color:#242424 !important;}
    body.hasLeftCol .fbQuestionsPollResultsBar {border-color:#a4a4a4 !important;background-color:#424242 !important;}
    body.hasLeftCol .fbQuestionsPollResultsBar .shaded {background-color:#4B0000 !important;border-color:#999999 !important;}
    body.hasLeftCol .fbQuestionsPollResultsBar .label {color:#ffffff !important;}
    body.hasLeftCol .fbQuestionsPollResultsBar .auxlabel .fcg {color:#000000 !important;}
    body.hasLeftCol #pagelet_side_ads, #contentCurve {display:none !important;}
    body.hasLeftCol .permalink_stream ul.uiStream > li.uiStreamStory span.messageBody {color:#ffffff !important;}
    body.hasLeftCol ul.uiStream > li.uiStreamStory .commentActions > .timestamp {color:#222222 !important;}
    body.hasLeftCol .uiUfiComment a.UIImageBlock_SMALL_Image > img {border:1px solid #000000 !important;border-radius:3px !important;-moz-border-radius:3px !important;-webkit-border-radius:3px !important;-o-border-radius:3px !important;}
    body.hasLeftCol .uiStream .uiAttachmentDesc, .uiStream .uiStreamPassive, .uiStream .uiStreamRobotextBeforeMessage, .uiStream .uiAdminAttribution, .uiStream .fcg {color:#222222 !important;}
    body.hasLeftCol .uiHeaderSection, .uiSideHeader {background-color:#aaaaaa !important;}
    body.hasLeftCol .uiHeader h3, .uiHeader h4 {color:#000000 !important;}
    body.hasLeftCol .uiHeaderTitle .uiHelpLink {background-color:#ffffff !important;}
    body.hasLeftCol .uiBoxWhite {background-color:#efefef !important;}
    body.hasLeftCol .uiComposerWhiteMessageBox .uiComposerMessageBox {background-color:#efefef !important;}
    body.hasLeftCol .uiComposer .attachmentFrame {background-color:#ffffff !important;}
    body.hasleftCol .uiStepList ol {border-right:1px solid #ffffff !important;}
    body.hasLeftCol .uiStream .uiStreamHeader .uiStreamHeaderChronologicalForm, body.hasLeftCol .uiStream .uiStreamHeader .uiStreamHeaderText {background-color:#efefef !important;}
    .MessagingReadHeader {background-color:#efefef !important;}
    .fbFlyoutDialog .tickerDialogFooter div.mentionsAddComment {background-color:#EDEFF4 !important;}
/* Timeline */
.timelineLayout, .timelineLayout #globalContainer, .timelineLayout #contentCol {background-color:#242424 !important;}
body.timelineLayout .fbTimelineTimePeriod, .timelineLayout #admin_panel_show, body.timelineLayout #rightColContent .pagesTimelineButtonPagelet, body.timelineLayout .fbTimelineScrubber {background-color:#242424 !important;}
body.timelineLayout .fbTimelineTopSection, body.timelineLayout #fbTimelineHeadline {background-color:#efefef !important;}
body.timelineLayout .fbTimelineCapsule .timelineUnitContainer {background-color:#efefef !important;padding:15px !important;border:1px solid #C4CDE0 !important;border-radius:3px !important;-moz-border-radius:3px !important;-webkit-border-radius:3px !important;}
body.timelineLayout .fbTimelineScrubber a, body.timelineLayout .fbTimelineScrubber .selected ul a {border-left-color:#ffcdcd !important;color:#dd4444 !important;}
body.timelineLayout .fbTimelineScrubber a:hover {border-left-color:#e02200 !important;}
body.timelineLayout .fbTimelineScrubber .selected a {color:#e02200 !important;}
body.timelineLayout .fbTimelineScrubber .selected a, body.timelineLayout .fbTimelineScrubber .selected ul a {border-left-color:#E02200 !important;}
body.timelineLayout .fbTimelineCapsule div.fbTimelineComposerUnit {padding:0px 5px 6px !important;}
body.timelineLayout .fbTimelineCapsule .timelineUnitContainer:hover {background-color:#ffffff !important;}
body.timelineLayout .uiButtonGroup {background-color:#CD4444 !important;}
body.timelineLayout .uiButton .uiButtonText {color:#ffffff !important;}
body.timelineLayout .uiComposerMessageBoxControls .uiButtonText, body.timelineLayout .fbTimelineEditCoverButton .uiButtonText, body.timelineLayout .fbTimelineProfilePicButton .uiButtonText, body.timelineLayout .fbTimelineFilters #fbTimelinePagesFilterSelectorButton .uiButtonText {color:#000000 !important;}
body.timelineLayout .uiSelector .openToggler .uiSelectorButton, body.timelineLayout .uiSelector .openToggler .uiSelectorButton:active, body.timelineLayout .uiSelector .openToggler .uiSelectorButton:focus, body.timelineLayout .uiSelector .openToggler .uiSelectorButton:hover {background-color:#aa0000 !important;border-color:#CD4444 #CD0000 !important;}
body.timelineLayout li.fbTimelineUnit .topBorder, body.timelineLayout li.fbTimelineUnit .bottomBorder, .fbTimelineVerticalAds {display:none !important;}
body.timelineLayout #fbTimelineHeadline h2 a {color:#aa0000 !important;}
.fbTimelineShowOlder .uiMorePager a.uiMorePagerPrimary, .fbTimelineShowOlder .uiMorePager a.uiMorePagerPrimary:hover, .fbTimelineContentHeader h3.uiHeaderTitle, .fbTimelineInlineSectionHeader h3.uiHeaderTitle {background-color:#FFCDCD !important; color:#000000 !important;}
.fbTimelineFilters .openToggler #fbTimelinePagesFilterSelectorButton {background-image: url('http://s-static.ak.facebook.com/rsrc.php/v1/y5/r/ZWvAgFWgfHn.png') !important;}
.fbTimelineFilters .openToggler #fbTimelinePagesFilterSelectorButton .uiButtonText {color:#ffffff !important;}
.fbTimelineShowOlder .uiMorePager a.uiMorePagerPrimary:hover {background-color:#ffcdcd !important;}
.profileEditButton, .profileEditButton:hover {background-color:rgba(205, 20, 20, 0.8) !important;}
.fbTimelineSection > .loadingIndicator {background-color:#242424 !important;}
ul.fbTimelineComposerAttachments .uiComposerAttachment .nub {top:26px !important;}
    html {display:block !important; position:absolute !important;top:0px;left:0px;right:0px;width:100% !important;height:auto !important;}
}
