css:
body.main-page {
	background:#20190f !important;
}
#template_body {
	background: #302719 !important;
}

/* new beta cr beta layot things... */
#template_body_beta {
	background: #302719 !important;
}
#header_beta {
	margin-bottom: 0 !important;
}
ul.topbar-nav-sub li.dim a:hover {
	background: #F78C25 !important;
	border-bottom: 1px solid #F78C25 !important;
	color: #fff !important;
	font-weight: bold !important;
}
/* somewhere near this comment should be end of new beta things */

/* base orange color
	#ffa626
*/
.showforumtopic-messages td {
	background: #13120f !important;
	color: #666 !important;
}
.showforumtopic-message-user,
.showforumtopic-message-footer {
	border-right: 0 !important;
}
.showforumtopic-message-footer {
	border-bottom: 10px solid #302719 !important;
}

/* remove social stuff */
#root,#header_social  {
	display: none !important;
}

/* better (at least I think that way) current forum widget */
.widget-forumnav {
	background-image: linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
	background-image: -o-linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
	background-image: -moz-linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
	background-image: -webkit-linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
	background-image: -ms-linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
}
div#showforumtopic .widget-forumnav:hover {
	height: auto;
}

div#showforumtopic .widget-forumnav {
	height: 15px;
}

.widget-forumnav .widget-forumnav-spacer,.widget-forumnav-children-roundrect-top,.widget-forumnav-children-roundrect-bottom {
	display: none !important;
}
.widget-forumnav-children-roundrect, #template_container.template-container-beta, #template_container.template-container {
	background: transparent !important;
}

.widget-forumnav-ancestor, .widget-forumnav-current {
	display: inline !important;
}

div#showforumtopic .widget-forumnav .widget-forumnav-children-roundrect {
	visibility: hidden !important;
	background: transparent !important;
}
div#showforumtopic div.widget-forumnav:hover > div.widget-forumnav-children-roundrect {
	visibility: visible !important;
}

.widget-forumnav-children td, .widget-forumnav-branch {
	color: #666 !important;
}

/* some ad... */
.showforumtopic-ad-footer, #template_ad_leaderboard {
	display: none !important;
}

#footer {
	border: 0 !important;
	background-image: -o-linear-gradient(bottom, rgb(19,18,15) 34%, rgb(19,18,15) 75%, rgb(10,10,8) 100%);
}
#footer_menu {
	border: 0 !important;
	background: #13120f !important;
}

/* invisible magic */
.showforumtopic-message-contents-header-actions,
.showforumtopic-message-contents-footer-actions {
	visibility: hidden;
}
.showforumtopic-message-contents:hover > .showforumtopic-message-contents-header .showforumtopic-message-contents-header-actions  {
	visibility: visible;
}
.showforumtopic-message-footer:hover > .showforumtopic-message-contents-footer-actions {
	visibility: visible;
}

/* new frame for avatars */
.showforumtopic-message-user-thumblink img {
	background: #000 !important;
	box-shadow: 0px 0px 5px 3px #000;
	padding: 1px !Important;
}

/* quotes */
.bb-quote {
	background: #161511 !important;
	border: 1px solid #302719 !important;
}
.bb-quoters, .bb-quoters-end {
	display: none !important;
}

/* prev, next page buttons */
.paginator-lite {
	background: #12110e !important;
	border: 1px solid #13120f !important;
}
.paginator-lite:hover {
	background: #1d1c16 !important;
	color: #787878 !important;
}
.paginator-lite-selected {
	background: #ffa426 !important;
	border: 1px solid #302719 !important;
}
.paginator-lite-disabled {
	background: #231e17 !important;
	border: 1px solid #29241d !important;
	color: #333 !important;
}
a.paginator-lite[title="First"] {content: "«" !important}
a.paginator-lite[title="Last"] {content: "»" !important}


/* search tool */
.search-form-channel input[type="text"] {
	background: #13120f !important;
	color: #ffa426 !important;
	border: 1px solid #302719 !important;
}

/* spoiler tag */
.bb-spoiler {
	background: transparent !important;
}
.bb-spoiler-highlight {
	background: transparent !important;
}

/* forum topics */
.widget-forumcategory-display th, .widget-forumcategory-display td {
	border: 0 !important;
}
img[src="http://static.ak.crunchyroll.com/i/icons/sticky_lock.gif"] {
	color: #ffa626 !important;
	content: "§" !important;
}
img[src="http://static.ak.crunchyroll.com/i/icons/sticky.gif"] {
	color: #ffa626 !important;
	content: "φ" !important;
}
.widget-forumcategory-display {
	background: #13120f !important;
	color: #aaa !important;
}
.widget-forumcategory-display tr:hover {
	background: #353126 !important;
	color: #d2d2d2 !important;
}
.widget-forumcategory-display tr:hover > .widget-forumcategory-display-title .widget-forumcategory-display-nomod a {
	color: #e9e7e1 !important;
}
.widget-forumcategory-display-title .widget-forumcategory-display-firstfewpage:hover {
	background: transparent !important;
}
.widget-forumcategory-display tr td:nth-child(4), .widget-forumcategory-display-starter {
	display: none !important;
}
.widget-forumcategory-display .widget-forumcategory-display-title {
	width: auto !important;
}

/* list all forums */
#forum_main #forum_main_left .forum-category {
	background: #13120f !important;
	color: #666 !important;
}
#forum_main #forum_main_right {
	border: 0 !important;
}
.forum-category-head {
	border: 0 !important;
	padding: 5px 0 0 5px !important;
}
.forum-category {
	margin-bottom: 15px !important;
}
.topbar-nav-trail {
	display: none !important;
}

#forum_main #forum_main_right .forum_main-topics {
	background: #13120f !important;
	color: #666 !important;
	padding: 5px !important;
}
#forum_main_right .forum_main-topics h2 {
	border: 0 !important;
}
#forum_main_right .forum_main-topics li:hover {
	background: #353126 !important;
	color: #d2d2d2 !important;
}
#forum_main_right .forum_main-topics li:hover > .forum_main-topic-desc .forum_main-topic-desc-text .forum_main-topic-desc-title {
	color: #e9e7e1 !important;
}
.forum_main-topics .forum_main-topic-desc {
	background: transparent !important;
}
.forum_main-topic-desc-numcomments {
	background: transparent !important;
}
.forum_main-topic-desc-numcomments:after {
	content: " replies";
}
