*{
margin          :0    !important;
padding         :0    !important;
font-size       :1em  !important;
list-style-image:none !important;
border          :none !important;
}

body          { background-image:none !important;}
div#container {width:auto !important;}

div#leftside                  {width:auto !important; float:none !important; padding:0.5em 1em !important;}
div#leftside div.entry        {border: 3px solid #e8e7dc !important;}
div#leftside h2               {font-size:1.2em !important; background-color:#e8e7dc !important; background-image:none !important; padding:0.5em 1em !important; margin-bottom:0.5em !important;}
div#leftside h2 span.subtitle {font-size:0.8em !important; color:#000000 !important;}

div#rightside               {float:left !important;}
div#leftside div.entry img  {float:left !important; padding:0em 1em 1em 0.5em !important;}
div#leftside div#dotcom_box {clear:both !important; font-size:0.8em !important; padding:0.5em 1em !important; background-color:#ffffff !important;}
div#leftside div#column_box {clear:both !important; font-size:0.8em !important; padding:0.5em 1em !important; background-color:#ffffff !important;}

p.entry_info   {font-size:0.8em !important; padding:0.5em 1em !important; color:#000000 !important;}
div#notice_box {background-color:#e8e7dc !important; padding:0.5em 1em !important;}

div#rightside   {width:100% !important; float:none !important; font-size:0.9em !important; display:none !important;}
div#footer_link {display:none !important;}
div#footer      {display:none !important;}

ul.archive-list {margin-left:2em !important;}
ul.module-list  {margin-left:2em !important;}
div#adsense_box {display:none !important;}

p.nav_bar {background-color:#e8e7dc !important; padding:0.5em 1em !important; margin-bottom:1em !important;}

div#cmt_box textarea {border:1px solid #000000 !important;}
div#cmt_box input    {border:1px solid #000000 !important;}

}
