css:
@-moz-document domain('dailypaul.com') {

#page {
    width: 100% !important;
}
#header {
    margin: 26px auto !important;
    width: 1000px !important;
}
#block-nice_menus-1 .content {
    background-color: #005 !important;
}
#content {
    border-top: 1px solid #AAA;
    overflow: hidden;
}
#main_content {
    float: left;
    padding: 0 !important;
    width: 65% !important;
}
#main {
    margin: 0 !important;
    padding: 0 !important;
}
#pre_contents {
    display: none !important;
}
#post_content {
    display: none !important;
}
#main > .breadcrumb {
    background-color: #EEE;
    margin: 0 !important;
    padding: 4px 16px !important;
}
#main > .breadcrumb a {
    font-weight: bold !important;
}
#main > .title {
    background-color: #FFF;
    border-top: 1px solid #AAA !important;
    font-family: Tahoma, Arial, Helvetica, sans-serif !important;
    margin: 0 !important;
    padding: 4px 16px !important;
}
#main > .node {
    border-bottom: 0 !important;
    margin: 0 !important;
    padding: 0 0 16px !important;
}
#main > .node > .soc.filled {
    left: 16px !important;
    top: 12px !important;
}
#main > .node > .vud-widget {
    background-color: #EEE;
    border: 1px solid #CCC;
    box-shadow: 0.5em 0.5em 0.5em #224;
    margin: 10px 16px 0 0;
    padding: 10px;
}
#main > .node > .title {
    background-color: #005;
    margin: 0 0 8px !important;
    padding: 32px 16px 10px !important;
}
#main > .node > .title, #main > .node > .title a {
    color: #FFF;
    font-family: Tahoma, Arial, Helvetica, sans-serif !important;
    text-decoration: none;
}
#main > .node > .title a:hover {
    color: #FFA !important;
}
#main > .node > .submitted {
    padding: 4px 16px 0 !important;
}
#main > .node > .submitted .taxonomy {
    padding: 4px 16px !important;
}
#main > .node > .submitted .taxonomy a {
    font-weight: bold !important;
}
#main > .node > .content {
    margin-top: 0 !important;
    padding: 0 16px !important;
}
#main > .node > .links {
    margin: 0 !important;
    min-height: 0 !important;
    padding: 4px 16px !important;
}
#main > .node > .above_comments {
    display: none !important;
}
#main > .node > .links a {
    font-weight: bold !important;
}
#main > .box {
    border-top: 1px solid #AAA !important;
    padding: 16px !important;
}
#main > #comments {
    margin: 0 !important;
    padding: 0 16px 16px !important;
}
/*#main li.comment_reply {
    background-color: #FFF !important;
    border: solid #AAA !important;
    border-width: 1px 0 0 1px !important;
}
#main li.comment_reply a {
}
#main .comment li.comment_reply {
    border-radius: 0 0 12px 0 !important;
}
#main .comment.replied li.comment_reply {
    border-radius: 0 !important;
    border-width: 1px !important;
}*/
#sidebars {
    float: right;
    width: 35% !important;
}
#sidebars-table {
    width: auto !important;
}
#sidebar1a {
    padding: 16px !important;
}
#sidebar1b {
    padding: 16px !important;
}
#sidebar-right {
    width: auto !important;
}
body #sidebar-right .block {
    margin: 0 !important;
    padding: 16px !important;
}
body #sidebar-right .block .title {
    margin-top: 0 !important;
}
body #sidebar-right .block ul {
    margin-left: 10px;
}
#block-block-46 {
    display: none !important;
}
#block-views-activeForumTopics-block_1 {
    background-color: #FFC;
    box-shadow: 0.5em 0.5em 0.5em #995 inset;
}
#block-views-recentcomments-block_1 {
    background-color: #CFC;
    box-shadow: 0.5em 0.5em 0.5em #595 inset;
}
#block-user-3 {
    background-color: #FDD;
    box-shadow: 0.5em 0.5em 0.5em #955 inset;
}
#sidebar-ad_region {
    display: none !important;
}

ul.nice-menu ul li {
    width: 20em;
}
ul.nice-menu-down li ul li ul, #header-region ul.nice-menu-down li ul li ul {
    left: 20em;
}

}
