padding         :0    !important;
font-size       :1em  !important;
list-style-image:none !important;
border          :none !important;
}

body {margin:0.5em 1em !important;}

div#outerslice     {display:none !important;}
div#content iframe {display:none !important;}
div.skyscraper     {display:none !important;}
div#bp1,div#bp2,div#bp3,div#bp4,div#bp5,div#bp6,div#bp7,div#bp8,div#bp9,div#bp0{display:none !important;}

div#cobrand  {display:none !important;}
img.centered {display:none !important;}
