div#content      {width:auto !important; background-color:#ffffff !important;  padding:0.5em 1em !important; float:none !important; background-image:none !important;}
div.headerbottom {background-image:none !important;}

h2           {background-color:#a09899 !important; margin:0em -1em !important; margin-top:1em !important;}
h2 a         {display:block !important; width:auto !important; padding:0.5em 1em !important; color:#ffffff !important;}
h2 span      {padding:0.5em 1em !important; color:#ffffff !important;}
input        {border:1px solid #a09899 !important;}
textarea     {border:1px solid #a09899 !important;}
div.post img {float:left !important; margin-right:1em !important;}

div.content ul {font-size:0.9em !important;}
p.byline       {font-size:0.8em !important;}
div.postmeta   {font-size:0.8em !important;}

div#subcontent                          {font-size:0.8em !important; width:auto !important; background-color:#fff !important;}
