@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.ubuntu.ru") {

body {font: 88%/130% "Ubuntu Beta",UbuntuBeta,Ubuntu,"Bitstream Vera Sans","DejaVu Sans",Verdana,arial,helvetica,sans-serif!important;}
.smf a:link, .smf a:visited,.smf .windowbg2 {color: #575757!important;}
.smf a:hover {color: #DD4814!important;}
div#__container > div:first-child { max-width: 100%!important;}
#__search-box {float: left!important; margin-left: 0px!important; border-radius: 0!important; border-bottom: #DDD 1px solid!important; width: 215px!important;}
#search__input {background: #fff;}
.smf .poster {text-align: left!important;}
.smf .poster h4 {margin: 0.2em 0 0.4em 1.3em!important;}
#__header-menu {border-radius: 0!important; margin: 0 -1px!important;}
#__header-menu .header-submenu > div {margin-left: 210px!important; margin-right: 16px!important; border-radius: 0!important; border-bottom: #DDD 1px solid!important;}
#__wrapper {box-shadow: none!important; border: #ddd 2px solid!important; border-width: 0 1px 1px!important; border-radius: 0!important;}
#__header-menu > ul li a {text-shadow: none!important; font-size: 16px!important; padding: 24px 14px 0 14px!important;}
#__header-menu .header-submenu a {font-size: 12px!important; padding: 13px 12px 0 12px!important;}
#__main {color: #575757!important; padding: 0 0px 12px!important;}
#__main-title {border-bottom: #DDD 1px solid; height: 60px;}
#__main-title h1 {margin: 8px 8px 8px 15px!important; font-size: 20px!important; color: #757479!important;}
#__main-title #upshrink {padding-top: 18px!important;}
.smf h3.catbg {font-size: 14px!important;}
.smf #upper_section {background-color: #EFEFEF!important; color: #575757!important; border-radius: 0!important; border-color: #DDD!important; border: 1px solid #E3E3E3!important; border-width: 0 0 1px 0!important; margin-bottom: 0!important;}
div.user a {text-decoration: underline!important; font-size: 13px!important;}
div.user img {max-width: 80px;max-height: 80px; border: #FFF 4px solid!important;box-shadow: 0 0 6px rgba(0,0,0,0.2);border-radius: 3px;}

/*topic images*/
img[src*="/images/png/topic/"],img[src$="/icons/quick_lock.png"],img[src$="/icons/quick_sticky.png"],img[src$="/icons/pm_read.png"],img[src$="/icons/pm_replied.png"] {
    height:18px!important;
    padding-left:18px!important;
    width:0!important;
    background-repeat: no-repeat;
    background-image: url(data:image/png;base64,);
}
img[src$="/normal_post.png"] {background-position: 0 0; }
img[src$="/hot_post.png"] {background-position: -18px 0; }
img[src$="/veryhot_post.png"] {background-position: -36px 0; }
img[src$="/my_normal_post.png"],img[src$="/icons/pm_replied.png"] {background-position: -54px 0; }
img[src$="/my_hot_post.png"] {background-position: -72px 0; }
img[src$="/my_veryhot_post.png"] {background-position: -90px 0; }

img[src$="/normal_post_locked.png"],img[src$="/icons/pm_read.png"] {background-position: 0 -18px; }
img[src$="/hot_post_locked.png"] {background-position: -18px -18px; }
img[src$="/veryhot_post_locked.png"] {background-position: -36px -18px; }
img[src$="/my_normal_post_locked.png"] {background-position: -54px -18px; }
img[src$="/my_hot_post_locked.png"] {background-position: -72px -18px; }
img[src$="/my_veryhot_post_locked.png"] {background-position: -90px -18px; }

img[src$="/normal_post_sticky.png"] {background-position: 0 -36px; }
img[src$="/hot_post_sticky.png"] {background-position: -18px -36px; }
img[src$="/veryhot_post_sticky.png"] {background-position: -36px -36px; }
img[src$="/my_normal_post_sticky.png"] {background-position: -54px -36px; }
img[src$="/my_hot_post_sticky.png"] {background-position: -72px -36px; }
img[src$="/my_veryhot_post_sticky.png"] {background-position: -90px -36px; }

img[src$="/normal_post_locked_sticky.png"] {background-position: 0 -54px; }
img[src$="/hot_post_locked_sticky.png"] {background-position: -18px -54px; }
img[src$="/veryhot_post_locked_sticky.png"] {background-position: -36px -54px; }
img[src$="/my_normal_post_locked_sticky.png"] {background-position: -54px -54px; }
img[src$="/my_hot_post_locked_sticky.png"] {background-position: -72px -54px; }
img[src$="/my_veryhot_post_locked_sticky.png"] {background-position: -90px -54px; }

img[src$="/normal_poll.png"] {background-position: 0 -72px; }
img[src$="/hot_poll.png"] {background-position: -18px -72px; }
img[src$="/veryhot_poll.png"] {background-position: -36px -72px; }
img[src$="/my_normal_poll.png"] {background-position: -54px -72px; }
img[src$="/my_hot_poll.png"] {background-position: -72px -72px; }
img[src$="/my_veryhot_poll.png"] {background-position: -90px -72px; }

img[src$="/normal_poll_locked.png"] {background-position: 0 -90px; }
img[src$="/hot_poll_locked.png"] {background-position: -18px -90px; }
img[src$="/veryhot_poll_locked.png"] {background-position: -36px -90px; }
img[src$="/my_normal_poll_locked.png"] {background-position: -54px -90px; }
img[src$="/my_hot_poll_locked.png"] {background-position: -72px -90px; }
img[src$="/my_veryhot_poll_locked.png"] {background-position: -90px -90px; }

img[src$="/normal_poll_sticky.png"] {background-position: 0 -108px; }
img[src$="/hot_poll_sticky.png"] {background-position: -18px -108px; }
img[src$="/veryhot_poll_sticky.png"] {background-position: -36px -108px; }
img[src$="/my_normal_poll_sticky.png"] {background-position: -54px -108px; }
img[src$="/my_hot_poll_sticky.png"] {background-position: -72px -108px; }
img[src$="/my_veryhot_poll_sticky.png"] {background-position: -90px -108px; }

img[src$="/normal_poll_locked_sticky.png"] {background-position: 0 -126px; }
img[src$="/hot_poll_locked_sticky.png"] {background-position: -18px -126px; }
img[src$="/veryhot_poll_locked_sticky.png"] {background-position: -36px -126px; }
img[src$="/my_normal_poll_locked_sticky.png"] {background-position: -54px -126px; }
img[src$="/my_hot_poll_locked_sticky.png"] {background-position: -72px -126px; }
img[src$="/my_veryhot_poll_locked_sticky.png"] {background-position: -90px -126px; }

/*post images*/
img[src*="/images/png/post/"] {height:16px!important; padding-left:16px!important; width:0!important; background-repeat: no-repeat;}
img[src*="/images/png/post/topicsolved.png"] {background-image: url(http://forum.ubuntu.ru/Themes/ubuntu-ru-beta/images/post/topicsolved.gif)}
img[src$="/post/angry.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/cheesy.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/clip.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/exclamation.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/grin.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/lamp.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/moved.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/question.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/recycled.png"] {background-image: url(http://forum.ubuntu.ru/Themes/ubuntu-ru-beta/images/post/recycled.gif);}
img[src$="/post/sad.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/smiley.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/thumbdown.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/thumbup.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/wink.png"] {background-image: url(data:image/png;base64,);}
img[src$="/post/wireless.png"] {background-image: url(http://forum.ubuntu.ru/Themes/ubuntu-ru-beta/images/post/wireless.gif);}
img[src$="/post/xx.png"] {
    background-image: url(data:image/png;base64,);
}
img[src$="/icons/quick_lock.gif"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:16px!important;
    padding-left:16px!important;
    width:0!important;
}
img[src$="/icons/quick_sticky.gif"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:16px!important;
    padding-left:16px!important;
    width:0!important;
}
img[src$="/new.png"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:16px!important;
    padding-left:16px!important;
    width:0!important;
    margin-bottom: -3px!important;
    margin-left: 2px!important;
}

/*boardindex_table*/
#boardindex_table tr.catbg th.first_th,#boardindex_table div.cat_bar, #personal_messages tr.catbg th, #whos_online tr.catbg th {background: #FFF !important;}
#boardindex_table div.cat_bar h3 {margin-top: 4px!important; background: transparent!important; font-weight: bold!important;font-size: 16px!important;}
#boardindex_table h3 .unreadlink {display: none!important;}
#boardindex_table .table_list {border-collapse: collapse; border-bottom: #DDD 1px solid!important; margin-bottom: 10px!important;}
/*#boardindex_table .table_list td[colspan="4"] {border-top: 1px solid #DDDDDD!important;}
#boardindex_table .table_list td[colspan="4"]:first-child {border-top: none!important;}*/
#boardindex_table .table_list .content {border-bottom: 1px solid #DDDDDD!important;}
#boardindex_table .divider {display: none;}
#boardindex_table .table_list td {border-width: 0!important;}
#boardindex_table .table_list tr.windowbg2 {border-top: #DDD 1px solid!important;}
#boardindex_table .table_list td {background: #FFF!important;padding: 4px 7px!important;}
#boardindex_table .table_list #board_35 td,
#boardindex_table .table_list #board_29 td,
#boardindex_table .table_list #board_25 td,
#boardindex_table .table_list #board_41 td,
#boardindex_table .table_list #board_39 td,
#boardindex_table .table_list #board_31 td,
#boardindex_table .table_list #board_14 td,
#boardindex_table .table_list #board_15 td,
#boardindex_table .table_list #board_38 td,
#boardindex_table .table_list #board_44 td,
#boardindex_table .table_list #board_53 td,
#boardindex_table .table_list #board_24 td,
#boardindex_table .table_list #board_45 td,
#boardindex_table .table_list #board_47 td,
#boardindex_table .table_list #board_49 td,
#boardindex_table .table_list #board_20 td {background-color: #F9F9F9!important;}
#boardindex_table .table_list td.children.windowbg {background-color: #F9F9F9!important; padding: 8px 0 10px 20px!important; font-size: 12px!important;}
#boardindex_table .table_list tr#board_54_children td,
#boardindex_table .table_list tr#board_30_children td,
#boardindex_table .table_list tr#board_61_children td,
#boardindex_table .table_list tr#board_50_children td,
#boardindex_table .table_list tr#board_17_children td {background: #FFF!important;}
#boardindex_table a.subject {color: #DD4814!important;text-decoration: underline!important;}
#boardindex_table a.subject:hover {color: #222!important;}
#messageindex .subject strong a {color: #606060!important;}
#messageindex .subject strong a:hover {color: #DD4814!important;}
/*board images*/
.smf ul.quickbuttons li.reply_button {background-position: 0 -60px!important;}
.smf ul.quickbuttons li.quote_button {
    background: url(data:image/png;base64,) no-repeat scroll 0 0px transparent!important;
}
.smf .topic_table td.stickybg2,.smf .topic_table td.locked_sticky2 {
    background-image: url(data:image/png;base64,)!important;
    background-position: 98% 13px!important;
    background-repeat: no-repeat!important;
}
.smf .topic_table td.lockedbg2 {
    background-image: url(data:image/png;base64,)!important;
}
.smf .topic_table td.lastpost {
    background-image: none!important;
}
img[src$="/last_post.png"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:16px!important;
    padding-left:16px!important;
    width:0!important;
    margin-top: 9px!important;
}
img[src$="/icons/quick_lock.png"] {background-position: 0 -18px; }
img[src$="/icons/quick_sticky.png"] {background-position: 0 -36px; }
img[src$="/on.png"],img[src$="/on2.png"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:32px!important;
    padding-left:32px!important;
    width:0!important;
}
img[src$="/off.png"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:32px!important;
    padding-left:32px!important;
    width:0!important;
}
/*end of board images*/

div.cat_bar {background: #FFF!important;}
div.cat_bar h3, #whos_online h4 {background: transparent!important; color: #858585!important; font-weight: bold!important;}
#whos_online .title_bar {border-bottom: #DDD 1px solid!important;border-top: #DDD 1px solid!important;}
#statistics .title_bar, #statistics .titlebg, #stats .titlebg th, .description, #whos_online .title_bar, #admin_content .title_bar, #admin_content .titlebg {background: #EFEFEF!important;} 
#statistics .title_bar, #stats .titlebg, .description, #admin_content .title_bar {border-bottom: #DDD 1px solid!important;border-top: #DDD 1px solid!important;}
.description {border-width: 1px 0!important;border-radius:0!important;}
#statistics #stats_left,
#statistics #stats_right,
#statistics #top_posters,
#statistics #top_boards,
#statistics #top_topics_replies,
#statistics #top_topics_views,
#statistics #top_topics_starter,
#statistics #most_online {width: 50%!important;}
#statistics .windowbg2 {background: transparent!important;}
#statistics .topslice, #statistics .botslice {display: none!important;}
#recent .catbg th,#messageindex .catbg th, #memberlist .catbg th {background: #FFF!important;}
#recent .catbg.centertext {background-color: #FFF!important; font-weight: normal!important; font-size: 12px!important;}
#recent .catbg.centertext a {text-decoration: underline!important; color: #858585!important;}
/*#recent .cat_bar {background-color: #F9F9F9!important; border-bottom: 1px solid #DDDDDD!important; border-top: 1px solid #DDDDDD!important;}*/
#recent .table_grid td, #messageindex .table_grid td, #mlist .table_grid td {border-bottom: 1px solid #DDDDDD!important; background-color: #FFF!important; padding: 6px 1px!important; border-right: 0!important; border-left: 0!important;}
.childboards .table_list td {border-top: 1px solid #DDDDDD!important;border-bottom: 1px solid #DDDDDD!important; background-color: #F9F9F9!important; padding: 6px 1px!important; border-right: 0!important; border-left: 0!important;}
#recent .table_grid tr:nth-child(2n+1) td, #messageindex .table_grid tr:nth-child(2n+1) td, #mlist .table_grid tr:nth-child(2n+1) td  {background-color: #F9F9F9!important;}
#recent .first_th, #messageindex .first_th {width: auto!important;}
.icon1, .icon2 {width: 44px!important;}
#recent td.subject, #messageindex td.subject {padding-left: 10px!important;}
#recent .table_grid .whos_viewing td, #messageindex .table_grid .whos_viewing td {border-top: 1px solid #DDDDDD!important; padding: 6px 15px!important;}

/*buttons*/
.pagesection span, .pagelinks, .pagesection .floatleft, .pagesection {color: transparent!important;letter-spacing: -2px;}
.pagesection { margin-left: -43px!important;}
.pagesection .buttonlist ul li a, .pagesection a,
#moderationbuttons .buttonlist ul li a,
.mark_read .buttonlist ul li a,
.button_submit, .spoilerbutton, #pollmoderation a,
.dropmenu a.firstlevel {
    text-transform: none!important;
    margin-bottom: 6px!important;
    background-color: #FAFAFA!important;
    background-image: -moz-linear-gradient(center top , #FFFFFF, #FFFFFF 25%, #E6E6E6)!important;
    background-repeat: no-repeat;
    border-color: #CCCCCC #CCCCCC #BBBBBB!important;
    border-radius: 4px 4px 4px 4px!important;
    border-style: solid;
    border-width: 1px;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.2) inset, 0 1px 2px rgba(0, 0, 0, 0.05)!important;
    color: #333333!important;
    line-height: 18px!important;
    padding: 0 7px!important;
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.75)!important;
    letter-spacing: normal;
    font-size: 0.8em!important;
    cursor: pointer!important;
    outline: 0 none;
}
.dropmenu a.firstlevel span {background: none!important; height: auto!important;}
.pagesection .buttonlist ul li a {margin-right: 3px!important;}
.spoilerbutton {font-size: 11px!important;height: 22px!important; padding: 0 5px!important;}
.pagesection .buttonlist ul li a span {color: #333333!important;letter-spacing: normal;}
.pagesection a,.pagesection strong {
    font-size: 0.8em!important;
    text-decoration: none!important;
    height: 20px!important;
    line-height: 20px!important;
    margin-left: 1px!important;
    padding: 0 7px!important;
    display: inline-block;
    letter-spacing: normal;
}
.pagesection strong {
    border-color: #DBDBDB #DBDBDB #BBBBBB!important;
    border-radius: 4px 4px 4px 4px!important;
    border-style: solid;
    border-width: 1px;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.2) inset, 0 1px 2px rgba(0, 0, 0, 0.05)!important;
    color: #222!important;
}
.pagesection a strong {
    border-width: 0!important;
    font-weight: normal!important;
    box-shadow: none!important;
    margin: 1px 0 0 0!important;
}
a[href="#lastPost"],a[href="#top"],a[href="#bot"] {margin: 0 0 0 4px!important;}
a[href="#lastPost"]:before,a[href="#bot"]:before {content:'';}
a[href="#top"]:before {content:'';}
.pagesection a:hover strong {color: #FFF!important;}
.pagesection span[onclick^="expandPages"] {
    margin: 0 2px 0 4px!important;
    color: #333333!important;
    letter-spacing: normal;
}
.smf #recent .buttonlist ul {padding: 0!important;}
.pagesection .buttonlist ul li a.active,
#moderationbuttons .buttonlist ul li a.active,.dropmenu a.firstlevel.active {
    border-color: rgba(0, 0, 0, 0.1)!important;
    background-color: #2C001E!important;
    color: #FFFFFF!important;
    text-shadow: 0 -1px 0px rgba(0, 0, 0, 0.9)!important;
    background-image: -moz-linear-gradient(center top , #AB3B77, #71274F)!important;
}
.pagesection .buttonlist ul li a.active span {color: #FFFFFF!important;}
.pagesection .buttonlist ul li a:hover,.pagesection a:hover,
#moderationbuttons .buttonlist ul li a:hover,
.mark_read .buttonlist ul li a:hover,
.button_submit:hover, .spoilerbutton:hover,
#pollmoderation a:hover, .dropmenu a.firstlevel:hover {
    background-color: #FAA732!important;
    background-image: -moz-linear-gradient(center top , #FBB450, #F89406)!important;
    background-repeat: repeat-x;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25)!important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25)!important;
    color: #FFF!important;
}
.pagesection .buttonlist ul li a:hover span {color: #FFF!important;}
/*end of buttons*/

/*topic*/
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    background: url(data:image/gif;base64,) no-repeat scroll 0.1em 0.1em transparent!important;
    border-bottom: 2px solid #DEDEDE!important;
    border-top: 2px solid #DEDEDE!important;
    color: #9C9C9C!important;
    font-size: 12px!important;
    line-height: 1.4em!important;
    margin: 0.2em 0 1em 0!important;
    overflow: auto!important;
    padding: 1.2em 1.6em!important;
}
blockquote.bbc_standard_quote {background-color: #FFFFFF!important;}
blockquote.bbc_alternate_quote {background-color: #F7F7F7!important;}
code.bbc_code {
    background: none repeat scroll 0 0 #FFFFFF!important;
    border-bottom: 2px solid #DEDEDE!important;
    border-top: 2px solid #DEDEDE!important;
    display: block!important;
    font-family: "dejavu sans mono","monaco","lucida console","courier new",monospace!important;
    font-size: 12px!important;
    line-height: 1.5em!important;
    padding: 8px 1em!important;
    margin: 0.2em 0 1em 0!important;
    white-space: pre!important;
}
.spoiler {border: 1px dashed #C4C4C4; padding: 10px!important; margin-bottom: 8px!important;}
.windowbg .spoiler {background: #F7F7F7;}
.windowbg2 .spoiler {background: #FFF;}
.spoilerbody {margin: 4px 0!important;}
#forumposts table.table_grid {border-bottom: 1px solid #DDDDDD!important; margin-bottom: 10px!important;}
#forumposts .windowbg, #forumposts .windowbg2,#personal_messages span.botslice span {border-bottom: 1px solid #DDDDDD!important;}
#forumposts .topic_table table.table_grid td, .flow_hidden div.core_posts {border-top: 1px solid #DDDDDD!important; background-color: #FFF!important; padding: 6px 1px!important;}
#forumposts .topic_table table.table_grid tr:nth-child(2n+1) td {background-color: #F9F9F9!important;}
.flow_hidden div.core_posts.windowbg2,.flow_hidden div.core_posts.windowbg2 .topslice span, .flow_hidden div.core_posts.windowbg2 .botslice span {background-color: #F9F9F9!important;}
#forumposts .windowbg, .core_posts.windowbg  {background-color: #EFEFEF!important; color: #575757!important;}
.smf .inner,.smf .signature,
.smf .custom_fields_above_signature,
.smf hr, .flow_hidden .list_posts.smalltext,
.smf .hrcolor {border-top: 1px dotted #C4C4C4!important;background-color: transparent!important;}
#forumposts .windowbg span.topslice span, 
#forumposts .windowbg span.botslice span {background-color: #EFEFEF!important;}
.core_posts {margin-bottom: 0!important;}
.core_posts.windowbg span.topslice span, 
.core_posts.windowbg span.botslice span {background-color: #FFF!important;}
#forumposts span.topslice span {border-top: #fff 0px solid;}
#forumposts .poster h4 {font-size: 16px!important; font-weight: bold!important; margin: 0.1em 0 6px 1em!important;}
#forumposts .keyinfo h5 a {text-decoration: underline!important;}
#forumposts .topic_starter {font-weight: bold; color: #FB6666!important;}
#forumposts .codeheader, #forumposts .quoteheader {font-size: 10px!important;}
span.topslice span, span.botslice span {border-radius: 0!important;}
#whoisviewing{border-top: 1px solid #DDDDDD!important;background-color: #FFF!important;}
#display_jump_to, #message_index_jump_to {display: none;}
/*end of topic*/

.nextlinks, .nextlinks_bottom {display: none;}
.navigate_section {padding: 8px 0.5em 8px 10px!important; margin-bottom: 8px!important;}
#main_content_section .navigate_section {border-top: #DDD 1px solid!important;border-bottom: #DDD 1px solid!important; background: #F9F9F9!important;}
#main_content_section .navigate_section:first-child {border-top: none!important;}
.table_grid{margin-bottom: 5px!important;}
.table_grid td.subject span a {font-size: 15px!important; text-decoration: underline!important; line-height: 22px!important; display: inline-block;}
.smf span.upperframe span {border-top-left-radius: 0px!important; border-top-right-radius: 0px!important; border-width: 1px 0 0 0!important;}
.smf span.lowerframe span {border-bottom-left-radius: 0px!important; border-bottom-right-radius: 0px!important; border-width: 0 0 1px 0!important;}
.smf .roundframe {border-width: 0!important;}
#whoisviewing {border-bottom: #DDD 1px solid!important;}
.roundframe {padding: 0!important;}
.roundframe div.cat_bar, .roundframe .title_barIC, .roundframe .titlebg {background: transparent!important;}
.roundframe .title_barIC, .roundframe .titlebg {background: #FFF!important;}
.roundframe .title_barIC {border-bottom: #DDD 1px solid!important;border-top: #DDD 1px solid!important;}
.roundframe div.cat_bar h3 {background: transparent!important; font-weight: bold!important;font-size: 16px!important; color: #7B7B7B!important;}
ul.dropmenu li a {font-size: 12px!important;}
ul.dropmenu li a:hover, div.buttonlist a:hover {color: #FFF!important;}
.windowbg2.mymenu {padding: 10px!important;background: transparent!important;}
#basicinfo .catbg {background: #F7F7F7!important; color: #858585 !important; border-bottom: #DDD 1px solid!important; font-weight: bold!important;}
#memberlist h4.catbg {background: transparent!important; color: #858585 !important; font-weight: bold!important;}
.attachments {border-top: 1px dotted #C4C4C4!important;}
#poll .windowbg, #memberlist .cat_bar {background-color: #F9F9F9!important; border-bottom: 1px solid #DDDDDD!important; border-top: 1px solid #DDDDDD!important;}
#poll .windowbg .topslice span, #poll .windowbg .botslice span {background: #F9F9F9!important; border: 0!important;}
#poll_options ul.options {border-top: 1px dotted #C4C4C4!important;}
#poll_options div.submitbutton {border-bottom: 1px dotted #C4C4C4!important;}
h4#pollquestion {font-weight: bold!important;}
.signature {color: #A1A1A1!important;}
div[style="width: 98.8%;"] div, #postAdditionalOptionsHeader, #shortcuts {padding-left: 8px!important;}
.smf textarea:hover, .smf textarea:focus,
.smf input.input_text:hover, .smf input.input_text:focus,
.smf select:hover, .smf select:focus {border: 1px solid #DDD!important;}
.smf div#profile_error, .smf div#profile_success {background-color: #FCF8E3!important; border: 1px solid #FBEED5!important; border-radius: 4px 4px 4px 4px!important; margin: 15px 5px 18px 15px!important; padding: 8px 35px 8px 14px!important; text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5)!important;}
.smf div#profile_error {color: #B94A48!important; background-color: #F2DEDE!important; border-color: #D3A1A9!important;}
.smf div#profile_success {background-color: #DFF0D8!important; border-color: #B7D39E!important; color: #468847!important;}

#__header-menu #menu_nav li ul {border-radius: 0px 0px 4px 4px!important; border: #DDD 1px solid!important; border-width: 0 1px 1px!important; background: #FFF!important; padding: 4px 0!important;}
#__header-menu #menu_nav li > div {padding-top: 1px!important; margin-left: -1px!important;}
#__header-menu #menu_nav li li a {padding: 6px 0!important; background: transparent!important; border-radius: 0px!important;}
.dropmenu li ul {
    background: none!important;
    background-clip: padding-box!important;
    background-color: #FFFFFF!important;
    border-color: rgba(0, 0, 0, 0.2)!important;
    border-style: solid!important;
    border-width: 1px!important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2)!important;
    padding: 4px 0!important;
    border-radius: 4px 4px 4px 4px!important;
    margin-top: 1px;
}
.dropmenu li a.firstlevel span:after {content:' ';}
#adm_submenus .dropmenu li a.firstlevel span:after {content:' ';}
.dropmenu li a.firstlevel {padding-right: 0!important;}
.dropmenu li ul:before {
    border-bottom: 7px solid rgba(0, 0, 0, 0.2)!important;
    border-left: 7px solid transparent!important;
    border-right: 7px solid transparent!important;
    content: ""!important;
    display: inline-block!important;
    left: 9px!important;
    position: absolute!important;
    top: -7px!important;
}
.dropmenu li ul:after {
    border-bottom: 6px solid #FFFFFF!important;
    border-left: 6px solid transparent!important;
    border-right: 6px solid transparent!important;
    content: ""!important;
    display: inline-block!important;
    left: 10px!important;
    position: absolute!important;
    top: -6px!important;
}
.dropmenu li ul li {border: 0!important;}
.smf .dropmenu li li a:hover, .smf .dropmenu li li:hover > a, #__header-menu #menu_nav li li a:hover {
    background: #DD4814!important;
    color: #FFF!important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25)!important;
}
form[action*="action=bookmarks"] tr.catbg th,
.generic_list tr.catbg th,
#admin_content tr.catbg th {background: #FFF!important; color: #575757!important;}
form[action*="action=bookmarks"] .table_grid td,
.generic_list .table_grid td,
#admin_content .table_grid td  {border-bottom: 1px solid #DDDDDD!important; background-color: #FFF!important; padding: 6px 1px!important; border-right: 0!important; border-left: 0!important;}
form[action*="action=bookmarks"] .table_grid tr:nth-child(2n+1) td,
.generic_list .table_grid tr:nth-child(2n+1) td,
#admin_content .table_grid tr:nth-child(2n+1) td{background-color: #F9F9F9!important;}
.generic_list .table_grid td, #admin_content .table_grid td {padding-left: 8px!important;}
#admin_content .windowbg.core_posts .topslice span {background: #EFEFEF!important;}
#admin_content .windowbg.core_posts .botslice span {background: #EFEFEF!important;}
#admin_content .core_posts {border-bottom: 1px solid #DDDDDD!important;}
#admin_content .list_posts {border-top: 1px dotted #C4C4C4!important;}
#admin_content .titlebg th {background: #FFF!important;}

}
