#sideNavi > a > img,          /* 左カラム - ITpro Premium 詳細はこちら */
.leafTitleBox,                /* 中央カラム - テーマタイトル */
.themeTopic,                  /* 中央カラム - PR xxxのトピックス */
.leafNaviTop > .printLink,    /* 中央カラム - 上プリントボタン */
