* {
margin   :0    !important;
padding  :0    !important;
font-size:100% !important;
border   :none !important;
}

body{background-color:#666666 !important;}

div.naviright         {display:none !important;}
div#banner a:after    {content:"NETAFULL" !important; font-size:1.6em !important; font-weight:bold !important; padding:0.5em !important;}
div#banner img        {display:none !important;}
div#banner div.right  {display:none !important;}
div#navi              {padding:0.5em !important; border-bottom:5px solid #666666 !important; width:auto !important;}
div#wrapper div#right {display:none !important;}
div#sub               {display:none !important;}
div#topicpath         {padding-left:1em !important; font-size:0.9em !important;}

div#adsense  {display:none !important;}
div#adsense1 {display:none !important;}
div#adsense2 {display:none !important;}
div#adsense3 {display:none !important;}
div#adsense4 {display:none !important;}
div#adsense5 {display:none !important;}

div#container          {width:auto !important; float:none !important; margin :0.5em 1em !important;}
div#wrapper            {width:auto !important; float:none !important; padding:0.5em 1em !important;}
div#center             {width:auto !important;}
div.content h3         {clear:left !important;}
div.content div.body   {clear:left !important; margin-top:1em !important;}
div.content            {width:auto !important;}
div.content blockquote {width:auto !important; padding:0.5em 1em !important; background-image:none !important;}

div.content h1          {clear:left !important;}
div.content h1 a        {float:left !important; font-size:0.9em !important; font-weight:normal !important; padding:0em 0.5em !important;}
div.content p.dateright {float:left !important; font-size:0.8em !important; margin-top:0.2em !important; margin-left:1em !important;}

div#footer                 {padding:0.5em 1em !important; font-size:0.8em !important; background-color:#ffffff !important;}
div#footer li              {background-image:none !important;}
div#footer div.footWrapper {width:66% !important;}
div#footer div.footLeft    {width:40% !important;}

div#footer div.footLeft p img          {display:none !important;}
div#footer div.footLeft p iframe       {display:none !important;}
div#footer div.footLeft div#technorati {display:none !important;}
div#footer div.footCenter              {width:59% !important;}
div#footer div.footCenter table#reviewposter_table                       {width:98% !important;}
div#footer div.footCenter table#reviewposter_table th#reviewposter_title {height:0.8em !important;}

div#footer div.footRight {width:33% !important; float:left !important;}

a[href^="http://www.amazon.co.jp"]          {display:none !important;}
a[href^="http://click.linksynergy.com"]     {display:none !important;}
a[href^="http://jobboard.drecom.jp/"]       {display:none !important;}
a[href ="http://netafull.net/sitemap.html"] {display:none !important;}
a[href^="http://agilemedia.jp/"]            {display:none !important;}

h2            {font-size:0.9em !important; margin-top:1em !important;}
h3#trackbacks {font-size:0.8em !important}

}
