css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.forest.impress.co.jp/docs/"),
               url-prefix("http://www.forest.impress.co.jp/backno/"){

body {
    background-color: #339900 !important;
    padding: 0 7% 0 0 !important; /* 全体もっと左に寄せ、記事本文を画面中央にする */
}
.base {
    background-color: #FFFFFF !important;
    margin-left: auto !important;
    margin-right: auto !important;
    min-width: 768px !important;
    width: 840px !important;
}
#site-logo {
    left: auto !important;
}
#content-wrap {/* margin-right: -305px; */
    margin-right: 0 !important;
}
#content-inner {/* margin: 0 316px 0 144px; */
    margin: 0 19px 0 144px !important;
}
h2.ttl-article {/* 記事タイトル */
    font-size: 175% !important;
}

.social_bookmark,
div[class^="ad_"]/* Ad */ {
    display: none !important
}
}



@-moz-document url-prefix("http://www.forest.impress.co.jp/img/") {
/* -- 2011年4月頃まで（？）の古いフォーマットが適用された画像拡大ページ -- */
/* 画像,画像下のリンクをセンタリング */
#content p,
ul.nav-contentend {
    text-align: center !important;
}
/* リストのマーカー用画像のように使用されている画像 */
ul.nav-contentend li {
    background: none !important;
    padding-left: 0 !important;
}
}
