css:
@-moz-document domain("neopets.com") {


#main #header {border: none !important; background-image: url(http://i.imgur.com/YNLHP.png) !important;}
#main #ban {border: none !important; background-image: url(http://i.imgur.com/Bi4VY.png) !important;}
#main #footer {background-image: url(http://i.imgur.com/2rhEE.png) !important;}
#main #footer td {text-align: center !important; color: #001E66;}
.brand-mamabar {display: none !important;}
.footerNifty {display: none !important;}
.copyright .sf {position: relative !important; left: 100px !important;}
.copyright .sf img {display: none !important;}
a:link, a:visited, a:active {color: #21366D;}
a:hover {color: #2071C5;}
.eventIcon, #header td.user.medText {color: #001E66 !important;}
.eventIcon a:link, #header td.user.medText a:link {color: #283030 !important;}
.eventIcon a:visited, #header td.user.medText a:visited {color: #283030 !important;}
.eventIcon a:active, #header td.user.medText a:active {color: #283030 !important;}
.eventIcon a:hover, #header td.user.medText a:hover {color: #283030 !important;}
#nst {color: #283030 !important;}
.eventIcon {color: #283030;}
#template_nav img {opacity: 0 !important; filter: alpha(opacity=0) !important;}
ul.dropdown {background: #FFFFFF !important; border: solid 1px #283030 !important; opacity: 0.9 !important; filter: alpha(opacity=90) !important;}
li:hover ul.dropdown a, li.over ul.dropdown a {color: #283030 !important;}
li:hover ul.dropdown a:Hover, li.over ul.dropdown a:hover {color: #414444 !important;}
.marqueeSlot, .marqueeSlot a:link, .marqueeSlot a:visited, .marqueeSlot a:active, .marqueeSlot a:hover {background-color: #60A0D8 !important;}
.marqueeSlotOn, .marqueeSlotOn a:link, .marqueeSlotOn a:visited, .marqueeSlotOn a:active, .marqueeSlotOn a:hover {background-color: #4880C0 !important;}
.sidebarHeader a:link, .contentModuleHeader a:link, .contentModuleHeaderAlt a:link {color: #FFFFFF !important;}
.sidebarHeader a:visited, .contentModuleHeader a:visited, .contentModuleHeaderAlt a:visited {color: #FFFFFF !important;}
.sidebarHeader a:active, .contentModuleHeader a:active, .contentModuleHeaderAlt a:active {color: #FFFFFF !important;}
.sidebarHeader a:hover, .contentModuleHeader a:hover, .contentModuleHeaderAlt a:hover {color: #FFFFFF !important;}
.moreLink, .moreLinkAlt {border: #FFFFFF 1px solid !important;}
.sidebarHeader {background: #2071C5 !important; color: #FFFFFF !important; border: none !important;}
.contentModuleHeader, .contentModuleHeaderAlt {background: #C85048 !important; color: #FFFFFF !important; border: none !important;}
.sidebarModule, .contentModule {border: none !important;}
.sidebarTable {border: #192654 2px solid !important;}
.contentModuleTable {border: #A82028 2px solid !important;}
.eventIcon a img {width: 0px !important; height: 25px !important; padding-right: 25px !important; background: url(http://i.imgur.com/tDLlB.png) no-repeat !important;}
.eventIcon a img[alt~="Neomail"] {background-position: 0px 0px !important;}
.eventIcon a img[alt~="accepted"][alt~="NeoFriend"] {background-position: -25px 0px !important;}
.eventIcon a img[alt~="requesting"] {background-position: -50px 0px !important;}
.eventIcon a img[alt~="Battledome"][alt~="accepted"] {background-position: -75px 0px !important;}
.eventIcon a img[alt~="challenged"] {background-position: -100px 0px !important;}
.eventIcon a img[alt~="given"] {background-position: -125px 0px !important;}
.eventIcon a img[alt~="trade"] {background-position: -150px 0px !important;}
}
