css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://boards.endoftheinter.net/"), url-prefix("https://boards.endoftheinter.net/") {
 tr:nth-child(odd) td {
    background: #222 !important;
    }
 tr {
    border: 1px solid #000 !important;
    }
 td {padding: 2px 3px 2px !important}
}
@-moz-document url-prefix("http://boards.endoftheinter.net/topics/"), url-prefix("https://boards.endoftheinter.net/topics/") {
 tr:hover { box-shadow: inset 0px 21px 0px #000 !important;}
}
@-moz-document url-prefix("https://boards.endoftheinter.net/showmessages.php"), url-prefix("http://boards.endoftheinter.net/showmessages.php"), url-prefix("https://archives.endoftheinter.net/showmessages.php?") {
 a#nextpage {
    background: #000 !important;
    color: #cccc33 !important;
    }
 table.message-body tr td.message, table.message-body tr td.userpic {
     background-color: transparent !important;
 }
 div.message-container:nth-child(even) {
     background-color: #202020 !important;
 }
 div.message-container:nth-child(odd) {
     background-color: #151515 !important;
 }
 div.message-top {
     margin: 0 !important;
     padding: 1px 3px !important;
 }
 b[style="color:blue"] {
     color: #6666ff !important;
 }
}
@-moz-document url-prefix("http://endoftheinter.net/main.php"), url-prefix("https://endoftheinter.net/main.php") {
 table.grid td {
     background-color: transparent !important;
 }
 table.grid tr:nth-child(even) {
    background: #151515 !important;
    }
 table.grid tr:nth-child(odd) {
    background: #202020 !important;
    }
}
@-moz-document domain("endoftheinter.net") {
    textarea[name="message"] {
        background-color: #000 !important;
        color: #bbb !important;
    }
/* Whatever */
body, table.classic tr td {
  background: #000000;
  color: #DFDFDF;
}
td, th {border-color: #000 !important;}
.quickpost {background-color: #000 !important;
color: #bbb !important;}
a {
  color: #ccc;
}
    
a:visited {
  color: #969696;
}

a span.m {
  border-bottom: 2px dotted #ccc;
}

a:visited span.m {
  border-bottom: 2px dotted #969696;
}

ins {
  background-color: #ddffdd;
  border: green;
}
ins.pair {
  border-left: red;
}
ins img {
  border: green;
}

del {
  background-color: #ffdddd;
  border: red;
}
del.pair {
  border-right: red;
}
del img {
  border: red;
}

em {
  color: #FF0000;
}

div.menubar, div.userbar, div.infobar {
  color: #c3c3c3;
}

div.menubar {
  background: #404040;
}

div.userbar {
  background: #303030;
}

div.infobar {
  background: #252525;
}

table.message-body tr td.message, div.message, table.message-body tr td.userpic {
  background: #151515;
}

div.message-top {
  background: #050505;
}

table.classic tr th {
  background: #404040;
  color: #c3c3c3;
}

table.classic tr th.title {
  color: #DFDFDF;

}

div.menubar a, div.userbar a, div.infobar a, table.classic tr th a, table.grid tr th a {
  color: #c3c3c3;
}

div.menubar a:visited, div.userbar a:visited, div.infobar a:visited, table.classic tr th a:visited, table.grid tr th a:visited {
  color: #c3c3c3;
}

div.menubar a:hover, div.userbar a:hover, div.infobar a:hover, table.classic tr th a:hover, table.grid tr th a:hover {
  color: #ffff99;
}

div.menubar a:active, div.userbar a:active, div.infobar a:active, table.classic tr th a:active, table.grid tr th a:active {
  color: #00bfff;
}

table.grid tr td {
  background: #151515;
}

table.grid tr th {
  background: #252525;
  color: #c3c3c3;
}

table.search {
  border: solid 2px #151515;
}

table.search tr th, table.search tr td {
  background: #151515;
 }

table.poll div {
  background: #303030;
}

div.graph {
  border: 1px solid #DFDFDF;
  background: #303030;
}

div.graph span {
  background: #151515;
}

.image_grid .grid_block .block_desc {
  background: #151515;
}

.quoted-message {
  border-left: 2px solid #050505;
}

table.message-body td.userpic {
  border-left: 2px solid #000000;
}

.quoter-button {
  background: #252525;
  border: 1px solid #303030;
}

a.quoter-button:visited {
  color: #c3c3c3}


.tag-pop-hover .tag-div {
  background: #151515;
  border: 1px solid #303030;
}
/* sure */
}
