css:
/* Courses styles (shared) */
@-moz-document url-prefix('http://ioc.xtec.cat/campus/course/view.php?') {

    /* Sidebar */
    .sideblock {
        background-color: whiteSmoke;
        border: 1px solid #ddd !important;
    }
    .sideblock .header {
        padding: 0 !important;
    }
    .sideblock .header .hide-show-image {
        margin-top: 4px;
        margin-right: 5px;
        background-color: white;
        padding: 2px;
    }
    .sideblock .title h2 {
        color: #58656A !important;
        background: #ddd !important;
        padding: 0 0 0px 10px !important;
        border-bottom: 1px solid #ddd !important;
        font-weight: bold !important;
    }
    .sideblock .content {
        padding-top: 5px !important;
    }
    
    /* Content */
    #breadcrumb-wrap-left {
        background: none !important;
        background-color: #383635 !important;
        border: 1px solid #DDD !important;
    }
    #breadcrumb-wrap-right {
        background: none !important;
        background-color: #383635 !important;
    }
    .navbar {
        background: none !important;
        background-color: #383635 !important;
    }
    .navbar a:link, .navbar a:visited {
        color: #fff !important;
    }
    .navbar a:hover, .navbar a:visited {
        color: #fff !important;
    }
    .breadcrumb ul li {
        color: #fff !important;
    }
    .headermain, h1.headermain {
        width: 100%;
        height: 20px;
        text-transform: uppercase !important;
    }
    #course-title {
        padding: 10px 25px !important;
        margin-bottom: 15px !important;
    }
    .star, h2.headingblock, .block_site_main_menu .title h2 {
        display: none !important;
    }
    #ioc-links {
        display: none !important;
    }
    
    /* Unread messages */
    .unread {
        background-color: #eee;
        padding: 1px 15px !important;
        float: right !important;
        border: 1px solid #ddd !important;
    }
    
    .unread a {
        color: #58656A !important;
    }

}
/* Courses styles (shared) */


