/* F_IOC_ATNESTU_1 - Atenció a l'estudiant */
@-moz-document url-prefix('http://ioc.xtec.cat/campus/course/view.php?id=842') {
    #course-view .section td.content {
        background-color: whiteSmoke;
    }
    #course-view .current .side {
        background-color: whiteSmoke;
    }
    div.summary {
        padding: 2px 2px 3px 6px !important;
        margin-bottom: 10px !important;
        border-bottom: 1px solid #ddd !important;
    }
    div.summary>font {
        color: #58656A !important;
        font-weight: normal !important;
    }
}
/* F_IOC_ATNESTU_1 - Atenció a l'estudiant */

/* F_TAL - Taller aprendre en línia 11-12 S2 */
@-moz-document url-prefix('http://ioc.xtec.cat/campus/course/view.php?id=4075') {
    /* Taller aprendre en línia 11-12 S2 - F_TAL */
    #course-view .section td.content {
        background-color: whiteSmoke !important;
    }
    #course-view .current .side {
        background-color: whiteSmoke !important;
    }
    #course-view .section .label >div {
        background-color:whiteSmoke !important;
    }
    #course-view .section .label >span {
        background-color:whiteSmoke !important;
    }
    #course-view .hidden .side {
        background-color: whiteSmoke !important;
    }
    ol {
        list-style: none !important;
        background-color: #E3E3E3 !important;
        border: 1px solid #D1D1D1 !important;
        padding: 10px !important;
    }
    ol li {
        margin: 6px 0 6px 0 !important;
    }
    div.summary h1 {
        font-size: 13px !important;
        margin-bottom: 10px !important;
        padding: 2px 2px 2px 6px !important;
        background-color: transparent !important;
        color: #58656A !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
        border-bottom: 1px solid #ddd !important;
    }
    div.summary h1 div {
        background-color: transparent !important;
        color: #58656A !important;
    }
    span.label h1:first-child {
        display: none !important;
        color: #58656A !important;
    }
    span.label:last-child h1 {
        font-size: 13px !important;
        margin-bottom: 10px !important;
        padding: 2px 2px 2px 6px !important;
        background-color: transparent !important;
        color: #58656A !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
        border-bottom: 1px solid #ddd !important;
    }
}
/* F_TAL - Taller aprendre en línia 11-12 S2 */

