css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.bbc.co.uk") {
DIV[class="arr"]
{
line-height: 0.8 !important; 
color:black !important;
}
div br{
line-height: 0 !important;
display:none !important;
}
body
{
color:black !important;
}
.arr{
	padding:0px 0 10px 10px;
	background:url("http://newsimg.bbc.co.uk/nol/shared/img/v3/arrow.gif") no-repeat 0px 5px;	
	line-height:0.8 !important;
}
.arr a, .statsStory .arr{
	font-size:1em !important;
	line-height:0.8 !important;
color:#009 !important;
}
.arrlink{
	background:url("http://newsimg.bbc.co.uk/nol/shared/img/v3/arrow.gif") no-repeat 0 0px;
	padding:0 0 0px 5px;
}
a.nlp
{
color:#009 !important;
}
DIV[id="blq-mast"]
{
display:none !important;
}
DIV[id="blq-accesslinks"]
{
display:none !important;
}
div[class="nlp"]
{
color:black !important;
font-weight: bold !important;
line-height: 0.8 !important ;
}
div[class="o"]
{
color:black !important;
}
div[class="mvb"]
{
color:black !important;
}
div[class="ticko"]
{
color:black !important;
font-weight: bold !important;
}
div[class="tickh"]
{
color:black !important;
font-weight: bold !important;
}
div[class="ticki"]
{
color:black !important;
font-weight: bold !important;
}
a.lp{
	font-weight:bold !important;
	color:#900 !important;
	font-size:10px !important;
}

.mvb a.lp{
	font-size:9px !important;
}
a.lp:visited{
	color:#990000 !important;
}
a.tsh{
	font-size:16px !important;
	font-weight:bold !important;
	color:#009 !important;
}
#customise ul li{
list-style-type:none;
margin: 0;
padding:0 0 0 7px;
display: block;
background:url("http://newsimg.bbc.co.uk/nol/shared/img/v3/arrow.gif") 0 7px no-repeat;
line-height:1em;
font-size:1.2em;
}
a.tsh:visited{
	color:#4F85AE !important;
}
a.tsl{
	color:#009 !important;
	padding:0 0 3px 0 !important;
}
a.tsl:visited{
	color:#009 !important;
}
a.tsl:visited{
	color:#009 !important;
}
a.shl, a.sshl{
	color:#000090 !important;
	font-weight:bold !important;
	text-decoration:none !important;
}
a.shl{
	font-size:12px !important;
}
a.sshl{
	font-size:11px !important;
}
a.shl:visited, a.sshl:visited{
	color:#000090 !important;
}
a.shl:hover, a.sshl:hover{
	text-decoration:underline !important;
}
a.tickl{
	font-size:11px !important;
	line-height:12px !important;
	text-decoration:none !important;
	color:#009 !important;
	font-weight:bold !important;
}
a.tickl:hover{
	color:#66C !important;
	text-decoration:none !important;
}
}
