css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {


/*BODY*/

/*[[compacted-center]]*/ 



body

  {

    background: #1c1c1c !important;  
    color: #CCCCCC !important;
    text-shadow: #000000 1px 1px 5px;

  }
  
.inputtext, input#recaptcha_response_field {
  
   background-color: #333333 !important;
   color: #cccccc !important;
   border-style: solid !important;
   border-color: #555555 !important;
  
  }
  
.postblock {
  
   background-color: #333333 !important;
   color: #cccccc !important;
   border-style: solid solid solid solid !important;
   border-color: #555555 !important;
  
  }

.pages, .pages td, th {
background-color: #1c1c1c !important;
color: #cccccc !important;
border-style: none none none none !important;

}

.pages td b {

color: #cccccc !important;
font-weight: bold !important;
font-size: 18px !important;

}



.postername, .commentpostername, span.filetitle, span.replytitle {

color: #556600 !important;


}

#header, #navbot, #navbotr {

color: #cccccc !important;

}

a:link, a:visited, span.omittedposts {

color: #9c9c9c !important;

}




/* hide elements */

.logo, .img, .recaptchatable td img#recaptcha_logo, img#recaptcha_tagline {

  display: none !important;

}

img[src*="http://static.4chan.org/support/"]{
    display: none !important;

}


/* borders */

hr {
    border-style: none none none none !important;
}


/* 4chan X */

#watcher, #watcher > .move, #options, #updater {

   background-color: #333333 !important;
   color: #cccccc !important;

}


#qr .move, #qr.reply.dialog, #qr .move a.close {

   background-color: #333333 !important;
   color: #cccccc !important;
   width: 30% !important;

}

#qr select, #qr > form {

   background-color: #333333 !important;

}

}

style_setting_options.value:
form
	{
	    margin: 0 auto !important;
		background: #1c1c1c;
		max-width: 900px !important;
		padding: 10px 5px 5px 5px;
		width: 85% !important;
	}

td.reply {

background: #333333 !important;
color: #CCCCCC !important;
border-style: none none none none!important;
width: 900px !important;
}

td.reply {

background: #333333 !important;
color: #CCCCCC !important;
border-style: none none none none!important;
width: 100% !important;
}
