css:
@-moz-document url-prefix("http://egza.org/bird/") {
.tw {
background-color: #eee;
margin-right: auto !important;
margin-left: auto !important;
left: auto;
}
.tw div {
padding: 7px 10px;
-webkit-transition: background .3s ease-out; -moz-transition: background .3s ease-out;
}
.tw .utils {
opacity: .5;
-webkit-transition: all .3s ease-out; -moz-transition: all .3s ease-out;
}
.rtinfo {
text-align: left;
float: left;
direction: ltr;
}
.tw > div {
margin: 0 auto;
padding: 0 5px;
width: 530px;
}
.tw div > div {
background: #fff;
margin: 15px 50px 15px 0;
padding: 5px;
border-radius: 10px;
}
.tw div:first-child > div {
background: #ffffdb;
border-right: 2px solid #999;
}
    .tw div:first-child > div.fromme, .tw div > div.fromme {
        background: #CFD;
    }
    .tw div:first-child > div.tome, .tw div > div.tome {
        background: #CCF;
    }
#tw2c > div:first-child:hover {
background-color: #fff !important;
}
.tw div > div:hover {
background: #d5e9ff !important;
}
.tw div > div:hover .utils {
opacity: 1;
}

.tw-parent .uicon {
margin-top: 5px;
margin-right: -57px;
width: 48px;
height: 48px;
border-radius: 5px;
box-shadow: 1px 1px 3px -1px #000;
}

.fromme {
margin: 12px 0 12px 50px !important;
}
.fromme .uicon {
float: left;
margin-left: -77px;
}

.dummy {
display: none;
}
.tw .status {
display: block;
font-size: 13px;
line-height: 21px;
margin: 4px 5px;
}
#reps > div {
min-height: 49px;
}

#reps > div:before, 
#reps > div:after
{
    clear: both;
    content: " x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x x ";
    display: block;
    font-size: xx-large;
    height: 0 !important;
    line-height: 0;
    overflow: hidden;
    visibility: hidden;
}

}
