css:
@-moz-document domain("www.automarket.ro") {
body{background:#444 !important}
.article_drive_test,
.article{font:normal 16px/1.2 georgia,'times new roman',times,garamond,serif!important}
.excerpt,.article .excerpt{margin:0.8em 0!important;font:bold 1.2em/1.25em georgia,'times new roman',times,garamond,serif!important}
.article_content>div,
#article_body{line-height:22px}
.article_content>div strong,
#article_body strong{color:black;font-weight:normal;background:rgba(50,150,190,.08);text-shadow:1px 1px 0 rgba(0,0,0,.1)}
.table_style_1{font-size:14px!important}
.article_title,
.article h1{font:bold normal 30px/32px 'lucida grande', 'times new roman', garamond, serif !important;margin:10px 0!important}
.main_image .zoom,
.article .zoom{width:auto!important;height:auto!important;left:0;right:0!important;top:0;bottom:0!important;background:none!important}
#jb_iframe{display:none!important}
#search_home{display:none!important}
.article_drive_test .zoom{left:0!important;width:auto!important;height:auto!important;right:0!important;top:0!important;bottom:0!important;background:0!important}
.article_drive_test .zoom:hover,
.article_drive_test .zoom:focus{background:url("http://automarket.ro/img/lupa_zoom.png") no-repeat center center !important}
.main_image{overflow:hidden}
.main_image .overlayer,
.main_image .top_description,
.main_image .general_rating{-moz-transition:margin-top 150ms ease-in-out;-webkit-transition:margin-top 150ms ease-in-out;-o-transition:margin-top 150ms ease-in-out;-ms-transition:margin-top 150ms ease-in-out;transition:margin-top 150ms ease-in-out}
.main_image:hover .overlayer,
.main_image:hover .top_description,
.main_image:hover .general_rating{margin-top:-90px!important}
.branding_links_container{display:none!important}
.top_menu{top:0!important}
.top_menu>.nav{margin:0!important}
#main_automarket>img[width="1"] {display:none!important}
.article_utils>ul{margin:0!important;padding:0!important;}
#header_container{padding-top:0!important}
#header_v4{height:125px!important;width:1100px!important}
#header_v4 .top_menu{margin:10px 0 0 !important;position:relative !important;width:1100px!important}
#main_container{padding-top:125px!important;width:1100px!important}
#left{width:750px!important}
[id^="ticker"],
#eScapeISdropin,
.google_ads_article,
#box_alert_container,
#AutomarketBrandingModal_mask,
#AutomarketBrandingModal_c,
.article_utils,
.meta br,
.logo_and_banner>div,
.banner_300x250,
#comments_list li>ins,
.box>div[id]:not(#categories),
.content_container+.fb-like,
body>object,#topl.inside,
[id^=bmone2t],
.article_widget.widget_configurator,
.article_widget.widget_newsletter,
#banner_billboard{display:none!important}
#curr_img{max-width:100%!important}
#header_v4 a.logo{margin-top:0!important}

#banda_footer,#footer{-moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box;width:1100px!important}
#banda_footer{height:163px!important}

.listare_stiri           { font:normal 16px/20px georgia,times,serif!important}
.listare_stiri .stire    { padding:20px 0 !important}
.listare_stiri .stire h3 { font-size:17px!important;line-height:22px!important}
.listare_stiri .stire .info,
.listare_stiri .stire .info a{font-size:13px!important}


#page_nav span, #page_nav a{padding:5px 8px !important;margin-right:6px !important}
#page_nav .container-navigation-trigger, #page_nav .next-group{padding:2px!important;margin-right:2px !important}

.custom_container #left{width:720px!important}
.container_branded_article,
.custom_container .content_page{background-repeat: repeat-x !important}



.article_layout_2{font:normal 15px/20px georgia,times,serif!important}
.article_layout_2 .entry{padding-left:0!important}
.article_layout_2 .entry,
.article_layout_2 .entry p{font-size:16px!important;line-height:22px!important}


.box_comment_list ul li .right{width:500px!important}
}
