css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

#logo {height: 0 !important;
	width: 0 !important;
	padding-left: 400px !important;
	padding-top: 175px !important;
	background: url(http://i3.photobucket.com/albums/y90/insaneonyx/PhotoshopnGraphics/dwlogo9.png) no-repeat !important;}


body 
	{background-color:#FFFFFF;background: url('http://i3.photobucket.com/albums/y90/insaneonyx/PhotoshopnGraphics/dwbg9.png') bottom left #000000 no-repeat fixed !important;}

*  
	{text-shadow:none !important;}
	


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0.2) !important;}		


#auto_pagination_loader
	{background:rgba(56,161,207, 0.00)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .5 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #ab6b0f !important;}	
	
.nipple { 
   margin-left:1px !important;
   background: none !important;
   width: 0px !important;
   height: 0px !important;
   border-right: solid 6px rgba(131,9,6, 1.00) !important;
   border-bottom: 7px solid transparent !important;
   border-left: 0px solid transparent !important;
   border-top: 7px solid transparent !important;
   margin-top: 0px !important;
 }
	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes {
	color: rgba(000,000,000, 0.80) !important; 
	background: #ab6b0f !important;}

	
	

	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#ab6b0f !important;  
	background-image:none !important;
	border:0px !important}

div.hide_overflow
	{font-size:.10;}

a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:9px;}
a.tag
	{position:relative;top:-10px;}
.selected  .tag
	{height:14px;background:#ab6b0f !important; 
	position:relative;top:0px;
	color:rgba(000,000,000, 0.80) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #ab6b0f !important;}
	


div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#ab6b0f !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}



blockquote
	{margin-left:-5px !important;}



.login.sign_up,
A[href="/forgot_password"] {display: none !important;}	



.mceContentBody {
  background: none !important; }
 


.permalink {

  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(170,179,188, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}

.permalink:hover {
background: rgba(127,139,152, 1.0 ) !important;

}
 


#left_column #posts .notification {
                        background-color:#531210 !important;
                        color:#531210 !important;
                        border-top-color: #531210 !important;
                        border-right-color: #531210 !important;
                        border-left-color: #531210 !important;
                        border-bottom-color: #531210 !important
                    }

#left_column #posts .notification .block {
                            color:#531210 !important;
                        }

#left_column #posts .notification.alt {
                        background-color: #531210 !important;
                    }
#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#531210 !important;
                    }
#left_column #posts .notification a {
                        color:#565656 !important;
                        border-bottom:0px !important;
                    }
#left_column #posts .notification blockquote {
                        border-left-color:#531210 !important;
                    }

#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#531210 !important;
opacity:0.5 !important;
                    }

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
                    }

#right_column a#dashboard_switch_blog_menu_current:hover 
	{background:#5c1206;}
	
#nav .nav_item.active
	{background-color:#531210;}
	
#right_column
	{color:#5c1206;}
	
#left_column #posts .post .permalink
	{background-color:#531210;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#531210 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

.notification_type_icon
	{display:none !important}

#left_column #posts .notification 
	{background-color:#830906 !important;
	color:#000 !important;
	border-bottom-color: #830906 !important}

#new_post_notice_container
	{color:#fff !important}

#new_post_notice_container a
	{color:#fff !important}

}
