css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 

 {



*{

text-shadow: none !important; 

color: #f3b685 !important;

}



body {

background:none; !important;

}



.dashboard_switch_blog_menu_header{

background-color: #2a052a !important;

color: #666 !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current

{

background-image: none !important;

font-family :Helvetica !important;

font-size: 11px !important;

}



.notification_type_icon{

display: none !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



.dashboard_switch_blog_menu_item{

background-color: #2a052a !important;

color: #666 !important;

border: 0px !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



.dashboard_switch_blog_menu_footer{

background-color: transparent !important;

color: #666 !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



.dashboard_switch_blog_menu_wrapper{

background-color:#2a052a !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



#dashboard_index .post_title{

color: #A27872 !important;

font-family: Helvetica !important;

font-size: 25px !important;

}



h1,h2,h3,h4,h5,h6{

color: #8A675E !important; 

font-family: Helvetica !important;

font-size: 12px !important;

}



a:link, a:visited {

color: #B38689 !important; 

text-decoration: none !important;

font-weight: normal !important;

text-transform: lowercase !important;

filter: alpha(opacity=100) !important;

-moz-opacity: 1 !important;

opacity: 1 !important;

-webkit-transition: color 1s !important;

font-family: Helvetica !important;

font-size: 12px !important;

}



a:hover {

color: #8c183a !important;  

filter: alpha(opacity=100) !important;

-moz-opacity: 0.100 !important;

opacity: 1 !important;

-webkit-transition: color 1s !important;

font-family: Helvetica !important;

font-size: 11px !important;

}



li.post {

background: #110615!important;

border:0px!important;

-webkit-box-shadow:none !important;

-moz-box-shadow:none !important;

box-shadow:none !important;

}



#new_post {

background-color: #110615!important;

}



#new_post .arrow,.avatar_and_i .arrow,.arrow {

background: none!important;

width: 0px!important;

height: 0px!important;

border-right: solid 9px #110615!important;

border-bottom: 9px solid transparent!important;

border-left: 2px solid transparent!important;

border-top: 9px solid transparent!important;

margin-top: 0!important;

}



.post_avatar,.sub_avatar {

background-color: transparent !important;

}



.post.photo_post .photo, .post.photo_post .video_thumbnail {

-moz-box-shadow: 1px 1px 3px #999999 !important;

}



.content.is_media, .mask.top, .mask.bottom {  

background: none !important;

}



#right_column UL:first-child.right_column_section, .big_button {  

background-image: none !important;

}



.post.photo_post .photo, .post.photo_post .video_thumbnail{

box-shadow: 0px 0px 0px 0px #110615 !important;

-moz-box-shadow: 0px 0px 0px 0px #110615 !important;

-webkit-box-shadow: 0px 0px 0px 0px #110615 !important;

border-top: 0px solid transparent !important;

border-bottom: 0px solid transparent !important;

border-right: 0px solid transparent !important;

border-left: 0px solid transparent !important;

-moz-border-radius: 10px!important;

-webkit-border-radius: 10px!important;

border-radius: 12px!important;

}



#content_top,#content_top {

display: none!important;

}



#post_state,.text_field,#tag_editor{

-moz-border-radius:9px!important;

-webkit-border-radius:9px!important;

border-radius:9px!important;

}



#content_bottom {

display: none!important;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color: #cccccc!important; 

background: transparent !important;

font-family: Helvetica !important;

font-size: 11px !important; 

}



ol#posts li.notification{

color: #f3b685 !important;

background-color: #2a052a !important; 

border-bottom: 1px solid #2a052a !important;

border-top: 1px solid #2a052a !important; 

border-left: 1px solid #2a052a !important; 

border-right: 1px solid #2a052a !important;  

opacity: 1;

font-family: Helvetica !important; 

font-size: 11px !important;

}



.notification_type_icon.reblog_icon:before{

position: absolute !important;

content: "" !important;

border-top: solid 4px transparent !important;

border-bottom: solid 4px transparent !important;

border-left: solid 4px #2a052a !important;

left: 0 !important;

width 0 !important;

height: 0 !important;

margin-top: -5px !important;

margin-left: 10px !important;

}



.notification_type_icon.reblog_icon:after{

position:absolute!important;

content:""!important;

border-top:solid 4px transparent!important;

border-right:solid 4px #320428!important;

border-bottom:solid 4px transparent!important;

left:0!important;width:0!important;

height:0!important;

margin-top:-10!important;

margin-left:-4px!important;

}



h1 {

color: #d4694c;

text-shadow: 0 0 0 #ffffff;

font-family: Helvetica !important; 

}



.no_posts_found, h1.dashboard_header {

color: #cccccc important!;

}



.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {

background: none!important;

width: 0!important;

height: 0!important;

margin-top: 0!important;

border-bottom: solid 10px #2a052a !important;

border-right: 10px solid transparent !important;

border-left: 10px solid transparent !important;

}



.nipple{

background: none !important;

width: 0 !important;

height: 0 !important;

border-right: 0 solid transparent !important;

border-bottom:0 solid transparent !important;

border-left:0 solid transparent !important;

border-top: 0 solid transparent !important;

margin-top: 0 !important;

margin-left: 0 !important;

}



.nipple.border {

background: none !important;

width: 0 !important;

height: 0!important;

border-right: 0 solid transparent !important;

border-bottom: 0 solid transparent !important;

border-left: 0 solid transparent !important;

border-top: 0 solid transparent !important;

margin-top: 1px !important;

margin-left: 0 !important;

}



.post_question_nipple {

background: none !important;

width: 0 !important;

height: 0 !important;

margin-top: 0 !important;

border-top: 8px solid transparent !important;

border-right: 8px solid transparent !important;

border-left: 8px solid transparent !important;

}



.return_to_top_icon{

content: "" !important;

background: #774F67 !important;

border: solid thin #6B475D !important;

-moz-border-radius: 9px!important;

-webkit-border-radius: 9px!important;

border-radius: 9px !important;

}



.return_to_top_icon:before{

width: 0px!important;

position: absolute!important;

content: ""!important;

margin-top: 12px!important;

margin-left: 12px!important;

border-right: solid 13px transparent!important;

border-bottom: solid 13px #4B3541 !important;

border-left: solid 13px transparent!important;

}



.return_to_top_icon:after{

position:absolute!important;

content: ""!important;

width: 12px!important;

height: 13px!important;

margin-top: 25px!important;

margin-left: 19px!important;

background-color: #42263A !important;

}



a[class^="like_button"],a[class*="already_like"],.notification_type_icon.like_icon,.forever_alone .likes{

background-image:none!important;

}



a[class^="like_button"]:before,a[class^="like_button"]:after,a[class*="already_like"]:before,a[class*="already_like"]:after,.notification_type_icon.like_icon:before,.notification_type_icon.like_icon:after,.forever_alone .likes:before,.forever_alone .likes:after{

position:absolute!important;

content:""!important;

left:10px!important;

top:0!important;

width:10px!important;

height:15px!important;

background:#14020A!important;

-moz-border-radius:50px 50px 0 0!important;

-webkit-border-radius:50px 50px 0 0!important;

border-radius:50px 50px 0 0!important;

-moz-transform:rotate(-45deg)!important;

-webkit-transform:rotate(-45deg)!important;

-moz-transform-origin:0 100%!important;

-webkit-transform-origin:0 100%!important;

}



a[class*="already_like"]:before,a[class*="already_like"]:after{

background: #9b084a!important;

}



a[class^="like_button"]:before,a[class^="like_button"]:after,a[class*="already_like"]:before,a[class*="already_like"]:after{

margin-top:17px!important;

margin-left:503px!important;

}



.notification_type_icon.like_icon:before,.notification_type_icon.like_icon:after{

margin-top:-1px!important;

margin-left:-5px !important;

}



a[class^="like_button"]:after,a[class^="already_like"]:after,.notification_type_icon.like_icon:after,.forever_alone .likes:after{

left:0!important;background: #14020A !important;

-moz-transform:rotate(45deg)!important;

-webkit-transform:rotate(45deg)!important;-

moz-transform-origin:100% 100%!important;

-webkit-transform-origin:100% 100%!important;

}



.forever_alone .likes:before,.forever_alone .likes:after{

margin-top:17px!important;

margin-left:12px!important;

}



a[class*="already_like"]:after{

background:#9b084a!important;

}





.notification_type_icon.follow_icon,.controls_section .following{

background-image:none !important;

}



.notification_type_icon.follow_icon:before,.controls_section .following:before{

position:absolute!important;

content:""!important;

background: #0E010E !important;

left:4px!important;width:14px!important;

height:5px!important;margin-top:8px!important;

margin-left:-4px!important;

border-top-right-radius:4px!important;

border-top-left-radius:4px!important;

}



.notification_type_icon.follow_icon:after,.controls_section .following:after{

position:absolute!important;

content:""!important;  

background: #0E010E !important;

left:9px!important;

width:6px!important;

height:10px!important;

margin-top:20px!important;

margin-left:-5px!important;

-moz-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;

-webkit-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;

border-radius:5px 5px 5px 5px / 5px 5px 10px 10px;

}



.controls_section .following:before {

width: 18px!important;

height: 6px!important;

margin-top: 7px!important;

margin-left: 8px!important;

}



.controls_section .following:after {

width:8px!important;

height:12px!important;

margin-top:-19px!important;

margin-left:8px!important;

}



.notification_type_icon.reblog_icon{

margin-top:2px!important;

background-image:none!important;

border-top:solid 2px #0E010E!important;

border-right:solid 2px #0E010E!important;

border-bottom:solid 2px #0E010E!important;

border-left:solid 2px #0E010E!important;

border-bottom-right-radius:1px!important;

border-top-left-radius:1px!important;

height:5px!important;width:10px!important;

}



.notification_type_icon.reblog_icon:before{

position:absolute!important;

content:""!important;

border-top:solid 4px transparent!important;

border-bottom:solid 4px transparent!important;

border-left:solid 4px #0E010E!important;

left:0!important;

width:0!important;

height:0!important;

margin-top:-5px!important;

margin-left:10px!important;

}



.notification_type_icon.reblog_icon:after{

position:absolute!important;

content:""!important;

border-top:solid 4px transparent!important;

border-right:solid 4px #1B0317!important;

border-bottom:solid 4px transparent!important;

left:0!important;width:0!important;

height:0!important;

margin-top:-15px!important;

margin-left:-4px!important;

}



.controls_section .tag{

background-image:none!important;

}



.controls_section .tag:before{

position:absolute!important;

content:""!important;

width:9px!important;

height:9px!important;

background-color: none!important;

margin-top:0px!important;

margin-left:-25px!important;

-moz-transform:rotate(45deg)!important;

-webkit-transform:rotate(45deg)!important;

-moz-transform-origin:0 100%!important;

-webkit-transform-origin:0 100%!important;

-moz-box-shadow:1px 1px 1px rgba(0,0,0,0)!important;

-webkit-box-shadow:1px 1px 1px rgba(0,0,0,0)!important;

box-shadow:1px 1px 1px rgba(0,0,0,0)!important;

}



.controls_section .tag:after{

position:absolute!important;

content:""!important;

width:6px!important;

height:7px!important;

background:transparent!important;

margin-top:-17px!important;

margin-left:-28px!important;

border-top:solid 2px #110210!important;

border-left:solid 2px #300629!important;

-moz-border-radius:3px!important;

-webkit-border-radius:3px!important;

border-radius:3px!important; 

-moz-transform:rotate(86deg)!important;

-webkit-transform:rotate(86deg)!important;

-moz-transform-origin:100% 100%!important;

-webkit-transform-origin:100% 100%!important;

}



.notification_type_icon.like_icon:before,.notification_type_icon.like_icon:after{

margin-top:-1px!important;

margin-left:-5px !important;

}



.selected .posts{

border-top-right-radius:6px!important;

border-top-left-radius:6px!important;

border-top:solid thin #20051C!important;

}



.dashboard_options_form,.post_avatar.flat.lighter_blue{

background:#160213!important;

border-color:#180515!important;

color:#B99D88!important;

}



.dashboard_options_form_disclaimer,.dashboard_options_form a,.publish_on_day, .publish_on_time{

color:#BBA38F!important;

}



.selected .count,.selected .posts,.selected .followers,.selected .messages,.selected .drafts,.selected .queue{

background-color:#0E030A!important;

color: #B49F8E!important;

}



#right_column UL:nth-child(3) LI:first-child:not([class="selected"]) A{

background-color:#300629!important;

border-top-right-radius:6px!important;

border-top-left-radius:6px!important;

border-top:solid thin #300629!important;

}



#right_column UL:nth-child(3) LI:first-child[class="selected"] A{

background-color:#300629!important;

border-top-right-radius:6px!important;

border-top-left-radius:6px!important;

margin-top:-1px!important;

border-top:solid thin #300629!important;

}



#right_column UL:nth-child(3) LI:first-child A:hover{

background-color:#300629!important;

}



#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background-color: #d95944 !important;

background-image: none;

background-repeat: repeat;

background-attachment: scroll;

background-position: 0% 0%;

padding-top: 5px;

margin-right: 10px !important;

padding-bottom: 5px;

padding-left: 5px;

}



#show_radar a, #hide_radar a,

#hide_recommended_blogs a, #show_recommended_blogs a {

color: transparent !important;

font-family: Helvetica !important;

font-size: 11px !important;

background: none !important;

}



#left_column  H1,div.l-content DIV:nth-child(3) H1{

font-size:18px!important;

}



#left_column H1,.optional,.first,#left_column H2:nth-child(10),.mceLayout,#left_column H2:nth-child(10),#controls DIV,.advanced_option,#use_url_link A,#add_photo_link A,#left_column H2:nth-child(8),#add_link_description  A,#right_column H2{

color:#f2b586!important;

border-color:#300629!important;

}



#left_column H2:nth-child(10){

margin-top:-30px!important;

}



#left_column #posts .post .permalink {

background-color: #300629 !important;

}



#left_column #posts .post{

border-bottom: 1px #300629 !important;

}



#left_column #posts .post .post_avatar{

width: 64px !important; 

height: 64px !important; 

margin: 0px !important;

-moz-border-radius: 30px !important; 

-webkit-border-radius: 60px !important; 

}



#left_column #posts .post .sub_avatar{

width: 30px !important; 

height: 30px !important; 

margin: 0px !important;

-moz-border-radius: 25px !important; 

-webkit-border-radius: 60px !important; 

border-radius: 25px !important;

}



.permalink{

background:none!important;

width: 16px!important;

height:16px!important;

background: #08030a!important;

border-bottom-left-radius:10px!important;

border-top-right-radius:24px!important;

margin-top:6px!important;

margin-right:6px!important;

-moz-box-shadow:-1px 1px 2px #08030a!important;

-webkit-box-shadow:-1px 1px 2px #08030a!important;

box-shadow:-1px 1px 2px #08030a!important;

}



.permalink:hover{

background:#9b084a!important;

}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color: #0E010E !important;

font-family: Helvetica !important;

font-size: 16px !important;

background-image: none !important;

border: 0px !important;

}



#nav .nav_item .nav_menu {

border-bottom: 0px !important;

font-family: Helvetica !important;

font-size: 12px !important;

}



#nav .nav_item .nav_menu a {

color: #888 !important;

border-top-color: #eee !important;

font-family: Helvetica !important;

font-size: 12px !important;

}



#nav .nav_item .nav_menu a:hover {

color: #C59D92 !important;

font-size: 12px !important;

}



#footer, #footer a {

color: #AD867B !important;

background-color: transparent !important;

background-image: none !important;

font-family: Helvetica !important;

font-size: 12px !important;

}



A[class^="card"]{

-moz-box-shadow: 0px 2px 2px #110615 !important;

}



.card .photo {

border-bottom:solid thin #110615 !important;

-moz-box-shadow: 1px 2px 2px #110615, -1px 1px 1px #300629 !important;

}



blockquote{

margin-left:-5px!important;

border-left:solid 3px #ea8c61!important;

}



li.selected,li.recessed,a.active,div.post_filter_tabs a{

background:none!important;

}



DIV[class^="post_question"]:not([class="post_question_nipple"]),.chat_line:nth-child(even),PRE{

background-color:#410531!important;

color: #f3b685!important;

}



#recommended_tumblelogs {

background: transparent !important;

}



ol#posts li.post.new_post {

background: #110615 url('http://oi42.tinypic.com/p16gz.jpg') top center no-repeat !important;

}



img[src*="http://oi42.tinypic.com/p16gz.jpg"]{

width:0;

height:70px;

padding-right:250px;

opacity: 0;

}



}
