css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
	.hborder {
		background-color:#333 !important;
	}
	
	.border {
		background-color: #555 !important;
	}

	div.logo img, th[bgcolor="#e04000"], hr, #hd, #ft, #option-button, #navtopr, img.rotating, td[align="right"], .bf, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], #recaptcha_logo, #recaptcha_tagline, .rules {
		display: none !important;
	}
	
	body {
		padding: 0;
		background: #2C2C2C !important;
		color: #eee !important;
	}
	
	#header {
		left: 0 !important;
		right: 0 !important;
		top: 0 !important;
	}

	a {
		color: #efefef !important;
	}

	#navtop {
		width: 100%;
		background: #000000 !important;
		text-align: center !important;
		color: #eee !important;
		padding: .4em 0 !important;
		top: 0;
		z-index: 1;
	}

	#navtop a:hover {
		background: #555 !important;
	}

	.logo, .logo font b span{
		color: #fff !important;
	}

	form {
		margin: 1em !important;
	}

	.inputtext, textarea {
		width: 295px !important;
	}

	td.postblock {
		background: #444 !important;
		border: 1px solid #666 !important;
		color: #aaa !important;
	}

	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, #backwash_tooltip, td.reply {
		background-color:#333 !important;
		padding: .2em !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		-moz-border-radius: 3px !important;
		-webkit-border-radius: 3px !important;
		border-radius: 3px !important;
	}

	td.replyhl {
		background-color:#4B4B4B !important;
		padding: .2em !important;
		border: 1px solid #4B4B4B !important;
		color: #eee !important;
		-moz-border-radius: 3px !important;
		-webkit-border-radius: 3px !important;
		border-radius: 3px !important;
	}
	
	td.reply, td.replyhl {
		width: 100% !important;
	}
	
	#updater {
		color: #fff !important;
	}

	.postername, .commentpostername, .omittedposts, .abbr {
		color: #fff !important;
	}

	.filetitle, .replytitle {
		color: #e15014 !important;
		font-size: large;
	}

	.doubledash {
		visibility: hidden !important;
	}

	th {
		background: #222 !important;
		padding: .2em !important;
		font-size: small;
		width: 200px !important;
	}

	.boxbar {
		background: #444 !important;
		color: #eee !important;
	}

	.box-outer {
		border: none !important;
		background: #333 !important;
		color: #eee !important;
	}

	.box-inner {
		background: #333 !important;
		color: #eee !important;
	}

	.pages {
		 background: #444 !important;
		 border: none !important;
	}

	.pages td, .pages td b {
		color: #eee !important;
	}

	div.logo img, pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt ! important;
		color: #222 !important;
		background: #888888 !important;
		border-color: gray !important;
		border: 1px solid;
	}

	#filter-container .yuimenuitem-selected {
		border-color: gray !important;
	}

	.boxbar, #preview-tooltip-nws h2, #preview-tooltip-ws h2, #disclaimer-dialog h2, .hd {
		background-color: transparent !important;
	}

	.menubutton {
		background-image: none !important;
	}
	
	dd {
		color: #fff !important;
		border-bottom: 1px gray solid !important;
	}
	
	#recent-images li {
		background: #333 !important;
	}
	
	pre, textarea {
		color: inherit !important;
		background-color: #333333 !important;
		font-size: 12pt ! important;
		border-color: gray !important;
	}
	
	pre, textarea, input:not([type="checkbox"]):not([type="reset"]), select {
		-moz-appearance: none !important;
		color: #222222 !important;
		background-color: #888888 !important;
		font-size: 12pt ! important;
		border: solid gray 1px !important;
	}

	input[type="checkbox"] {
		-moz-appearance: none !important;
		-webkit-appearance: none !important;
		background: transparent !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		height: 10px !important;
		width: 10px !important;
		-moz-border-radius: 2px !important;
		-webkit-border-radius: 2px !important;
		border-radius: 2px !important;
	}

	.reply input[type="checkbox"] {
		border: 1px solid rgba(255,255,255,.3) !important;
	}

	input[type="checkbox"]:checked, .reply input[type="checkbox"]:checked {
		background: rgba(255,255,255,.4) !important;
	}
	
	img#recaptcha_reload {
		height: 0px !important;
		padding-top: 18px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}

	img#recaptcha_switch_audio {
		height: 0px !important;
		padding-top: 15px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}
	
	img#recaptcha_whatsthis {
		height: 0px !important;
		padding-top: 16px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}
}
