css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.sockshare.com/file/"), url-prefix("http://www.putlocker.com/file/") {

/* Buttons (Continue / Pro) */

    #submitButton
    {
        width:800px !important;
        display:inline !important;
        font-size:50px;
    }

    .confirm_button
    {
        display:none !important;
    }
    
/* REMOVE UNIMPORTANT STUFF*/

    .site-header,
    .ad_728x90_top,
    .push,
    .site-footer,
    h3,
    h2,
    .player_hover_ad,
    .share_bar,
    .mobile_badge,
    .download_file_link,
    img[src="/images/icon_no.gif"],
    img[src="/images/icon_yes.gif"],
    #embed_code
    {
        display:none !important;
    }



/* BLACK */

    body,
    th,
    .choose_speed,
    .container-back,
    .choose_speed td,
    .site-content
    {
        color:#000 !important;
        background-image:none !important;
        background-color:#000 !important;
	   border-width: 0px !important;
    }
}
