css:
/* by hkr (dreamflame in) */
@-moz-document url("about:newtab"){
  * {
    text-shadow: 1px 1px 5px rgba(0,0,0,1) !important;
    -moz-transition: 100ms ease-out !important;
  }
  vbox#scrollbox {
    background: #020202 center center fixed repeat url("data:image/png;base64,") !important;
    box-shadow: inset 0px 0px 8px 2px rgba(0,0,0,1), inset 0px 0px 200px 10px rgba(0,0,0,1) !important;
    outline: none !important;
    border: 0px !important;
  }
  ul#grid {
    overflow: visible !important;
    width: 704px !important
  }
  li.cell {
    outline-color: transparent !important;
    margin: 16px !important;
  }
  li.cell:hover { outline-color: rgba(255,255,255,.6) !important; }
  a.site, img.site-img { border-radius: 8px; }
  a.site {
    outline-color: transparent !important;
    background: -moz-linear-gradient(top, rgba(95,3,175,1) 0%, rgba(125,25,255,1) 50%, rgba(125,25,255,1) 60%, rgba(175,89,255,1) 100%) !important;
    box-shadow: inset 0px 0px 20px rgba(240,180,255,.7), 0px 0px 10px rgba(20,0,50,1) !important;
    border: 1px solid rgba(0,0,0,1) !important;
    opacity: .8 !important;
  }
  a.site:focus { box-shadow: inset 0px 0px 20px rgba(240,180,255,.7) !important; }
  a.site:hover, a.site:focus { opacity: 1 !important; }
  span.site-title, span.site-strip { box-shadow: 0px 0px 10px rgba(0,0,0,1) !important; }
  span.site-title {
    background: -moz-linear-gradient(center top, rgba(255,255,255,.01) 0%, rgba(0,0,0,.3) 4%, rgba(0,0,0,.9) 100%) center bottom repeat !important;
    height: 29px !important;
    padding-top: 2px !important;
    border: none !important;
    border-top: 1px solid rgba(0,0,0,.7) !important;
    border-radius: 0px 0px 6px 6px;
  }
  span.site-strip {
    width: 193px !important;
    background: -moz-linear-gradient(center top, rgba(255,255,255,.2) 0%, rgba(0,0,0,.6) 4%, rgba(0,0,0,.9) 100%) center bottom repeat !important;
    border: 1px solid rgba(255,255,255,.1) !important;
    border-top: none !important;
    border-bottom: none !important;
    border-radius: 9px 9px 0px 0px;
  }
  img.site-img, span.site-title, input.strip-button { opacity: .8 !important; }
  span.site-title:hover, span.site-strip:hover { opacity: 1 !important; }
  input.button {
    box-shadow: inset 0px 0px 8px rgba(0,0,0,.8) !important;
    border-radius: 4px;
  }
}
