css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
#right_column {
	background: none;
}
#dashboard_index, #dashboard_edit_post {
	background-image: url('http://i.imgur.com/tnDBR.jpg') !important;
	background-attachment: fixed !important;
        background-size: cover !important;
        background-position: bottom center !important;
}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: rgba(44,71,98,0.6) !important;
}
.recessed {
	background: rgba(40,70,90,0.7) !important;
}
#right_column .selected {
	background: -moz-linear-gradient(center top , #84AC54 0pt, #7CA44B 50%, #739946 50%, #6B903E 100%) repeat scroll 0 0 transparent !important;
}
.selection_nipple {
	border-bottom: 12px solid rgba(44,71,98,0.6) !important;
}

.post_info a {
	text-decoration:none !important;
}
.post_info a:hover {
	text-decoration:underline !important;
}

li.regular {
	border:2px solid #474747 !important;
}
li.regular .post_info a {
   	color:#474747 !important;
}

li.note[id*="post"] {
	border:2px solid #2C4762 !important;
}
li.note .post_info a {
   	color:#2C4762 !important;
}

li.photo {
	border:2px solid #d86143 !important;
}
li.photo .post_info a {
  	color:#d86143 !important;
}

li.quote {
	border:2px solid #f19e34 !important;
}
li.quote .post_info a,
span.quote {
   	color:#f19e34 !important;
}

li.link {
	border:2px solid #58bc8a !important;
}
li.link .post_info a {
   	color:#58bc8a !important;
}

li.conversation {
	border:2px solid #4ca8d7 !important;
}
li.conversation .post_info a {
   	color:#4ca8d7 !important;
}
.chat_line strong {
   	color:#4ca8d7 !important;
}
  
li.audio {
	border:2px solid #ab7ec3 !important;
}
li.audio .post_info a {
   	color:#ab7ec3 !important;
}

li.video {
	border:2px solid #78828c !important;
}
li.video .post_info a {
   	color:#78828c !important;
}
}
