css:
/* wikipedia.org - Mepis blue                                                       */
/* by Agnelo de la Crotche - 04.02.2008  - http://www.unixversal.com                */
/* This is a light blue redesigned Wikipedia for Mepis fans                         */
/*                                                                                  */
/* other Wikipedia styles based on the same rules set                               */
/* wikipedia.org - Black & silver       http://userstyles.org/styles/6044           */
/* wikipedia.org - Ubuntu coffee beans  http://userstyles.org/styles/6069           */
/* wikipedia.org - chameleon green      http://userstyles.org/styles/6080           */
/* wikipedia.org - Mandriva blue        http://userstyles.org/styles/6083           */
/* wikipedia.org - Fedora slate blue    http://userstyles.org/styles/6157           */
/* wikipedia.org - Ubuntu Gold          http://userstyles.org/styles/6464           */
/*                                                                                  */
/* LCARS redesigned styles :                                                        */
/* Black Google in LCARS terminal               http://userstyles.org/styles/6738   */
/* Black Google in LCARS - supplemental         http://userstyles.org/styles/6906   */
/* wikipedia.or Start Trek LCARS terminal       http://userstyles.org/styles/6594   */
/* userstyles.org Star Trek LCARS redesigned    http://userstyles.org/styles/6510   */
/* Enjoy !                                                                          */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

@-moz-document domain(wikipedia.org),
               domain(wikimedia.org),
               domain(wikinews.org),
               domain(wikibooks.org),
               domain(wikiquote.org),
               domain(wikiversity.org),
               domain(wikisource.org),
               domain(wiktionary.org){
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

:focus { outline: none !important}   /* don't outline focused objects */
* {background-color: transparent !important; border-color: #66bef3 !important}

/* enforce transparent bg */
html, .noprint, .vectorTabs, #simpleSearch, #content, #footer {background: transparent !important}

a{color: #01277e !important}

/* enforce dark fg */
table.plainlinks {background-color: #002d4d !important}



/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* background */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* gradient */

/* to disable the gradient comment out the rule below and uncomment the rule following */
/* (the line directly after the label "solid") */

body{
  background: #62a1c7 url("data:image/png;base64,") top left repeat-x !important} 
/* solid  */
/* body{background: #62a1c7 !important} */


#mw-panel.collapsible-nav div.portal {background-image: url("data:image/png;base64,")!important; background-position: 0 3px !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* links colors */

/* article body */
a:link{color: #1b57de !important; font-weight: bold !important}
a:visited{color: #fde20b !important; font-weight: bold !important}
a:hover{color: #b0d7f2 !important; text-decoration: none !important; font-weight: bold !important}

/* non existing pages */
a.new:link, a.new:visited{color: #b82209 !important}
a.new:hover{color: #dd1212 !important}

/* tabs on user preferences page */
#preftoc a{color: #d2e1eb !important} 
#preftoc a:hover{color: #ffffff !important} 
#preftoc li.selected a {color: #fd250c !important}

/* wikitable */
.wikitable a:link{color: #1b57de !important} 
.wikitable a:visited{color: #fde20b !important} 
.wikitable a:hover{color: #5576cc !important} 

/* plainlinks */
.plainlinks a{color: #1f4f76 !important; font-weight: normal !important}
.plainlinks a:hover{color: #ffffff !important; font-weight: normal !important} 

/* catlinks */
#catlinks a{color: #254591 !important; font-weight: normal !important}
#catlinks a:hover{color: #b4bfda !important; font-weight: normal !important} 

/* footer */
#footer a{color: #9bc9e8 !important;  font-weight: normal !important}
#footer a:hover{color: #ffffff !important; font-weight: normal !important} 

/* ambox/bandeau */
table.ambox a{color: #ffffff !important} 
table.ambox a:hover{color: #ffffff !important; text-decoration: underline !important} 
.bandeau a{color: #ffffff !important} 
.bandeau a:hover{color: #ffffff !important; text-decoration: underline !important} 
.bandeau-portail-texte a, .cadre_portail a{color: #70b5f3 !important} 
.bandeau-portail-texte a:hover, .cadre_portail a:hover{color: #ffffff !important} 

/* thumbnails caption */
.thumbinner a:link{color: #6dd6e1 !important; font-weight: normal !important}
.thumbinner a:visited{color: #fde20b !important; font-weight: normal !important}
.thumbinner a:hover{color: #ffffff !important; font-weight: normal !important}

/* specialchars */
#editpage-specialchars a, #mw-editTools a{color: #143fae !important; font-weight: normal !important} 
#editpage-specialchars a:hover, #mw-editTools a:hover{color: #ffffff !important; font-weight: normal !important}

/* message box */
.messagebox a{color: #45a9e5 !important; font-weight: normal !important}
.messagebox a:hover{color: #ffffff !important; font-weight: normal !important}

/* info box */
.infobox a{color: #102573 !important}
.infobox a:hover{color: #ffffff !important}
 
/* new article - anonymous login */
.permissions-errors a, .mw-newarticletextanon a{color: #bac3d2 !important; font-weight: normal !important} 
.permissions-errors a:hover, .mw-newarticletextanon a:hover{color: #ffffff !important; font-weight: normal !important} 

/* new article - registered user */
.mw-newarticletext a, #noarticletext a{color: #67a9df !important; font-weight: normal !important} 
.mw-newarticletext a:hover, #noarticletext a:hover{color: #6cb9f7 !important; font-weight: normal !important}

/* copy warning */
#editpage-copywarn a{color: #f1e37d !important; font-weight: normal !important} 
#editpage-copywarn a:hover{color: #ffffff !important; font-weight: normal !important}

/* alternative links box */
.wikilien_alternatif a:link, .wikilien_alternatif a:visited{color: #ffdb84 !important}
.wikilien_alternatif a:hover{color: #ffffff !important}

/* subtitles */
#siteSub a, #contentSub a{color: #fea201 !important; font-weight: normal !important}
#siteSub a:hover, #contentSub a:hover{color: #fde20b !important; font-weight: normal !important} 

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

/* borders and corners */
h1, .mw-headline, .infobox, .vcard{-moz-border-radius-topleft: 30px !important; -webkit-border-top-left-radius: 30px !important}
h1, .mw-headline, .infobox, .vcard, .catlinks{ -moz-border-radius-bottomleft: 30px !important; -webkit-border-bottom-left-radius: 30px !important}
h1, .mw-headline, .portlet h5{border: none !important}

/* personal toolbar */
#p-namespaces * { margin-left: 10px !important} 
#p-personal li a {color: #8bc7f1 !important} 
#p-personal li a:hover {color: #fde20b !important ; text-decoration: none !important}
#p-personal li {padding-right: 15px !important}
#pt-userpage, #pt-anonuserpage, #pt-login, #mw-panel.collapsible-nav div.portal h5 { background-image: url("data:image/png;base64,") !important; background-position: 0 0 !important} 

/* top navbar */
#p-namespaces li, #p-views li {height: 1.5em !important; margin-top:9px !important}
div.vectorTabs {background-image: none !important}
div.vectorTabs * {background-image: none !important; padding-top: 3px !important ; margin-left: 2px !important}
div.vectorTabs ul li, div#p-cactions ul li {background-image: none !important; background: none !important; border: 1px solid #31e9f8 !important; -moz-border-radius-topleft: 12px !important; -webkit-border-top-left-radius: 12px !important; -moz-border-radius-topright: 12px !important; -webkit-border-top-right-radius: 12px !important}
div#mv-head { width: 90% !important } 
div.vectorTabs ul li, div#p-cactions ul li {background-color: #0079b3 !important}
div.vectorTabs ul li:hover, div#p-cactions ul li:hover {background-color: #005180 !important; color: #ffffff !important}
div.vectorTabs ul li *, div#p-cactions ul li * { color: #ffffff !important}
div.vectorTabs ul li.selected, div#p-cactions ul li.selected {background-color: #003454 !important; color: #8dbee0 !important}
div.vectorTabs ul li.selected:hover, div#p-cactions ul li.selected:hover {background-color: #001b32 !important; color: #fced0d !important}
div.vectorTabs ul li.selected *, div#p-cactions ul li.selected * {color: #8dbee0 !important}
div#p-cactions ul li.selected {border-bottom: none !important}
div.vectorTabs ul {height: 30px !important}
div.vectorTabs ul li a:hover, div#p-cactions ul li a:hover {text-decoration: none !important}

/* search box */
#searchInput {background-color: #7fbcda !important}
#searchButton {display: none !important}
td.os-suggest-result, .suggestions-result {background-color: #45b0e7 !important}
td.os-suggest-result span, .suggestions-result span {color: #000000 !important}
td.os-suggest-result-hl, .suggestions-special, .suggestions-result-current {background-color: #0079b3 !important}
td.os-suggest-result-hl span, .suggestions-result-current span {color: #ffffff !important}
.suggestions-special {background-color: #018dd5 !important}
.suggestions-special:hover {background-color: #006496 !important; color: #fced0d !important}
.special-label {color: #000000 !important}
.special-label:hover {color: #fced0d !important}
#simpleSearch {border: none !important; background:none !important}
#simpleSearch input#searchInput {border: 1px solid #a0b2e7 !important; -moz-border-radius: 20px !important; -webkit-border-radius: 20px !important}

/* left side bar */
#mw-panel a {color: #1a3486 !important}
#mw-panel a:hover {color: #ffffff !important}
#mw-panel h5 {color: #ffffff !important}
#mw-panel h5:hover {color: #8d270f !important}
#mw-panel.collapsible-nav div.portal h5 { background-position: 0 8px !important}

/* content */
#content, .pBody, #footer {-moz-border-radius-topleft: 1em !important; -moz-border-radius-bottomleft: 1em !important; -webkit-border-top-left-radius: 1em !important; -webkit-border-bottom-left-radius: 1em !important; border-right : none !important; border-width: 5px !important}
#content { border: 1px solid #31e9f8 !important; border-right: none !important}

.toc, textarea, .catlinks {-moz-border-radius-bottomright: 30px !important; -webkit-border-bottom-right-radius: 30px !important}
.toc{-moz-border-radius-topright: 30px !important; -webkit-border-top-right-radius: 30px !important; border-width: 4px !important}
.infobox, .vcard{border-width: 4px !important}
.thumb, .tleft, .tright {border-width: 0px !important}
.thumbinner {border-width: 1px !important; border-bottom-width: 20px !important; -moz-border-radius-bottomright: 20px !important; -webkit-border-bottom-right-radius: 20px !important}
.jsprefs, table.ambox, table.ambox-content, #mw-js-message, .bandeau{-moz-border-radius-topleft: 20px !important; -webkit-border-top-left-radius: 20px !important}
table.ambox, table.ambox-content, #mw-js-message, .bandeau{-moz-border-radius-bottomleft: 20px !important; -webkit-border-bottom-left-radius: 20px !important; border-collapse: separate !important; border-left-width: 20px !important}
.jsprefs, table.messagebox, #userloginForm form, #contributions-footer-box, #talkpagetext{-moz-border-radius-topright: 20px !important; -webkit-border-top-right-radius: 20px !important}
.jsprefs, table.messagebox, #userloginForm form, #contributions-footer-box{-moz-border-radius-bottomright: 20px !important; -webkit-border-bottom-right-radius: 20px !important}
.jsprefs, table.messagebox, #userloginForm form {border-collapse: separate !important; border-right-width: 20px !important; border-bottom-width: 10px !important}

#editpage-specialchars, .mw-editTools .plainlinks{-moz-border-radius-topright: 15px !important; -webkit-border-top-right-radius: 15px !important; border-collapse: separate !important; border-right-width: 15px !important; border-top-width: 15px !important}

div.permissions-errors * {border-color: transparent !important}
div.permissions-errors, div.mw-newarticletextanon {border: solid 2px !important}
div#mw-recreate-deleted-warn {border: double 2px #af0000 !important}
textarea{border: solid 2px #839e30 !important; padding-left: 5px !important; padding-right: 5px !important; width: 98.7% !important}
input{border: solid 1px #839e30 !important; padding-left: 5px !important}
input[type=text], input[type=password]{border: solid 1px #839e30 !important; padding-left: 10px !important; padding-right: 10px !important}
input[type=password]{border-color: #fe9104 !important}
pre{border-style: dotted !important; padding: 1em !important}
.navbox, .collapsible, .autocollapse, .nowraplinks {border-left-style: dotted !important; border-right-style: dotted !important}
.catlinks{border-width: 2px !important; border-bottom-width: 30px !important}
.cadre_portail, #bandeau-portail{border: none !important}

/* footer */
#footer {margin-top: 10px !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

table, pre{color: #000000 !important}
.thumb{border: none !important} 

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* buttons and input areas */

textarea{background-color: #a3c6dc !important; color: #000000 !important; 
  border-style: solid !important; border-color: #3c5d81 #7d9bbd #7d9bbd #3c5d81 !important} 
input, select{background-color: #88b1df !important; color: #000000 !important;
  border-style: solid !important; border-color: #3c5d81 #7d9bbd #7d9bbd #3c5d81 !important} 
input[type=submit]{background-color: #246190 !important; color: #ffffff !important; 
  border-style: solid !important; border-color: #7d9bbd #11345a #11345a #7d9bbd !important}
input[type=text], input[type=password], select{background-color: #96c1da !important; color: #000000 !important; font-weight: normal !important;
  border-style: solid !important; border-color: #3c5d81 #7d9bbd #7d9bbd #3c5d81 !important} 

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* coloring classes */
h1{color: #ffffff !important; margin-bottom: 1em !important}
h2, h3, h4, h5, h6, .plainlinks{color: #e2e3a2 !important}
table.wikitable, table.wikitable *{background-color: #9ac4d9 !important; border-color: #39528f !important; color: #000000 !important}
table caption{background-color: #0f3c4d !important; border: none !important}
pre{
  background-color: #a9c7df !important; 
  border-style: dotted !important; border-color: #185f74 !important; 
  padding: 1em !important}

.cquote td{color: #072266 !important}
.thumbinner{background-color: #296390 !important; color: #a1abb3 !important}
.infobox{background-color: #4694c2 !important; color: #000000 !important}
.noprint, .noprint td{color: #4a5d8e !important}
.messagebox{background-color: #277197 !important; color: #000000 !important}
.ambox, .bandeau{background-color: #70b5d9 !important; color: #000000 !important}
.cadre_portail, #bandeau-portail{background-color: #123a7c !important; color: #000000 !important}
.mw-newarticletextanon{background-color: #b43d3d !important}
.mw-newarticletext, .noarticletext{background-color: #1d6186 !important}
.permissions-errors * {background-color: #8b1705 !important; color: #000000 !important}
#cpwarn{background-color: #b33521 !important; border-color: #ffffff !important}
.autocomment{color: #4c5773 !important}
.comment{color: #3a4d72 !important}
.prefsectiontip{color: #255377 !important}

/* coloring other elements */
#bodyContent, #footer{color: #000000 !important}
#talkheader th{background-color: #0e426e !important; color: #92a6b2 !important}
#contributions-footer-box, #catlinks{background-color: #4d8bac !important; color: #000000 !important}
#talkpagetext{background-color: #4479a7 !important; color: #000000 !important}
#editpage-specialchars, .mw-editTools .plainlinks{background-color: #4e90c3 !important; color: #000000 !important}
ul#preftoc li.selected{background-color: #32608a !important}
#p-cactions li.selected{background-color: #22577f !important}
#siteSub, #contentSub{color: #d2d7e3 !important}
#footer{background-color: #246392 !important; margin-bottom: 0px !important}
.wikilien_alternatif{background-color: #265979 !important}

/* change some syntax highlighing unreadable colors */
.kw1{color: #af0000 !important}
.kw3{color: #5965be !important}
.nu0{color: #ff00ff !important}
.br0{color: #0d8e06 !important}
.coMULTI, .co1{color: #776b69 !important}

/* restoring black text color */
.mw-newarticletextanon * {color: #000000 !important}
.mw-newarticletext *, .noarticletext * {color: #ccd0db !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* provide a coloured background for some black on transparent images */
/* unfortunately you have to add them to the list below.              */
/* Giving a coloured background while using a black background looks awful */

img[src$="/250px-Singleton_UML_class_diagram.svg.png"]
{background-color: #7495a1 !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* bullets - use a white circle within unordered lists */

ul li{
list-style-image: url("data:image/png;base64,") !important} 

ul#footer-info li, ul#footer-places li, ul#footer-icons li { list-style-image: none !important}

.toclevel-1, .toclevel-2, .toclevel-3, .toclevel-4, .toclevel-5, .toclevel-6{list-style-image: none !important}
ul#preftoc li{list-style-image: none !important}


/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* wikipedia blue logo embedded */
#p-logo a{background: url("data:image/png;base64,") 35% 50% no-repeat !important}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */

}
