css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
TechCrunch Japanese
http://jp.techcrunch.com/
*/

@-moz-document domain("jp.techcrunch.com") {
/* template v4 beta update 2008/04/14 r2 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#page
, #page div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#page * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#page a:link { color : #0000ee !important; }
#page a:visited { color : #551a8b !important; }
#page a:hover { color : #ee0000 !important; }
#page a:hover { background-color : transparent !important; }
/* /destroy div layout */

#bannerad
, #mainmenu
, #sponsors
, .entry .bookmark
, .entry > br
, #sidebar > center
, #logo a img
, #MBL_COMM
, script[src*=cm] + script + p
, script[src*=cm] + script + p + p
, script[src*=cm] + script + p + p + p
{ display : none !important; }

h1
, #sidebar ol
{ margin : 0 !important; }

h2
, h3
{ padding : 0 !important; }

#sidebar h3
, #sidebar ol li
{ color : #111 !important; }

#page h1 a
{ font-size : 150% !important; }

#sidebar > .block
, #page #container
{ margin : 1em 0 !important; }

.alignleft
, .alignright
{ display : inline !important; }

#logo a:before {
content : "TechCrunch Japanese";
}

}
