@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/* AGENT_SHEET */

  #main-window[sizemode=normal] #TabsToolbar[tabsontop=true]:not(:-moz-lwtheme) {
    background-image: -moz-image-rect(url(data:image/png;base64,), 0, 26, 27, 0),
                      -moz-image-rect(url(data:image/png;base64,), 0, 27, 27, 26),
                      -moz-image-rect(url(data:image/png;base64,), 0, 53, 27, 27) !important;
    background-repeat: no-repeat!important;
    background-size: 26px 27px, -moz-calc(100% - 52px) 27px, 26px 27px!important;
    background-position: 0 bottom, 26px bottom, 100% bottom!important;
  }
  #main-window[tabsintitlebar]:not([inFullscreen]) #TabsToolbar[tabsontop=true]:not(:-moz-lwtheme) {
    background-image: -moz-linear-gradient(bottom, rgba(86,86,86,.4) 1px, transparent 1px),
                      -moz-image-rect(url(data:image/png;base64,), 0, 26, 27, 0),
                      -moz-image-rect(url(data:image/png;base64,), 0, 27, 27, 26),
                      -moz-image-rect(url(data:image/png;base64,), 0, 53, 27, 27)!important;
    background-repeat: no-repeat!important;
    background-size: 100% 100%, 26px 27px, -moz-calc(100% - 52px) 27px, 26px 27px!important;
    background-position: center bottom, 0 bottom, 26px bottom, 100% bottom!important;
  }


#main-window[tabsontop="true"] .tabbrowser-tab:not([selected="true"]) .tab-text:not(:-moz-lwtheme) {
  text-shadow: 0 0 10px #fff,
                 0 0 10px #fff!important;
}

.tab-stack {
  min-height: 27px !important;
}

.tabbrowser-tab {
  -moz-appearance: none!important;
  margin-bottom: 1px!important;
  padding: 0!important;
  border: 0 solid transparent!important;
  background: transparent!important;
}

.tab-throbber[pinned],
.tab-icon-image[pinned] {
  -moz-margin-start: 0px!important;
  -moz-margin-end: 0px!important;
}

.tab-content {
  margin-top: 0px !important;
  border: solid transparent!important;
  border-width: 0!important;
  padding: 4px 25px 4px!important;
  margin-left: -14px!important;
}
#tabbrowser-tabs > tab:first-child .tab-content {
  margin-left: -3px!important;
}

.tabbrowser-tab:not(:first-child) > .tab-stack > .tab-content:not([selected="true"]) {
  background-image: url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       30px,
                       right!important;
  background-size: 30px 30px,
                   -moz-calc(100% - 60px) 30px,
                   30px 30px!important;
  -moz-border-image: none!important;
}

.tabbrowser-tab > .tab-stack > .tab-content:not([selected="true"]):hover {
  background-image: url("data:image/png;base64,"),
                    url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAAEnQAABJ0Ad5mH3gAAABbSURBVBhXPcvLCYAwEIThOWsFgi34Snxr1ELsv4GAhwQSMwp7+fhnYYFCZ8hLVcHa54b3/oJz7kQI4eMQDMsgxrj/pLmRVVhYszCxRjIIPX81pyLdT7q1rIbULyTOWk3IkRiVAAAAAElFTkSuQmCC"),
                    url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       30px,
                       right!important;
  background-size: 30px 30px,
                   -moz-calc(100% - 60px) 30px,
                   30px 30px!important;
  -moz-border-image: none!important;
}

.tabbrowser-tab:not([selected="true"]):hover + .tabbrowser-tab:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
  background-image: none!important;
}

.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not(:hover):not([selected="true"]) {
  background-image: url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;

  background-size: -moz-calc(100% - 0px) 35px!important;
  -moz-border-image: none!important;
}

.tabbrowser-tab > .tab-stack > .tab-content[selected="true"] {
  position: relative!important;
  background-image: url("data:image/png;base64,"),
                    url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAAEnQAABJ0Ad5mH3gAAABRSURBVBhXXYnBEYAgDATv58MiLMIG6L8WUASCjh/IxYGHeezs5gBgwbrtDk0Pcr8N9VEUmkFmZuaHOizRLiHMfsnhJI5pkRYyf0XhCVt9InToIqRtNFVJumIAAAAASUVORK5CYII="),
                    url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       30px,
                       right!important;
  background-size: 30px 30px,
                   -moz-calc(100% - 60px) 30px,
                   30px 30px!important;
  -moz-border-image: none!important;
}

#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #nav-bar,
#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme):not([customizing]) > #nav-bar[collapsed="true"] + toolbar,
#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme):not([customizing]) > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(hsl(210,58%,95%), hsl(210,75%,92%))!important;
}

#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #PersonalToolbar,
#navigator-toolbox:not(:-moz-lwtheme) toolbar:not(#toolbar-menubar):not(#TabsToolbar):not(#nav-bar):not(#addon-bar), 
#addon-bar:not(:-moz-lwtheme),
.findbar-container:not(:-moz-lwtheme) {
  background-image: -moz-linear-gradient(hsl(210,75%,92%), hsl(210,75%,92%))!important;
}

#main-window[tabsontop=true][disablechrome] #navigator-toolbox::after {
  background-image: -moz-linear-gradient(hsl(210,58%,95%), #E7EDF6)!important;
}

#navigator-toolbox::after {
  background-color: #A9BBCE!important;
}

#nav-bar:not(:-moz-lwtheme),
#PersonalToolbar:not(:-moz-lwtheme),
#browser-bottombox:not(:-moz-lwtheme) {
  box-shadow: none !important;
}

.tabs-newtab-button {
  -moz-margin-start: 6px !important;
}

#main-window[tabsontop=true][disablechrome] #navigator-toolbox::after {
  background-image: -moz-linear-gradient(#ECF3FB, #ECF3FB)!important;
}

#addons-page #header {
  background-image: -moz-linear-gradient(hsl(210,58%,95%), hsl(210,75%,92%))!important;
}
