css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lotro.com") {

.header_color, #favorites-menu li li, .forumhead, .blockhead, .forum_info DIV SPAN, #wp-calendar th {
      background-color: #cca010 !important;
    }

    .header_border, .basic-tooltip, .bbcode_container div.bbcode_quote, .sticky, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .nonsticky, .threadbit .deleted, .forum_info_block, .forum_info_form, #announcement_box > DIV:last-child, .postbit .postuseravatarlink img, .thread_info_block, .postbit, .postbitlegacy, .eventbit, .postbit .userinfo, .postbit .userinfo_noavatar, #above_threadlist_controls, #pagetitle, #above_threadlist, #above_postlist, .thread_controls, .navlinks, #below_postlist, #below_threadlist, #postlist, .forumfoot {
      border-color: transparent !important;
    }

    .ui-widget-header, .navbar-menu li li li, .navbar-menu li li li:hover, #favorites-menu li li, #charSelect li li, .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default, .ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus, .ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, .ui-state-highlight, .ui-widget-content .ui-state-highlight, .ui-widget-header .ui-state-highlight, .forumhead, .threadlisthead, .threadinfohead, .postbit .posthead, .thread_info h4, .wgo_block h2, .gradient_table th, .headline, .category_headline, .wfooter, .paginate_control, #wp-calendar caption {
      background-color: #cca010 !important;
      border-color: #998649 !important;
    }

    .header_text_color {
      background-color: #ffffff !important;
    }

    .header_color, .header_color a, .ui-widget-header, .ui-widget-header a, .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default, .ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus, .ui-state-highlight, .ui-widget-content .ui-state-highlight, .ui-widget-header .ui-state-highlight, .ui-widget-header a, .ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited, .ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited, .ui-state-highlight a, .ui-widget-content .ui-state-highlight a,.ui-widget-header .ui-state-highlight a, .forumhead, .optiontitle, .threadinfohead, .threadlisthead, .threadlisthead a, .posthead, .posthead a, .gradient_table th, .gradient_table th a, .blockhead, .wfooter, .paginate_control, #wp-calendar th, #wp-calendar caption, #user-links .navbar-menu li li a, #user-links .navbar-menu li li {
      color: #ffffff !important;
    }

    .content_color, .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, .threadbit .sticky, .threadbit .nonsticky, .postrow, .postbit .userinfo, .postbit .userinfo_noavatar, .thread_info_block, .info_subblock, .popupbody, .pagination span, .forum_info_block, .forum_info_form_block, .wgo_subblock, .formcontrols .blockrow, .section, #usercp_nav .blockbody, .blockbody, .postbit .postfoot, .postbitlegacy .postfoot, .eventbit .eventfoot, .formcontrols .blockrow.texteditor, .threadbit .deleted,.postbody .postrow, #above_threadlist_controls, #pagetitle, #above_threadlist, #above_postlist, .thread_controls, .navlinks, #below_threadlist, #below_postlist, #postlist, .forumfoot {
      background-color: #282828 !important;
    }

    .content_border, .searchbox, ul.contextmenu li ul, #wp-calendar td, UL.usermenu LI UL {
      border-color: #998649 !important;
    }

    .ui-widget-content, .gradient_table td, .blocksubhead, #wp-calendar .padday, #wp-calendar td, .as_results_table td {
      background-color: #0e0e0e !important;
      border-color: #998649 !important;
    }

    .content_color, a.content_color, .ui-widget-content, .forumrow_table, .foruminfo .forumdata, .threadlist, .threadbit, .forum_info_form_block, .forum_info_block, .postbody, .info_subblock, .thread_info_block, .userinfo_noavatar, .userinfo, .wgo_subblock, .pagination .current, .section, .blockrow legend, .blockrow p, .block .blockbody, #article_text, DIV.content_body, .announcerow .announceinfo, .announcerow, .announcerow .announcement, announcements .announcerow, .bug_forms, .usercp_forms, #wp-calendar td, #wp-calendar td {
      color: #DADADA !important;
    }

    a.content_color {
      color: #ffffff !important;
    }

    .content_color_light, .pagination, .stat_list th {
      color: #999999 !important;
    }

    .content_text_color, .friendlist .separator {
      background-color: #ffffff !important;
    }

    .content_bg_color, ul.contextmenu li ul {
      background-color: #141414 !important;
    }

    a, .ui-widget-content a, .ui-state-hover a, .ui-state-hover a:hover, .paginate_button, .sorting, .sorting_asc, .sorting_desc {
      color: #ffcc00 !important;
    }

    .link_color {
      background-color: #ffcc00 !important;
    }

h1 {
font-size: 16px !important;
}

h2 {
font-size: 14px !important;
}

h3.threadtitle {
font-size: 14px !important;
}

span.forumtitle {
font-size: 20px !important;
}

div#breadcrumbs_content {
font-size: 12px !important;
}

div.threadmeta, ul.threadstats.td.alt, dl.threadlastpost.td, ul.forumstats.td, div.forumlastpost.td {
font-size: 9px !important;
}



 Buttons 
.ui-button {
border-right-color: rgb(204,160,16) !important;
border-left-color: rgb(194,131,15) !important;
border-top-color: rgb(194,131,15) !important;
}

.signature {
text-align: center;
}

.searchlisthead, #yui-gen15, a.textcontrol {
background-color: #141414 !important;
border-color: transparent !important;
}

.popupctrl {
border-color: transparent !important;
}

}
