/* MENU */
/* hide user image, fix user menu style */
#menu-item-username img, #menu-item-username .count { display: none !important; }
#menu-item-supportus a { color: inherit !important; background-color: white !important; }
#menu-item-supportus:hover a { color: white !important; background-color: #222222 !important; }
/* hide stack icon */
#menu-item-stack:not(.active) span { display: none !important; }
#menu-item-stack:not(.active) a { padding-left: 4px; }
/* add border to popup menus */
#menu-item-more ul, #menu-username ul { border: 2px solid #83c441; border-top-width: 0; padding: 0 !important; }
/* 3-dot icon normal mouse pointer */
#menu-item-more { cursor: default; }
/* hide user image, fix user menu style */
#menu-item-username img { display: none; }
#menu-username, #menu-item-username { width: auto !important; padding: 0 5px !important; }
#menu-username li a:hover { color: white !important; background-color: #222222; text-decoration: none; }

/* CONTENT */
/* hide local blogs banner */
.locale-blogs-header { display: none; }
/* hide right sidebar */
#content-right { display: none !important; }
#content { width: 728px !important; overflow: visible; }
/* time machine menu empty space background */
#time-machine { background-color: white !important; display: table-cell !important; }
/* less flashy, green background for number-boxes */
.section-player a.thumb { background: fixed linear-gradient(to bottom, #83c441 5%, #63903a 95%); }
/* hide description and likes for tracks */
.section-track .post_info { display: none !important; }
.section-track .favcountlist { display: none !important; }
/* hide sponsored tracks, fix spacing between tracks */
#sponsored_tracks, .wb-ad-grid { display: none !important; }
#container { padding-bottom: 0 !important; margin-bottom: -20px !important; }
.paginator { margin: 0 !important; }
/* background color soft gradient */
#content-wrapper { background: fixed linear-gradient(to bottom, #f1f1f1 0%, #e6e6e6 100%); }

/* SOCIAL */
/* hide share links */
.fire-share-modal { display: none !important; }

}
