css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {


/* Liseda's YouTube */


/* Delete useless shit */
#watch-headline-user-info,#watch-sidebar,#watch-headline-user-info,#footer-container{display:none !important;}#masthead-nav{display: none !important;}
#logo-container img {display: none !important;}

/* LETS MAKE THIS CUTE */
#masthead-container{width: 100%}
#masthead{width: 100% !important;}
#masthead-user-bar-container{margin-right: 25px !important;}
#masthead-search-bar{width: 250px !important;}
#watch-main-container{margin-left: 300px !important;margin-top: 25px !important}
#watch-headline-container{margin-top: -20px !important;text-align:center;font-size: 17px !important;}
#watch-video-container {background-color: transparent !important;background-image: none !important;}
#watch-video {background-color: #171717 !important;background-image: -moz-linear-gradient(top,#111,#171717) !important;width: 854px !important;box-shadow:0px 0px 10px 5px;}
#watch-player {margin: 0 auto !important;height: 510px !important;width: 854px !important;}
#watch-video.wide,#watch-video.large {width: 1280px !important;}
#watch-video.wide #watch-player,#watch-video.large #watch-player {height: 748px !important;width: 1280px !important;}


}
