css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twinoid.com") 
{
 /*Hintergrund düsterer machen*/
  #content
  {
    background: none !important;
    background-color: rgba(0,0,0,0.5) !important;
    background-image: none !important;
  }
  #footer, #subBar
  {
    /*kann ich nicht wegmachen den weißen Streifen*/
  }
  /*Post Fenster farbig */
  /*#tid_forumMsg
  {
    color: #A4935D !important;
    background-image: url('http://data.dieverdammten.de/gfx/design/form_textarea.gif');
    background-color: grey;
    background-repeat: repeat;
  }*/
  
  /*transparenzen*/
  .tid_bg1
  {
    background-color: rgba(78,81,98,0.4) !important;
  }
  .tid_bg2
  {
    background-color: rgba(97,99,109,0.4) !important;
  }
  .tid_bg3
  {
    background-color: rgba(159,162,170,0.4) !important;
    color: #FFffFF !important;
  }
 
  .tid_post, .tid_form form
  {
    background-color: #303030!important;
    border: 1px solid black!important;
  }
  .tid_form textarea
  {
    background-color: rgba(0,0,0,0.3)!important;
    color: silver!important;
    border: none!important;;
  }
  .tid_post tid_userFriend/*grade noch sinnlos, sollte eigentlich die Farbe in _posts etwas waufhelen*/
  {
    color: crimson!important;
  }
  .tid_originContent
  {
    color: #AAAAAA !important;
  }
  .tid_threadLink
  {
    color: #AAAAAA !important;
  }
  .tid_read
  {
  }
  .tid_forumList .tid_games
  {
    background: none !important;
    background-color: rgba(0,0,0,0.4) !important;
    background-image: none !important;
    color: white !important;
  }
  div.tid_forumThread div.tid_editorContent
  {
    color: white!important;
  }
  /*Die Spiele Leiste zum ausklappen*/
  .siteContent h1, dt
  {
    text-shadow: 0 1px 0 black !important;
  }
  .rightColumn, .leftColumn
  {
   color: #CCCCCC !important;
    box-shadow: 0 16px 8px #0F0F0F !important;
    border-bottom: 1px solid black !important;
    border-left: 1px solid black !important;
    border-right: 1px solid black !important;
    background-color: rgba(0, 0, 0, 0.4); 
  }
  .siteContent h2
  {
    color: #BBBBBB !important;
    text-shadow: 0 1px 0 black !important;
  }
  .help
  {
     border: 1px dashed #5F5D5B !important;
     color: #929087!important;
  }
  strong
  {
    color: #6B7181 !important;
  }
  .group
  {
    background: rgba(255, 255, 255, 0.1) !important;
    background-image: none !important;
    box-shadow: 0 1px 4px #3F3D3B !important;
  }
  .group label
  {
     text-shadow: 0 1px 1px #000000 !important;
     color: #7B8191
  }
  /* Gescheitertes Projekt, sollte den "Meine Spiele" etwas schick machen, aber hey, ich nenne meine class nach einen HTMl Tag!
  .scontainer .assocManager table
  { 
     background: rgba(255,255,255, 0.2) !important;
     border-radius: 8px 8px 0px 0px !important;
  }
  */
  /*Textfarben überschreibem */
  .siteContent
  {
    color: #8B91A1 !important;
  }
  .warning /*Warnung mit Gehalt*/
  {
    background-color: rgba(200, 0, 0, 0.2) !important;
  }
  .about
  {
    color: #8B91A1 !important;
    text-shadow: 0 1px 0 black !important;
  }
  .demo
  {
    background-color: rgba(0, 0, 0, 0.9); 
    box-shadow: 0 0 8px #404040 !important;
    border: 1px solid #808080 !important;
  }
  a:hover
  {
    color: white!important;
  }
}
