css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

#watch-container, #watch7-video-container {overflow: hidden !important}
#watch7-player {overflow: visible !important}
#watch-player, #watch7-player {box-shadow: 0 0 8px #000 !important}
#watch-video:before {
  background: -moz-linear-gradient(right, #EEE 77%, rgba(238,238,238,0) 95%), -moz-element(#watch-video) no-repeat !important;
  content: "";
  height: 510px !important;
  left: -854px !important;
  right: 854px !important;
  position: absolute !important;
  -moz-transform: scaleX(-1) !important;
  opacity: 0.7 !important}
#watch-video:after {
  background: -moz-linear-gradient(left, #EEE 77%, rgba(238,238,238,0) 95%), -moz-element(#watch-video) no-repeat !important;
  content: "";
  height: 510px !important;
  left: 854px !important;
  right: -854px !important;
  top: 0 !important;
  position: absolute !important;
  -moz-transform: scaleX(-1) !important;
  opacity: 0.7 !important}

#watch7-player:before {
  background: -moz-linear-gradient(right, #FFF 77%, rgba(238,238,238,0) 100%), -moz-element(#watch7-player) no-repeat !important;
  content: "";
  height: 510px !important;
  left: -854px !important;
  right: 854px !important;
  position: absolute !important;
  -moz-transform: scaleX(-1) !important;
  opacity: 0.8 !important}
#watch7-player:after {
  background: -moz-linear-gradient(left, #FFF 77%, rgba(238,238,238,0) 100%), -moz-element(#watch7-player) no-repeat !important;
  content: "";
  height: 510px !important;
  left: 854px !important;
  right: -854px !important;
  top: 0 !important;
  position: absolute !important;
  -moz-transform: scaleX(-1) !important;
  opacity: 0.8 !important}

}
