css:
@-moz-document url-prefix("http://www.reddit.com/r/tabled") {

.usertext-body > .md > table,
.usertext-body > .md > table > tbody,
.usertext-body > .md > table > tbody > tr,
.usertext-body > .md > table > tbody > tr > td
{
  display: block;
}

.usertext-body > .md > table > thead
{
  display: none;
}

.usertext-body > .md > table > tbody > tr > td:empty
{
  display: none;
}
.usertext-body > .md > table > tbody > tr > td
{
  border-top: none;
  border-left: 1px solid #ccc;
  border-right: 2px solid #ccc;
  border-bottom: 2px solid #ccc;
  width: auto;
  margin-top: -1px;
  margin-left: 16px;
  border-bottom-left-radius: 2px;
}
.usertext-body > .md > table > tbody > tr > td:first-child
{
  border-top: 1px solid #ccc;
  margin-top: 30px;
  padding-left: 26px;
  margin-left: 0px;
  border-top-right-radius: 2px;
}
.usertext-body > .md > table > tbody > tr > td:first-child > span.keyNavAnnotation
{
  display: none; /* Take that RES */
}
.usertext-body > .md > table > tbody > tr > td:first-child > a[href*="reddit"]
{
  display: block;
  margin: -10px -10px -10px -26px;
  padding: 10px 10px 10px 26px;
  background-position: 10px 10px;
}
.usertext-body > .md > table > tbody > tr > td:last-child
{
  /* border-bottom: 2px solid #ccc; */
}

/* section titles */
.usertext-body > .md > hr
{
  display: none;
}

.usertext-body > .md > hr + p> strong,
.usertext-body > .md > h2
{
  display: block;
  color: black;
  margin: 50px 10px 0 10px;
  padding: 0 0 3px 1px;
  border-bottom: 2px solid #ccc;
  font-size: 18px;
  font-weight: normal;
}

}
