@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("play4free.com") {

div.friend-box.scrolled {
    display: none;
}

div.friend-box {
    display: none;
}

    /* Red/Pink text
        #800000
        #FF0000
        #FF4500
        #FF6347
        #FFDAB9
        #DDA0DD */

    .post_body span element:-moz-any(
    [style="color: #800000;"], [style="color: #FF0000;"], [style="color: #FF6347;"], [style="color: #FFDAB9;"], [style="color: #FF4500;"], [style="color: #DDA0DD;"]) {
	color: #FFDAB9 !important;
	}
	
    .post_body span element:-moz-any(
    [style="color: #FF4501;"]) {
	color: #FF4500 !important;
	}


    /* Yellow text
        #8B4513
        #DAA520
        #FFA500
        #FFD700
        #FFFFE0 */
    
    .post_body span:-moz-any(
    [style="color: #8B4513;"], [style="color: #DAA520;"], [style="color: #FFA500;"], [style="color: #FFFFE0;"], [style="color: #FFD700;"]) {
	color: #FFFFE0 !important;
	}
	
    .post_body span:-moz-any(
    [style="color: #FFD701;"]) {
	color: #FFD700 !important;
	}

    /* Green text
        #006400
        #6B8E23
        #808000
        #32CD32
        #98FB98
        #00FF00 */
    
    .post_body span:-moz-any(
    [style="color: #006400;"], [style="color: #6B8E23;"], [style="color: #808000;"], [style="color: #98FB98;"], [style="color: #32CD32;"], [style="color: #00FF00;"]) {
	color: #e5ffe5 !important;
	}
	
    .post_body span:-moz-any(
    [style="color: #32CD33;"]) {
	color: #32CD32 !important;
	}
        
    /* Blue text 1
        #2F4F4F
        #708090
        #4682B4
        #87CEEB
        #E0FFFF
        #00BBFF
        #1E90FF
        #000080
        #0000CD
        #1E90FF
        #00BFFF
        #87CEFA
        #00bbff */
    
    .post_body span:-moz-any(
    [style="color: #00bbff;"], [style="color: #2F4F4F;"], [style="color: #708090;"], [style="color: #4682B4;"], [style="color: #87CEEB;"], [style="color: #E0FFFF;"], [style="color: #00BBFF;"], [style="color: #00bbff;"], [style="color: #1E90FF;"], [style="color: #000080;"], [style="color: #0000CD;"], [style="color: #1E90FF;"], [style="color: #00BFFF;"], [style="color: #87CEFA;"]) {
	color: #E0FFFF !important;
	}
	
    .post_body span:-moz-any(
    [style="color: #00BBFg;"]) {
	color: #00BBff !important;
	}

    /* Purple text
        #4B0082
        #483D8B
        #9400D3
        #9370DB
        #E6E6FA
        #9300D3 */
    
    .post_body span:-moz-any(
    [style="color: #4B0082;"], [style="color: #483D8B;"], [style="color: #9400D3;"], [style="color: #9370DB;"], [style="color: #E6E6FA;"], [style="color: #9300D3;"]) {
	color: #E6E6FA !important;
	}
	
    .post_body span:-moz-any(
    [style="color: #9370Dc;"]) {
	color: #9370DB !important;
	}

    /* Pink text
        #800080
        #C71585
        #FF1493
        #FF69B4
        #DDA0DD */
    
    .post_body span:-moz-any(
    [style="color: #800080;"], [style="color: #C71585;"], [style="color: #FF1493;"], [style="color: #FF69B4;"], [style="color: #DDA0DD;"]) {
	color: #dec8de !important;
	}

    .post_body span:-moz-any(
    [style="color: #FF69B5;"]) {
	color: #FF69B4 !important;
	}

    /* Greyscale:
        #000000
        #696969
        #A9A9A9
        #DCDCDC
        #FFFFFF */

    .post_body span:-moz-any(
    [style="color: #000000;"], [style="color: #696969;"], [style="color: #A9A9A9;"], [style="color: #DCDCDC;"], [style="color: #FFFFFF;"]) {
	color: #DCDCDC !important;
	}

    .post_body span:-moz-any(
    [style="color: #A9A9A8;"]) {
	color: #A9A9A9 !important;
	}

        
    /* Invisible Grey:
        #312e2b */
    
    .post_body span:-moz-any(
    [style="color: #312E2B;"], [style="color: #312e2b;"]) {
	color: #E2DAC5 !important;
	}
	
    /* Quote Margin */
    blockquote {
    margin-left: 15px !important;
    }
    .codeblock {
    margin-left: 15px !important;
    }
    
