css:
@-moz-document url-prefix("https://n-1.cc") {
#elgg_topbar {
    height: 35px !important;
    }

.menubar a, .mainmenu_img {
    border-radius: 0px 0px 0px 0px !important; 
    border: 0px !important;
    }
    
body {
    color: #555555 !important;
    font: 13px/1.5 'Helvetica Neue','Arial','Liberation Sans','FreeSans','sans-serif' !important;
    font-weight: 400 normal !important;
    }

h1, h2, h3, label {
    font-weight: 400 normal !important;
    color: #333333 !important; 
}


#two_column_left_sidebar {
    background: #FFFFFF !important;
    border-radius: 5px 5px 5px 5px !important;
    }
#owner_block a {
    font: 13px/1.5 'Helvetica Neue','Arial','Liberation Sans','FreeSans','sans-serif' !important;
    font-weight: 400 normal !important; 
    }

#owner_block_bookmark_this a, #owner_block_rss_feed a, #owner_block_report_this a {
    font-size: 0.7em !important;
    color: #666666 !important;
    } 
    
#owner_block_submenu .submenu_group {
    border-bottom: 2px solid #E0EEF5 !important;
    }
.ownerblockline {
    border-bottom: 2px solid #E0EEF5 !important;
}

#owner_block_report_this{
    border: 1px solid #FFFFFF !important;
}

#owner_block_desc {
    border-bottom: 1px solid #FFFFFF !important;
}
    
#two_column_left_sidebar_maincontent {
    background: #FFFFFF !important;
    border-radius: 5px 5px 5px 5px !important; 
    }
    
#content_area_group_title, #profile_info, #profile_info_column_right {
    background: #FFFFFF !important;
    border-radius: 5px 5px 0px 0px !important;
    padding-top: 10px !important;
    padding-bottom: 5px !important;
}


#content_area_group_title h2{
    font-family: Helvetica Neue,Helvetica,sans-serif !important;
    font-weight: 400 normal !important;
    color: #666666 !important;
    font-size: 18px !important;
    line-height: 23px !important;
    width: 85% !important;

    }
    
#groups_info_column_right {
    width: 200px !important;
    }
#pages_breadcrumbs {
    padding-top: 10px !important;
    font-size: 15px !important; 
    font-weight: 600 bold !important; }

.contentWrapper, .forum_latest, #left_column .filerepo_widget_singleitem, .group_widget .search_listing, .search_listing, .friendsPicker .friendsPicker_container .panel .wrapper, .index_box, ul#twitter_update_list li, #profile_info_column_middle .profile_status, 
.filerepo_widget_singleitem, .shares_widget_wrapper, .thewire-singlepage .note_body, #left_column .filerepo_widget_singleitem, #group_pages_widget .search_listing, .group_widget, #filerepo_widget_layout, #group_river_widget {
    background: #ffffff !important;
    border-radius: 5px 5px 5px 5px !important; 
    border: 1px dotted #dedede !important;
}

.river_item {
    border-bottom: 1px dotted #dedede !important;
}

.river_item_list {
    border-top: 1px dotted #dedede !important;
    }

.comment_feed {
    background: #ffffff !important;
    
    }


#infovis {
    background: #e9e9e9 !important;
    border: 1px solid #BBBBBB  !important;
    border-radius: 3px 3px 3px 3px !important; 
}

#groups_info_column_left .odd, #profile_info_column_middle .odd, #profile_info_column_middle .even  {
    background: #e9e9e9 !important; }


#owner_block_submenu ul li a:hover {
    color: #555555 !important;
    background: #e9e9e9 !important; 
    text-decoration: none !important;
    border-radius: 5px 5px 5px 5px !important; 
    }

#owner_block_submenu ul li.selected a {
    color: #ffffff !important;
    background: #666666 !important; 
    text-decoration: none !important;
    border-radius: 5px 5px 5px 5px !important;
}

a {
    color: #0084B4 !important;
    text-decoration: none !important;
    }
    
a:hover {
    text-decoration: underline !important;
    }

.add_topic_button, .submit_button, input[type="submit"], a.archive_report_button, a.delete_report_button, .profile_info_edit_buttons a, .tidypics_download a, .filerepo_download a, .thewire-post .reply, .sharing_item_address a  {
    color: #FFFFFF !important;
    background: #CC0000 !important;
    border: 1px solid #CC0000 !important; }


#searchform input.search_submit_button {
color: #ffffff !important;
    background: #555555 !important;
    border: 1px solid #333333 !important }

.fc-event, .fc-agenda .fc-event-time, .fc-event a {
    color: #FFFFFF !important;
}

.fc-content .fc-state-highlight {
    background: #FFFF66 !important;
}

.dp-popup {
   background:#FFFFFF !important;}

/* calendar plugin */

table.jCalendar {
  background-color:#FFFFFF !important;
  border-bottom-color:#dedede !important;
  border-left-color:#dedede !important;
  border-right-color:#dedede !important;
  border-top-color:#dedede !important;
}
table.jCalendar td.current-month {
  background-color: #fff !important;
  border: solid 1px #e9e9e9 !important;
}


table.jCalendar td.today {
  background-color:#FFFF66 !important;
  color: #000 !important;
  font-weight: bold !important;
}

table.jCalendar td.event {

   background-color:#4690D6 !important;

}

.fc-header .fc-state-default, .fc-header .fc-state-default a {
  background-color: #e9e9e9 !important; 
  border: 1px solid #e9e9e9 !important; 
}

.pagination a:hover {
   color: #ffffff !important;
}


#layout_footer {
  background-color: transparent !important;
  height: 50px !important;
  border-radius: 0px !important;
  }

.footer_toolbar_links, .footer_toolbar_links a {
  color: #FFFFFF !important;
}

.collapsable_box_content {
    background: #FFFFFF !important;
    border: 1px solid #BBBBBB  !important;
    border-radius: 0px 0px 5px 5px !important; 
}

.collapsable_box_header {
    border: 1px solid #bbbbbb !important;
    background: #e9e9e9 !important;
}

a:hover.toggle_customise_edit_panel {
    background: #dedede !important;
}

/* image hover should indicate link. border rounded bytheface */

img {
border-radius: 2px !important;
}

img:hover {
opacity: 0.8 !important;
}


/* image effects on hover for gallery (just for the lulz) */

.tidypics_album_cover{
-webkit-transform:scale(0.9); /*Webkit: Scale 80% size*/
-moz-transform:scale(0.9); /*Mozilla scale version*/
-o-transform:scale(0.9); /*Opera scale version*/
-webkit-transition-duration: 0.5s; /*Webkit: Animation duration*/
-moz-transition-duration: 0.5s; /*Mozilla duration version*/
-o-transition-duration: 0.5s; /*Opera duration version*/
opacity: 0.9; /*initial opacity of images*/
border: 0px solid #fff !important;
}

.tidypics_album_images{
-webkit-transform:scale(1); /*Webkit: Scale 100% size*/
-moz-transform:scale(1); /*Mozilla scale version*/
-o-transform:scale(1); /*Opera scale version*/
-webkit-transition-duration: 0.5s; /*Webkit: Animation duration*/
-moz-transition-duration: 0.5s; /*Mozilla duration version*/
-o-transition-duration: 0.5s; /*Opera duration version*/
opacity: 0.9; /*initial opacity of images*/
border: 0px solid #fff !important;
}

.tidypics_album_cover:hover, .tidypics_album_images:hover{
-webkit-transform:scale(1.18); /*Webkit: Scale up image to 1.2x original size*/
-moz-transform:scale(1.8); /*Mozilla scale version*/
-o-transform:scale(1.8); /*Opera scale version*/
border: 1px solid #fff;
opacity: 1 !important;
}

}
