css:
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/explore"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/explore"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/") {
body {
background: #fff url(http://i.imgur.com/gtB6c.jpg) top 
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: #474747;
}

#posts .post {
background-color: #2a2a2a;
}

body, #posts .post.new_post .new_post_label {
font: normal 14px "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
line-height: 1.4;
color: #e8e8e8;
}


div.l-header .selection_nipple {
border-left: 30px solid transparent;
border-right: 30px solid transparent;
border-bottom: 12px solid #474747;
}

#right_column .radar .content .mask, #right_column .radar .content {
background: url(http://i.imgur.com/r8LN7.png)
}

#posts .post .permalink {
background: url(http://i.imgur.com/5cCta.png)
}

#right_column .controls_section .selected {
background: #68b6d0;
background:  -moz-linear-gradient(top,#68b6d0 0,#5b97aa 50%,#5b97aa 50%,#5b97aa 100%);
background:  -webkit-gradient(linear,left top,left bottom,color-stop(0%,#5b97aa),color-stop(50%,#68b6d0),color-stop(50%,#5b97aa),color-stop(100%,#5b97aa))
}

#right_column .controls_section .recessed {
background-color: #696969;
}

#return_to_top .return_to_top_icon {
background: -600px -190px url('http://i.imgur.com/Vzdqv.png') no-repeat transparent;
}
}
