css:
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/explore"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/") {


#right_column .radar
{
display:none !important;
}

body {
background: #fff url(http://fc06.deviantart.net/fs71/i/2011/151/1/3/fall_by_filsru-d35uw6l.jpg) center repeat fixed !important;
}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.4) !important;
}

#posts .post {
background-color: rgba(0,0,0,0.5) !important;
}

#posts .post .arrow {
background: url(http://i.imgur.com/zJnmE.png) no-repeat transparent;
}
body, #posts .post.new_post .new_post_label {
font: normal 14px "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
line-height: 1.4;
color: #e8e8e8;
}

a {
color: #b1b1b1;
}

#posts .post .post_controls .like_button {
background: -500px -150px url('http://i.imgur.com/qruqB.png') no-repeat transparent;
}


div.l-header .selection_nipple {
border-left: 20px solid transparent;
border-right: 20px solid transparent;
border-bottom: 11px solid rgba(0, 0, 0, .6);;
}

#right_column .radar .content .mask, #right_column .radar .content {
background: url(http://i.imgur.com/gH3HR.png)
}

#posts .post .permalink {
background: url(http://i.imgur.com/NVsYI.png)
}

#right_column .controls_section .selected {
background: #68b6d0;
background:  -moz-linear-gradient(top,#68b6d0 0,#5b97aa 50%,#5b97aa 50%,#5b97aa 100%);
background:  -webkit-gradient(linear,left top,left bottom,color-stop(0%,#5b97aa),color-stop(50%,#68b6d0),color-stop(50%,#5b97aa),color-stop(100%,#5b97aa))
}

#right_column .controls_section .recessed {
background-color: rgba(0,0,0,0.5);
}

#return_to_top .return_to_top_icon {
background: -600px -190px url('http://i.imgur.com/Vzdqv.png') no-repeat transparent;
}
}

@-moz-document url-prefix("https://www.tumblr.com/") {
.footer_links,
#footer,
#slideshow_track,
p.forgot_password,
a.login
{
display:none !important;
}

 body {
    background: url(http://fc06.deviantart.net/fs71/i/2011/151/1/3/fall_by_filsru-d35uw6l.jpg) center #000000 no-repeat fixed; background-size: 100%;!important;
  }
}
