css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tgfcer.com") {
/*
* UserStyles.org: clean TGFC : club.tgfcer.com without AD(Style 61442)
*
* Author      : tumuyan
* Created     : February 25, 2012
* Updated     : Apr 8, 2012
* Version     : 0.4 - bit
* Description : only fuck the ADs
* Usage       : For use with Stylish, a Firefox/Chrome extension (http://userstyles.org/)
*
*/
}

@-moz-document domain("club.tgfcer.com") {
.wrap{width:88% !important;}

.ad_footerbanner
,.ad_headerbanner
,.box,.headactions,img[src="hot.gif"]

,ins[style="display:inline-table;border:none;height:600px;margin:0;padding:0;position:relative;visibility:visible;width:160px"]
,iframe[id="google_ads_frame1"]

,iframe[id="cproIframe3"]
,iframe[id="cproIframe2"]
,iframe[id="cproIframe1"]
,iframe[id="cproIframe"]

,#dish1
,.ad_column
,#google_image_div
,#googlead

{display:none !important;}

#footer
{background:#ffffff !important;}

#quickpost

{
display:inline !important;
height:25px !important;
width:100px !important;
position:fixed !important;
bottom:25px !important;
z-index:52 !important;
}

#quickpost:hover{
bottom:25px !important;
height:380px !important;
width:80% !important;
background-color:#cccccc !important;

-moz-transition:  0.5s linear !important;
-moz-transition-timing-function: cubic-bezier(0.05, 1.4, 0.25, 1) !important;
-moz-transition: background-color .5s linear;

-webkit-transition: all .5s ease-in-out  !important;
-webkit-transition: background-color .5s linear  !important;

transition: background-color .5s linear  !important;
}
#quickpost h4{background-color:#000000 !important;}

.ad_text{
position:absolute !important;
top:66px  !important;
height:16px !important;
overflow:hidden !important;}
.ad_text:hover{height:140px !important;
-moz-transition:  0.3s ease-in-out !important;
-webkit-transition: all .5s ease-in-out  !important;
-o-transition: all .5s ease-in-out  !important;
}



#footfilter{
display:inline !important;
position:fixed !important;
bottom:25px !important;
width:120px !important;
height:12px !important;
right:30px !important;
z-index:50 !important;
background:transparent; 
overflow:hidden !important;
}


#footfilter:hover{
background-color:gray !important;
width:640px !important;
height:25px !important;
   -moz-transition: background-color .5s linear;
   -webkit-transition: background-color .5s linear;
   transition: background-color .5s linear;

}


p[class="btns"]
{position:absolute !important;
top:0px !important;
left:200px !important;
}
}

@-moz-document url-prefix("http://club.tgfcer.com/thread") {
#footfilter,#footfilter:hover{
width:220px !important;
height:25px !important;
right:30px !important;
z-index:50 !important;
background:transparent; 
overflow:hidden !important;
}
}



@-moz-document domain("club.tgfcer.com") {
td.postauthor dl.profile{display:none !important;}

.mainbox {border :0px !important;}
.ad_text{display:none;}
 }
