@namespace url(http://www.w3.org/1999/xhtml);















@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {







































#dashboard_index,







#dashboard_inbox,







#dashboard_edit_post,




#dashboard_submissions,







#dashboard_drafts,







#dashboard_post_queue,







#dashboard_likes,







#dashboard_followers,







#dashboard_following,







#dashboard_directory,







#dashboard_lookup_contacts,







#main_doc_page,







#dashboard_settings,







#dashboard_create_tumblelog,







#dashboard_members,







#main_tag,







#main_meetups,







#main_about,







#main_explore,







#dashboard_goodies,







#main_developers,







#main_themes,







#dashboard_manage_themes,







#main_edit_theme,







#main_jobs,







#main_policy,







#dashboard_tumblelog_settings {







background:#faeee1 url(http://oi40.tinypic.com/2lu9e7l.jpg) no-repeat bottom right fixed! important }















#dashboard_index div.l-content,







#dashboard_inbox div.l-content,







#dashboard_submissions div.l-content,







#dashboard_drafts div.l-content,







#dashboard_post_queue div.l-content,







#dashboard_likes div.l-content,







#dashboard_followers div.l-content,







#dashboard_members div.l-content,







#main_tag div.l-content,







#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







background:rgba(0, 0, 0, 0.2) !important }















.selection_nipple { border-bottom-color: rgba(255, 0, 0, 0.4) !important }































.permalink { background: none !important }















.avatar_frame {







background-color: rgba(255, 0, 0, 0.2) !important; border-color: rgba(255, 0, 0, 0.4) !important }















.notification {







background: rgba(255, 0, 0, 0.2) !important;







border: rgba(255, 0, 0, 0.6) !important;







background-image: none !important;







margin-bottom: 1px !important }















.notification a { border-bottom: 1px solid rgba(255, 0, 0, 0.6) !important }







.notification a:hover { border-bottom: 1px solid rgba(255, 0, 0, 0.8) !important }















.notification_type_icon { display: none !important }















.nipple { border-right-color: rgba(255, 0, 0, 0.4) !important }































.controls_section li { background: rgba(255, 0, 0, 0.4) !important }







.with_subtitle small { color: rgba(255, 0, 0, 0.8) !important }







.recessed a { color: rgba(255,255,255,0.7) !important }







.radar { display: none !important }































* { text-shadow: none !important }















h1, .title { color: rgba(255, 0, 0, 0.4) !important }















.no_posts_found, h1.dashboard_header { color: rgba(255, 0, 0, 0.4) !important }















.dashboard_options_form {







background: rgba(255, 0, 0, 0.2) !important;







border: none !important;







color: rgba(255, 0, 0, 0.8) !important }















.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(255,255,255,0.9) !important }















.blue_note, .blue_note a {







background: rgba(0,0,0,0.1) !important;







color: rgba(255, 0, 0, 0.8) !important }































#footer, #footer a {







border: none !important;







color: rgba(0,0,0,0.5) !important }



























































#pagination a {







background: rgba(0,0,0,0.4) !important;







color: rgba(255,255,255,0.7) !important }







#pagination a:hover {







color: rgba(255,255,255,0.9) !important }















#dashboard_controls a, #dashboard_controls .control {







color: rgba(255,255,255,0.7) !important }















.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {







display: none !important }















































